INSERT INTO `data` VALUES (0, 68, '猜猜我有多爱你', '山姆·麦克布雷尼', '黄色', NULL, 28, '4-6岁', 0, '一大一小两只兔子短短的对话，浓缩生命中最复杂、也是最伟大的情感。小兔子认真地向大兔子表达“我爱你”，并用各种身体动作和看得见的静物表达自己对大兔子的爱。但不管怎么比，永远也比不过大兔子来得多、来得高、来得远。最后，小兔子终于在一片酽酽爱意的包裹下睡去了。', '情感体验', 1, '兔子', NULL, NULL, NULL, NULL, NULL, NULL, '家庭关系', '夜晚', '家,自然', 0);
INSERT INTO `data` VALUES (1, 200, '生气的亚瑟', '奥拉姆', '棕色,紫色', '1983年英国鹅妈妈新人奖', 36, '3-6岁', 0, '亚瑟的妈妈让他去睡觉，不让他看电视片，所以亚瑟生气了。他非常、非常地生气，气到足以把整个宇宙都震成碎片……但突然之间，亚瑟忘记了究竟是什么惹得他如此生气……', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, '儿童', '情绪', '家庭关系', NULL, '家', 0);
INSERT INTO `data` VALUES (2, 201, '人是干什么用的', '（文）/马达莱纳·马托佐（图）', '粉色', '2022年博洛尼亚童书奖“非虚构类特别提名奖”', 0, '5岁以上', 0, '给问个不停的孩子的哲学启蒙书：人是干什么用的？梦是干什么用的？花是干什么用的？犀牛是干什么用的？美术馆里的画是干什么用的？……这本书是干什么用的？回答孩子千奇百怪的问题，培养惊人的思考能力！◆ 回答孩子千奇百怪的问题，培养惊人的思考能力！◆ 梳理孩子的日常提问，带孩子运用哲学思维，培养孩子的思考能力，让问个不停的孩子开始有逻辑地思考！', '哲理思考', 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `data` VALUES (3, 211, '威利的奇遇', '安东尼·布朗', '棕色', NULL, 0, '5岁以上', 1, '《威利的奇遇》是启发精选国际大师名作绘本系列之一，适合5岁以上儿童阅读。这本书延续了《梦想家威利》和《威利的画》的单页叙事法，并再度将绘本故事与艺术知识相结合——只是，这次的艺术门类变成了文学。在本书中，安东尼根据十个经典儿童文学故事来创作插画，仍然把所有角色都替换成大猩猩，而原著主人公正是威利本人。这种独特的演绎方式看似调皮冒险，却与相对工整难啃的文学著作碰撞出火花，让文学艺术展现出另一种新鲜的风貌，呈现出有趣且印象深刻的视觉效果。', '成长道理', 1, NULL, NULL, NULL, NULL, NULL, NULL, '外貌,身份', NULL, NULL, '乡村,水域,沙滩', 0);
INSERT INTO `data` VALUES (4, 214, '你感觉怎么样？', '安东尼·布朗', '蓝色', NULL, 0, NULL, 0, '你觉得怎么样？有时候我很快乐，有时候我也会感到悲伤……有时间我很自信，但有时候我也会感到害羞……这是一本关于感觉的绘本，绘本大师用他独特的视角生动地描绘出生活中各种各样的情感体验。你感觉怎么样？这对于成年人来说或许是再简单不过的问题，但如果把这个问题问向孩子，或许很多孩子先会一愣，然后需要用很长一段时间去思索着怎样回答这个问题。对孩子来说，这确实不是个容易回答的问题。如何形容？如何界定？如何回答呢？或许翻开本书，会有一个答案。在书中，绘本大师安东尼·布朗用艺术的画笔质朴地描绘日常中的情绪状态。夸张、传神的表情，在博得孩子开心的同时，也在告诉孩子，这样——代表快乐！', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '朝夕轮转', '家', 0);
INSERT INTO `data` VALUES (5, 220, '一起玩形状游戏', '安东尼·布朗', '蓝色', NULL, 32, '3-6岁', 1, '本书讲述的是象小妹和大熊哥哥超喜欢画画！他们会随意的画个形状，然后加上几笔，变成一个图案，这是他们永远玩不腻的游戏。想知道到底有多好玩吗？发挥你无与伦比的想象力与创造力，一起来玩这个快乐的形状游戏吧！', '线条色彩', 1, '熊,大象', NULL, NULL, NULL, NULL, NULL, NULL, '朋友关系', NULL, '家', 0);
INSERT INTO `data` VALUES (6, 221, '公园里的声音', '安东尼·布朗', '红色,绿色', '科特·马希拉大奖', 32, '5岁以上', 1, '来自两个不同家庭、一男一女、个性互异的两个小孩儿，加上一位趾高气昂、高贵华丽的妈妈，还有一位失业也失去斗志的爸爸，共计四个人，不约而同地一起带着各自的小狗到公园去散步。男孩贵气十足的妈妈讨厌女孩和她家的小狗；女孩失业的爸爸坐在公园长椅上心情沮丧地翻报纸找工作；忧郁孤独的男孩很开心遇到女孩，并快乐地和她在公园玩；天真开朗的小女孩带爸爸和小狗去公园散步，遇到不快乐的小男孩，邀他一起玩溜滑梯。', '成长道理', 1, '狗', NULL, NULL, NULL, NULL, NULL, NULL, '家庭关系,宠物关系', NULL, '公园', 0);
INSERT INTO `data` VALUES (7, 236, '小岛', '马克•杨森', '蓝色', NULL, 32, '10岁以上', 1, '遭遇海难的苏菲和爸爸登上了大海中的一座孤岛。在这座岛上，苏菲和爸爸渡过了春夏秋冬。然而，他们所在的岛其实是一只大海龟。海龟不仅收留了这家“不速之客”，更保护他们，为他们挡风遮雨。但海龟却有一个疑问：苏菲知道我是一只海龟吗？', '自然生物', 1, '猫,蝴蝶,蜘蛛,鸟,虾,鱼', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '春,夏,秋,冬,雪,打雷闪电,大风', NULL, 0);
INSERT INTO `data` VALUES (8, 237, '奥莉薇', '伊恩·福尔克纳', '灰色,红色', '2001年凯迪克银牌奖', 22, '3-6岁', 1, '小猪奥莉薇擅长很多事情，最拿手的一件事就是把人累昏，甚至常常把自己累昏！她会涂妈妈的口红、穿上妈妈的高跟鞋照镜子，还会吓弟弟。要是出门，她会把所有的衣服都拿出来穿一遍……画家不可思议地将所有人的关注聚焦于小奥莉薇身上，我们完全不必操心故事发生在何时、何地，而这也使得孩子更自然地联想到自身。', '成长道理', 1, '猫,狗,猪', NULL, NULL, NULL, NULL, NULL, '性格', '家庭关系,宠物关系', '早晨,中午,傍晚,夜晚', '家,沙滩', 0);
INSERT INTO `data` VALUES (9, 240, '寻找维尼', '林赛·马蒂克', '棕色,黄色', '凯迪克金奖', 0, '3-6岁', 0, '《寻找维尼》作者是故事主人公兽医哈里·科尔伯恩上尉的曾外孙女。讲述了讲述了在战争年代，一只真实的小熊的一段非凡的冒险旅程，这是一个令人心生温暖的故事。1914年，一战期间，兽医哈里·科尔伯恩在前往照顾部队马匹的途中，用20美元从猎人手里买下了一头失去父母的小熊，他以自己的家乡名“温尼伯”给它起名，小名为“维尼”，并把它带上了战场。从加拿大的田野到大洋彼岸的护航队，再到英国的一个军事基地……他与维尼形影不离，彼此产生了深厚的感情。但战争一触即发，最终，他不得不将维尼送去伦敦动物园照管。分别之际，他告诉维尼：尽管他们即将分离，但最重要的是他永远爱它，而它永远是自己的维尼……', '情感体验', 1, '熊', NULL, NULL, NULL, NULL, NULL, '职业', '宠物关系', NULL, '城镇', 0);
INSERT INTO `data` VALUES (10, 245, '疯狂星期二', '大卫·威斯纳', '黄色,蓝色,黑色', '凯迪克金奖', 32, '3-6岁', 1, '星期二晚上8点左右，池塘里昏昏欲睡的青蛙被惊醒了！接下来，没有文字的描述，只有在荷叶上尽情飞翔的青蛙带你穿越城镇的每个角落，感受一个不平静的夜晚，初升的太阳照耀着恢复平静的城镇。然而，下一个星期二的晚上，房子的外墙上借由灯光的照射，半空中出现了猪的影子……', '自然生物', 1, '青蛙', NULL, NULL, NULL, NULL, NULL, '外貌', NULL, '夜晚', NULL, 1);
INSERT INTO `data` VALUES (11, 246, '狼婆婆', '杨志成', '橘色,黑色', '凯迪克金奖', 32, '3-6岁', 1, '很久以前，每当妈妈出门之后，狼婆婆就会来敲门了，阿珊、阿桃和宝珠究竟能不能识破狼婆婆的阴谋呢？绘者利用粉笔渲染的特性，将动物兽性的跳跃距离拉长，彷佛呼吸到野狼出没的气味。明暗色调的强烈对比，让人有不寒而栗的诡异效果。', '自然生物', 1, '狼', NULL, NULL, NULL, NULL, '儿童', '能力', '家庭关系', '夜晚', '家,乡村', 0);
INSERT INTO `data` VALUES (12, 247, '晴朗的一天', '诺尼·霍格罗金', '黄色,橙色', '凯迪克金奖', 40, '4岁以上', 1, '这是一则古老的亚美尼亚民间故事，特别适合孩子大声朗读。一只调皮的狐狸无意间偷喝了一位老婆婆的牛奶，被恼羞成怒的老婆婆剪下了尾巴，要他用牛奶来交换，可是想得到牛奶并不那么容易，狐狸接下去都经历了什么事情呢？', '情感体验', 1, '狐狸', NULL, NULL, NULL, NULL, NULL, NULL, '群体关系', '朝夕轮转', NULL, 1);
INSERT INTO `data` VALUES (13, 252, '警官巴克尔和警犬葛芮雅', '佩吉·拉特曼', '蓝色,黄色,玫红色', '凯迪克金奖', 32, '3-6岁', 1, '拿坡里镇的巴警官比任何人都了解安全守则。但是每当他发表安全守则时，却没有人要听。直到有一天，拿坡里警局买了一条狗利亚的警犬。巴警官不知道狗利亚有一种示范安全守则的表演能力。突然间，每个人变得喜欢听巴警官的演讲，并且总是叮咛他说：“一定要带警犬利亚一起来喔。”然而，当巴警官发现真正的主角不是他时，便很伤心并决定不接受任何演讲的邀请。狗利亚可以说服他最好的拍档回到他的工作吗？而拿坡里又将会发生什么意外呢？', '情感体验', 1, '狗', NULL, '警察', NULL, NULL, NULL, NULL, '宠物关系', '朝夕轮转', '学校', 0);
INSERT INTO `data` VALUES (14, 259, '骑士与龙', '玛格丽特·霍奇斯（文）/特瑞娜·沙特·海曼（图）', '红色', '凯迪克金奖', 40, NULL, 0, '很久很久以前，那时候的土地上生活着精灵、仙女和怪兽。恶龙在乡间田野为非作歹，公主为了寻找屠龙的勇士独自离开城堡，终于找到了一名骑士。这名骑士骁勇善战，屡战屡败，却不肯放弃，直到他把恶龙杀死，后来骑士与公主结为夫妻，过上幸福的生活。', '情感体验', 1, NULL, NULL, '公主', NULL, NULL, NULL, '外貌,能力', '朋友关系', '朝夕轮转', NULL, 0);
INSERT INTO `data` VALUES (15, 279, '小房子', '维吉尼亚·李·伯顿', '蓝色', '凯迪克金奖', 0, '3-6岁', 1, '小房子每天站在山冈上看风景，除了日月星辰和四季的变化，小房子还看到乡村的景物随着挖马路、开商店、盖高楼、通地下铁……而一点一点地改变。结果，小雏菊和苹果树不见了，取而代之的是都市的乌烟瘴气和行色匆匆的人们。还好，小房子的主人的后代发现了小房子，把她移到了乡下，她又可以静静地欣赏大自然的风景了。', '自然生物', 1, NULL, NULL, NULL, NULL, NULL, '房子', NULL, NULL, '四季轮转', '城镇,乡村', 0);
INSERT INTO `data` VALUES (16, 286, '驴小弟变石头', '威廉·史代格', '彩色', '考德科特奖', 32, '3-8岁', 0, '一个雨天，驴小弟捡到一块可以实现他的愿望的魔石。但是在回家的路上，驴小弟被突然遇到的狮子吓着了，他下意识的想法却带来了意想不到的结果。他变成了石头。　　那么驴小弟后来是如何和他的爸爸妈妈重逢，又是如何变回自己的，这其中充满美丽温馨，还有神奇。图文生动有趣，这是一本深受各地孩子喜爱的现代经典。', '成长道理', 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '家庭关系', '四季轮转,朝夕轮转', NULL, 1);
INSERT INTO `data` VALUES (17, 290, '有个婆婆吞了一只苍蝇', '西姆斯•塔贝克', '黑色,粉色', '凯迪克奖', 36, '3-6岁', 0, '有个老婆婆吞了一只苍蝇，然后她吞下蜘蛛去逮苍蝇、吞下鸟儿去捉蜘蛛、吞下猫去抓鸟儿、吞下狗去咬猫、吞下奶牛去追狗，最后吞下一匹马——她撑死了！这是一首人们耳熟能详的民谣，可是谁也不知老婆婆肚子里的情况是怎样的，西姆斯•塔贝克用不断变大的洞洞，展现了生动跳跃的视觉效果，新鲜有趣！他还用撕纸拼贴的方式，把日常生活中的小物件融入故事中，又添了一份独到的艺术巧思！故事结构循环往复，语言富于韵律节奏，让孩子读起来朗朗上口；情节简单又夸张，充满了来自民间的诙谐和糅合了传统与现代的无厘头式幽默！', '自然生物', 1, '猫,狗,马,蜘蛛,鸟', NULL, '老人', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `data` VALUES (18, 294, '丢饭团的笑婆子', '布莱克·兰特（图）/小泉八云（文）', '绿色', '凯迪克金奖', 48, '3-6岁', 0, '这是一个在日本民间流传的故事 有一个特别喜欢笑、善于做饭团的婆子。一天，她的饭团忽然滚到了裂开的地缝里，她一路追呀追，跌到了地洞里。地洞里的菩萨们好心提醒她不要追，前面有恶鬼。笑婆子不管，继续追饭团。恶鬼来了，发现了嘻嘻笑着的笑婆子，把她掳回家。恶鬼们给了她一把只要一搅拌就会有饭不断跑出来的饭勺子，让笑婆子做饭给他们吃。有一天，笑婆子太想家了，于是决定逃走，她会成功吗？', '情感体验', 1, NULL, NULL, '老人', NULL, NULL, NULL, '性格', NULL, NULL, NULL, 1);
INSERT INTO `data` VALUES (19, 322, '霍金斯的恐龙世界', '芭芭拉·克利（文）/布莱恩·塞兹尼克（图）', '蓝色,棕色', '美国凯迪克银牌奖/美国图书馆协会优秀儿童图书 ', NULL, '5岁以上', 1, '《霍金斯的恐龙世界》是启发绘本馆出版的启发精选美国凯迪克大奖绘本之一，适合5岁以上儿童阅读。1853年，马车咔嗒咔嗒地驶过伦敦街头。水屋·霍金斯不敢逗留，他拿着恐龙模型的草图，匆匆赶往位于南区公园的工作室。今天有两位重要的人物即将造访，他们是英国维多利亚女王和阿尔伯特亲王。水屋正在进行一项令人兴奋的计划：组装恐龙！他和科学家理查·欧文合作，利用出土的，比对当今的生物，拼凑出恐龙的全貌。水屋想让好奇的小孩、很好的科学家，甚至是女王本人，都能仔细看看恐龙，看见历史。从英国水晶宫的恐龙雕塑，到纽约中央公园残破的记忆。一段震撼人心的真实故事，即将呈现在你的眼前。', '科学知识', 1, '恐龙', NULL, NULL, NULL, NULL, NULL, '职业', NULL, NULL, NULL, 0);
INSERT INTO `data` VALUES (20, 324, '影子', '迈克尔·莫波格', '蓝色,白色', '英国红房子童书奖', NULL, '7-10岁', 1, '男孩阿曼和妈妈为躲避战争侵害，在一个山洞里勉强住下，还遇见了一只受伤的狗。阿曼悉心地照顾这只狗，并给它取名叫“影子”。忠诚的“影子”陪伴并保护阿曼和妈妈迈上逃亡之路。一次偶然的机会，阿曼遇到了一群士兵（里面有“影子”的原主人），得知“影子”是一只帮助军队排雷的嗅探犬，阿曼不得不把“影子”留给了它的原主人，也是它原本属于的地方。逃亡之路危险而漫长，和“影子”在一起的特别回忆给了阿曼很多希望和力量，帮助阿曼度过无数黑暗时刻。 在这些士兵的帮助下，阿曼和妈妈终于安全到达英国，并度过了平静幸福的六年。然而，英国政府突然决定要把他们母子遣送回国，为了拯救阿曼和妈妈，他的同学们发起了一场伟大的行动…… ', '成长道理', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '夜晚', NULL, 0);
INSERT INTO `data` VALUES (21, 326, '迟到的理由', '姚佳', '黄色、棕色', '第五届信谊图画书奖图画书创作奖', 40, '3-8岁', 1, '也许，每个上学的孩子都有迟到的时候吧。迟到的时候又会怎么样呢?我们会去想各种理由来为自己辩护。本书以小猪上学迟到为引子，以幽默的想象为连结，趣味性十足，巧妙地表达了人们童年生活的普遍经验，引人入胜。结尾则以一句实话将故事迅速收场，既出人意料，又令人信服，让孩子明白“诚实才是最好的理由”', '成长道理', 1, '猪', NULL, NULL, NULL, NULL, NULL, '性格', NULL, '早晨', '家,学校', 0);
INSERT INTO `data` VALUES (22, 328, '小机灵和小迷瞪', '威廉利普金德/尼古拉斯莫尔德维诺夫', '白色,黄色', '凯迪克金奖', 32, '3岁以上', 1, '两只小黄狗小机灵和小迷瞪在院子里找东西吃，不停地挖啊挖，终于找到了一根骨头。可是骨头到底该归谁呢？他们问了农夫，农夫只关心马车；问了山羊，山羊只想着吃草；问了小剃头匠，他只为了试试手艺……一路上，他们帮了别人做了很多事，可骨头的事依旧没有解决。直到一只大狗的到来，抢了骨头，他们决定打退敌人，共同拥有这根骨头。问题终于迎刃而解了，原来分享就是*好的方法。一本向读者展现*童真画面，体会分享的可贵与快乐的绘本。', '成长道理', 1, '狗', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `data` VALUES (23, 330, '我好担心', '凯文·亨克斯', '彩色', '美国ABBY', 40, '3-6岁', 1, '本书是以老鼠为主角，通过拟人化的描绘，反映出幼儿在家庭、幼儿园的生活情境。故事内容对于捕捉小孩子的内在情绪，深刻而动人；对于孩子成长过程中父母、老师所扮演的角色，提供了深刻的思考与启发。书中的主角叫小莉，是一个性鲜明的小鼠妹，美丽又可爱，娇气又敏感。她生活一个充满爱的温馨的家庭里，有百般呵护她的爸爸妈妈，还有看似唠叨其实非常慈爱的奶奶。小莉平时总是太多地担心，无论大事情小事情还是不大不小的事情，她总是每日每夜、无时无刻地担心：爸妈会不会突然不见了，自己会不会因为变小在澡盆里淹死，墙如果裂开会不会有怪兽爬出来，院子里的大树会不会砸下来，暖气片里会不会爬出一条蛇；她更担心自己心爱的布娃娃小花瓣，怕它受委屈，怕它永远不见了；到游乐场她担心，过生日她担心，过节日她还担心……到了上幼儿园的时候，她的担心更是越来越多，这样的情绪也牵引得全家人跟着她担心……就在读者和鼠爸爸鼠妈妈一起，为初次离开家去上幼儿园的小莉深深担心的时候，故事出现了喜剧性转折的一幕：小莉自己解决了问题——她找到了心仪的小伙伴，和小伙伴玩得非常开心，连她在家里从来不离身的布娃娃小花瓣儿都撒了手……离开幼儿园时她告诉老师：“我一定会来。”“不要担心！”', '情感体验', 1, '鼠', NULL, NULL, NULL, NULL, NULL, '性格', NULL, NULL, '家,学校', 1);
INSERT INTO `data` VALUES (24, 333, '让路给小鸭子', '罗伯特·麦克洛斯基', '绿色', '凯迪克金奖', 63, '3-6岁', 1, '这本书一问世就好评如潮，同时还赢得了美国凯迪克大奖。当然，对这本书的批评声也不绝于耳。多年来，有些读者一直在质疑鸭爸爸“马拉先生”是个游手好闲、不称职的父亲。但是野鸭的爸爸们的的确确会把照料小鸭的工作丢给母鸭，自己一走了之；也许说不定母鸭会觉得这样更好些。所以，关于这种鸟类行为的细节，就像鸭嘴的细节一样，麦克洛斯基只是遵守了自然规律而已。在*后一幕中，鸭子一家的重新团圆深得孩子们的欢心，因为这本书的首次面世时，恰逢第二次世界大战，很多孩子被迫与他们的父亲分离。1987年，波士顿公园150周年庆典时，雕塑家南希舍恩（Nancy Schon）将“马拉夫人”和她著名的家人的铜像复制品安置在了铁栅栏门旁不远处。这个门也因为这本书而声名远播。每逢节假日，成百上千的孩子们就会跑去触摸这些铜塑。现在，这些被摸得发亮的铜塑就像一座不朽的纪念碑，颂扬着《让路给小鸭子》这本书的不朽魅力。', '成长道理', 1, '鸭', NULL, NULL, NULL, NULL, NULL, NULL, '家庭关系', NULL, '城镇', 0);
INSERT INTO `data` VALUES (25, 334, '树真好', '贾尼思•梅•伍德里（文）/马可•塞蒙（图）', '蓝色,绿色', '凯迪克金奖', 28, '3-6岁', 0, '“树真好。高高的大树遮住了天空”；“树长在河边，树长在山谷，树长在高高的山顶上”；“很多很多树在一起，就叫做大森林。树让一切都变得那么好”；“如果你只有一棵树，那也很好。因为，树上有好多树叶。整个夏天，都可以听到风吹树叶，沙沙地响”……作者特别用心地发掘了童年与树木之间的记忆和联系——“我们爬到树上，看各家的院子。我们坐在树杈上，静静地思考。我们还在树上，嬉笑玩耍，扮演海盗”……娓娓道来的散文化的讲述风格，令人在看似琐屑的欣赏过程中油然产生一种亲切而又温情的阅读体验。', '自然生物', 1, NULL, '树', NULL, NULL, NULL, NULL, NULL, NULL, '春,夏,秋', NULL, 0);
INSERT INTO `data` VALUES (26, 339, '小学霸的一天', '大卫•香农', '黄色,黑色', NULL, 40, '3-6岁', 0, '在蜂巢镇，忙忙蜂莉莉是最忙最忙的小蜜蜂了！\n\n除了上学，她还要学习美术、小提琴和芭蕾舞，她在棒球联盟打球，还是“蜜蜂童子军”的一员，做各种饼干义卖。忙忙蜂莉莉为什么这么忙？因为她要全力以赴，做最好的自己！\n\n这样一个小“学霸”，她也会有什么烦恼吗？', '情感体验', 1, '蜜蜂', NULL, NULL, NULL, NULL, NULL, NULL, '朋友关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (27, 340, '鸭子开车记', '大卫•香农', '红色,黄色', NULL, 40, '3-6岁', 1, '大卫·香农经典作品《鸭子骑车记》续篇，时隔14年，这只让人惦记的鸭子终于回来了！疯狂的自行车大冒险结束之后，鸭子在一辆拖拉机前停下了脚步……农场里的马、牛、鸡、狗、老鼠、绵羊……会和他一起开拖拉机吗？拖拉机将驶向哪里？他又将有怎样精彩的奇遇？一场激动人心的历险即将开启！大卫·香农笔下那些充满张力、色彩浓郁的画面，既吸引孩子的目光，更深入孩子的心灵，让他们萌发大胆尝试的勇气。', '情感体验', 1, '鸭', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '朝夕轮转', '城镇,乡村', 1);
INSERT INTO `data` VALUES (28, 341, '七只瞎老鼠', '杨志成', '黑色', '凯迪克银奖', 32, '3-6岁', 1, '七只瞎老鼠改写自印度民间故事——盲人摸象。内容描述七只瞎老鼠在池塘边遇到一个怪东西，谁也不知道那是什么，于是每一天，不同的老鼠轮流去“观察”，并回来报告同伴他们的发现，但是每一次的答案都不叫人满意。最后一天，轮到到白老鼠去了，他从怪东西的上下左右全跑了一遍，最后才下了一个结论，这个怪东西是……这是一个耳熟能详的故事，却又是一本清新脱俗的绘本，用拼贴技巧赋予传统故事“盲人摸象”新的视觉感受。', '成长道理', 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '家庭关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (29, 343, '胡萝卜怪', '阿伦·雷诺兹 文 / 彼得·布朗 图', '黑色,橘色', '凯迪克银奖', 0, '3-6岁', 1, '在山中一个叫跳跳沟的地方，长满了许多的胡萝卜。一只兔子每次路过的时候，都会随意拔出好多胡萝卜来吃。这个事情让胡萝卜们很生气，于是它们想出了一个主意，从这以后，兔子总觉得有长得像胡萝卜的怪物跟在自己后面……2013凯迪克银奖作品《胡萝卜怪》要来喽！', '自然生物', 1, NULL, '萝卜', NULL, NULL, NULL, '儿童', NULL, NULL, '朝夕轮转', NULL, 1);
INSERT INTO `data` VALUES (30, 346, '大家来听音乐会', '劳埃德·莫斯（文）/ 玛乔丽·普赖斯曼（图）', '橘色,紫色', '凯迪克奖', 32, '3-6岁', 1, '本书以向儿童介绍音乐和乐器为主，首先从长号的独奏开始，然后是二重奏、三重奏……每加一种乐器，乐团就加一种声音，到最后组成一个小型的乐团，正式展开一场音乐演奏会。每一位演奏者特别的造型、体态及服装，和他所演奏的乐器相映成趣，代表了乐器声音的高低、大小声、快慢……给读者留下深刻印象。', '艺术修养', 1, NULL, NULL, NULL, NULL, NULL, '音乐', NULL, '群体关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (31, 349, '太阳和月亮为什么住在天上', '埃尔芬斯通·戴罗尔 （文） / [美] 布莱尔·伦特 （图）', '蓝色', '凯迪克银奖', 0, '3-6岁', 0, '很久很久以前，太阳和他的妻子月亮还住在地面上；那时，太阳经常去拜访他的好朋友水。有一天，太阳问水：“为什么你从来都不来我家玩呢？”水说：“如果你想让我来拜访你，你必须建一所很大的房子，因为我们水族的人非常多。”太阳和妻子月亮修了一所大房子，邀请水来。水带着他的人不断地涌入，太阳和月亮被水和他的朋友们挤得无处容身，最后只好跑到天上去生活了。\n\n书中的人物形象设计具有非常显著的非洲民间艺术特质，无论是太阳、月亮还是水中的各种动物形象，都独具特色，令人过目难忘。', '节日民俗', 1, NULL, NULL, NULL, NULL, NULL, '太阳,月亮', NULL, '群体关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (32, 352, '我……有梦', '帕特里克·麦克唐奈', '绿色', '凯迪克银奖，文津奖推荐图书', 35, '3-6岁', 1, '《暖房子国际大奖绘本:我……有梦》内容简介：珍很喜欢在户外和动物们待在一起，坐在她最爱的树上，读《人猿泰山》这本书。她想象着，有一天，她也能在非洲丛林里，帮助所有的动物。让我们跟随珍的身影，一起探索这个神奇的世界，看她是如何美梦成真的吧。\n\n凯迪克大奖作者帕特里克•麦克唐 奈给我们讲述了一个美妙的故事，告诉我们，小女孩珍是如何成长为珍•古道尔博士，成长为一名灵长类动物学家、自然保护志士、人道主义者和联合国和平大使的。这册关于梦想、坚持与爱的传记绘本，记录了当代最杰出女性之一的成长故事，温暖细腻而又鼓舞人心。', '自然生物', 1, NULL, NULL, NULL, NULL, NULL, '儿童', NULL, NULL, NULL, NULL, 0);
INSERT INTO `data` VALUES (33, 354, '先有蛋', '劳拉·瓦卡罗·希格 （文）/劳拉·瓦卡罗·希格（图）', '蓝色,白色,绿色', '凯迪克银奖，苏斯博士奖银奖', 32, '3-6岁', 1, '从蛋变成鸡、蝌蚪变成青蛙、种子变成花朵、毛毛虫变成蝴蝶……。这是一本关于转变的图画书。但它不仅仅是一个故事，还蕴含了对“生命起源和变化”的审美感知。\n\n儿童美育中最重要的是对早期审美感知力的发掘与培养，其中对美的视觉能力是比“看”更高层次的“有意识地看”。\n\n在有趣的洞洞中孩子会发现图像构成和变化的“秘密”、事物之间的奇妙联系。你看，同一个洞洞既是“鸡蛋”又可以是“小鸡”的身子；既是“毛毛虫”又可以是“蝴蝶”的一部分……。在这些“部分”与“整体”的变化中，孩子感知了生命的成长和变化，理解了由“洞洞”构成的更丰富的意义，这就是“有意识地看”的能力。', '自然生物', 1, '蝴蝶,毛毛虫,鸡', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `data` VALUES (34, 356, '穿靴子的猫', '弗雷德·马塞利诺（文）/马尔科姆·阿瑟（图）', '棕色,红色', '凯迪克银奖', 0, '3-6岁', 1, '一只聪明的猫要帮助它穷困潦倒的主人翻身，于是它向主人要了一双靴子和一个布袋，穿过荆棘到森林里打猎。它把每次获得的猎物都先给了国王，它用机智和勇敢打败了富有的食人妖魔，最终它帮助主人得到了国王的青睐和公主的爱慕。\n\n弗雷德·马塞利诺在这本根据三百多年前的佩罗童话改编的图画书中，为读者端出了一道视觉盛宴。他运用一系列华丽而丰富的插图，把淘气的猫和它的主人的冒险经历，以及豪华的场面，热闹而又戏剧性地描绘出来。', '成长道理', 1, '猫', NULL, NULL, NULL, NULL, NULL, NULL, '宠物关系', NULL, '农牧场,城堡', 1);
INSERT INTO `data` VALUES (35, 357, '和我一起玩', ' 玛丽·荷·艾斯', '黄色,绿色', '凯迪克银奖，安徒生文学奖优秀作品奖', 40, '3-6岁', 1, '小女孩走在草地上，陆续邀请小动物和她一起玩，可是每当她提出邀请时，动物们却都离开了，直到她静静地坐在石头上，令人意外的事情发生了……小动物们又回来了！', '情感体验', 1, '兔子,松鼠,鹿,蛇,乌龟,青蛙', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `data` VALUES (36, 360, '一无所有', '婉达盖格', '黄色,绿色,粉色', '凯迪克银奖', 0, '3-6岁', 1, '从前，一位老爷爷外出寻找一只最美的猫，来给自己和妻子做伴。可他带回来了上百只、上千只、上百万只、上亿只、上万亿只猫。哪只才是最美的猫呢?', '成长道理', 1, '狗', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '朝夕轮转', '农牧场', 0);
INSERT INTO `data` VALUES (37, 361, '别让鸽子开巴士！', '莫·威廉斯', '白色,蓝色,橙色', '凯迪克奖', 31, '3-6岁', 1, '巴士司机临时有事，离开前特别嘱咐读者，千万“别让鸽子开巴士！”可是鸽子使出百般伎俩：苦苦哀求、撒个小谎、拍马屁、套近乎、耍赖……千方百计想坐上驾驶座，你会同意让鸽子开巴士吗？', '成长道理', 1, NULL, NULL, NULL, NULL, NULL, NULL, '性格', NULL, NULL, NULL, 1);
INSERT INTO `data` VALUES (38, 363, '会说话的骨头', '威廉·史塔克', '绿色', '凯迪克银奖', 32, '3-6岁', 1, '猪小姐小珠子偶然与一根会说话的骨头交上了朋友，她要回家告诉爸爸妈妈这个惊喜的消息。可是，他俩没到家以前，这根会说话的骨头要先对付一群戴面具的强盗，还要对付一只要把小珠子当晚餐吃的狐狸。这两场斗法又好玩儿又惊险！', '情感体验', 1, '狐狸,猪', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '朝夕轮转', NULL, 1);
INSERT INTO `data` VALUES (39, 364, '这样的尾巴可以做什么？', '史蒂夫·詹金斯', '蓝色', '凯迪克银奖', 40, '3-6岁', 1, '《这样的尾巴可以做什么?》内容简介：大象的鼻子可以喷水洗澡，蝙蝠的耳朵可以用来侦察，角蜥的眼睛可以喷血，猴子的脚可以用来拿东西……《这样的尾巴可以做什么?》是一本没有具体故事情节的书，然而以提问的方式带出各种动物身体不同部位的独特功能，是一本极好的科普认知图书。', '自然生物', 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '群体关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (40, 368, '睡吧，像老虎一样', '玛丽·洛格 （文） / 帕梅拉·扎加伦斯基（图）', '绿色,黄色', '凯迪克银奖，2013年夏洛特·佐罗托童书奖，《华盛顿邮报》年度童书奖', 0, '3-6岁', 1, '一个不想睡觉的小女孩，坚定地对爸爸、妈妈表示她不累、还不想睡。故事中的爸妈，以一种新颖的态度面对孩子不想睡觉的心情：他们没有勉强孩子入睡，而是很有技巧地让小女孩自己换上了睡衣、刷牙、洗脸、躺在床上。小女孩舒服地躺在床上，问爸爸和妈妈：世界上所有的动物都要睡觉吗？从家里的狗和猫，到小蜗牛、大鲸鱼，还有森林里的老虎……爸妈温柔平静地回答她提出来的问题，告诉她各种动物睡觉的地方和睡觉的姿态，然后亲亲她，把灯熄了，对小女孩说她可以整夜醒着。于是，小女孩想象自己和各种动物一样，在自己的窝里安全、温暖又舒适，不知不觉的进入了梦乡。', '情感体验', 1, '猫,狗,熊,蜗牛,鲸鱼', NULL, NULL, NULL, NULL, '儿童', NULL, '家庭关系', '夜晚', '家', 1);
INSERT INTO `data` VALUES (41, 369, '沼泽天使', '安妮·艾萨克斯 （文）/ 保罗·欧·泽林斯基（图）', '棕色', '凯迪克银奖', 0, '3-6岁', 0, '安杰莉卡·朗莱德出生时，跟其他婴儿一样，还没有妈妈高，需要别人的帮助才能爬到树上。她刚满两周岁，就建造了第一座小木屋。长大以后，以“沼泽天使”而闻名的她，可以抓住龙卷风，喝干整个一湖的水。她独自一人拯救移居者，以摆脱恶名远扬的“雷鸣坏蛋”这头可怕的巨熊，她和巨熊从大烟山的山顶一直搏斗到深深的湖底。这是一个以美国边远地区为背景，原创的民间故事。', '情感体验', 0, '熊', NULL, NULL, NULL, NULL, NULL, '能力', NULL, NULL, '城镇', 0);
INSERT INTO `data` VALUES (42, 370, '亚历山大和发条老鼠', '李欧·李奥尼', '黄色,红色', '凯迪克奖', 36, '3-6岁', 1, '小老鼠亚历山大总是被人追打，而他的朋友发条老鼠威利，却是小女孩安妮心爱的玩具。亚历山大羡慕极了，真希望自己也能变成一只发条老鼠，被人抱着，被人爱。可就在魔法蜥蜴要帮他实现愿望的时候，他却改变了主意……', '成长道理', 1, '鼠,变色龙', NULL, NULL, NULL, NULL, NULL, '性格', '朋友关系', NULL, '家', 0);
INSERT INTO `data` VALUES (43, 372, '一寸虫', '李欧·李奥尼', '绿色', '凯迪克奖', 30, '3-8岁', 1, '一条身体很小、一点力气都没有的小小一寸虫，它有很多敌人，像鸟儿，只要一个不小心，就会被鸟儿吃掉。当点心！可是一寸虫每次遇到危险时，总是能化险为夷，因为它知道自己有一个很得意的用处，它会──用自己的身体量东西，所以遇到了危险，它就用这个长处来帮敌人量身体、量尾巴、量脚……转移敌人的注意力。\n\n但是有一天，一寸虫遇到了一个很大的困难。有一只夜莺竟然要一寸虫量量它的歌到底有多长。歌要怎么量呀？如果它量不出来，就要被吃掉了。一寸虫该怎么办呢？别紧张，一寸虫急中生智，想到了一个很棒的方法，它还是用身体一寸一寸地量，最后……', '成长道理', 0, '毛毛虫,鸟', NULL, NULL, NULL, NULL, NULL, '能力', NULL, '朝夕轮转', NULL, 0);
INSERT INTO `data` VALUES (44, 373, '在森林里', '玛莉·荷·艾斯', '绿色,黑色', NULL, 50, '3-6岁', 0, '我戴着一顶新帽子，拿着一只新号角，到森林里去散步。一只大狮子正在打盹，听到我的号角声，就醒来了。它梳好头发，跟在我后面 两个象宝宝在喷水，它们看见了我，穿上衣服，跟着我走了 两只大狗熊坐在树下玩，它们看见了我，带上花生、果酱和勺子，也尾随而来……我身后的队伍越来越大，我们来到一块空地野餐和游戏。捉迷藏时，轮到我找人了，睁开眼睛，所有的小动物都不见了，爸爸出现在我的面前。我该回家了！小动物们等着我，当我到森林里散步的时候…… ', '情感体验', 1, '兔子,猴子,熊,大象,狮子', NULL, NULL, NULL, NULL, '儿童', NULL, '朋友关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (45, 374, '兔子先生和美好的礼物', '夏洛特·佐罗托 ', '蓝色,绿色', NULL, 40, '3-6岁', 1, '《兔子先生和美好的礼物》故事看似简单，写了一个小女孩遇到了一点小麻烦。起初看起来，兔子先生对解决这个难题实在帮不上太大的忙。大家都知道，没人可以将红色的屋顶、黄色的计程车、绿色的毛毛虫或者一片蔚蓝的湖水，当作送给生日礼物。可小女孩对兔子先生说，她的妈妈就是喜欢红色、黄色、绿色和蓝色。所以，兔子先生只好绞尽脑汁去尝试一次了。 \n\n\n小女孩和兔子先生是如何克服重重困难，解决了这个难题的呢？，作者将这个的礼物以故事的形式呈现给读者。 ', '成长道理', 1, '兔子', NULL, NULL, NULL, NULL, NULL, NULL, '朋友关系', '生日', NULL, 1);
INSERT INTO `data` VALUES (46, 375, '青蛙和蟾蜍好朋友', '艾诺·洛贝尔', '绿色', NULL, 256, '5-8岁', 1, '《青蛙和蟾蜍》系列共有四册，分别是：《青蛙和蟾蜍──好朋友》、《青蛙和蟾蜍──好伙伴》、《青蛙和蟾蜍──快乐时光》、《青蛙和蟾蜍──快乐年年》，每册书正文64页，图文参半，各有5个小故事，总共有二十个小故事。故事主要是在述说主角青蛙与蟾蜍这一对好朋友生活当中的种种趣事。文字优美流畅，叙述方式幽默童趣，故事感人又好玩，这两个角色表现出的童心及纯真的友谊将使读者会心一笑。他们的友情与默契贯串所有的故事，让读者在阅读时，从头到尾都沉浸在一种温馨的情绪中。然而，青蛙与蟾蜍特殊的个性、出人意料的行为及想法，又让故事的发展充满幽默风趣的惊喜。作者洛贝尔运用日常生活中最平凡的场境及生活点滴，透过想象的魔力，直接带领读者打开神奇的幸福宝藏，品味最美丽、最真挚的友谊。没有严肃的教条味，也没有沉重的批判思考，满满是至真至情，挑动读者内心深处最原始而纯真的感动。其故事在文字使用上，及文学内涵上都有极佳的阅读价值，是简易阅读书籍中的重要佳作，也是让孩子在阅读较具挑战性的短篇章节小说前的良好暖身材料。', '成长道理', 1, '青蛙', NULL, NULL, NULL, NULL, NULL, '外貌', '朋友关系', '春,夏', NULL, 0);
INSERT INTO `data` VALUES (47, 376, '火车快跑', '唐诺·克鲁斯', '白色', '凯迪克银奖', 32, '3-6岁', 1, '空白的轨道上跑来了一列彩色的火车——红、橙、黄、绿、蓝、紫，再加一个黑色蒸汽火车头，呜呜！咔嚓咔嚓！火车向前走，带你走过颜色之旅，带你走过城市和乡村，它飞奔向前、穿山洞、过隧道，带你走过白天和黑夜……\n\n快！上车吧！我们一起向前走。', '科学知识', 1, NULL, NULL, NULL, NULL, '火车', NULL, '能力', NULL, NULL, NULL, 0);
INSERT INTO `data` VALUES (48, 378, '偷梨子的大怪兽', '汤米·温格尔', '红色,粉色', NULL, 32, NULL, 1, '你上一次恶作剧是什么时候呢？\n\n从最简单的恶作剧中获得快乐，是一种被纯真赐福的能力。\n\n这是一个会让你“大吃一惊”又“哈哈大笑”的故事。正是这种单纯的恶作剧式的快乐，让《大力水手》的导演基恩·戴奇把它翻拍成了动画片。\n\n比故事本身更精彩的，是作者汤米·温格尔藏在每个小角落的小小恶作剧。\n\n把每一页仔细看一遍，你会发现很多“哇，原来这里还藏着这个！”的惊喜。\n\n汤米·温格尔不老老实实地说“时间过了很久”，而是在框上画了只小蜗牛，从上一页的左边，慢慢慢慢地，爬到了下一页的右边。\n\n挂在墙上的装饰鹿角，流下了口水。\n\n锯木棍来制作装怪兽的笼子，把画面外的框也锯断了。\n\n……\n\n那些无比单纯地享受着恶作剧的乐趣——同时也愿意被恶作剧逗乐的孩子，是这个复杂世界的明亮星光。', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, NULL, '外貌', '朋友关系', '四季轮转,傍晚', '城镇', 0);
INSERT INTO `data` VALUES (49, 379, '英雄秃鹰奥兰多', '汤米·温格尔', '红色,白色', NULL, 32, NULL, 1, '这是那种孩子们会特别喜欢的故事，\n\n有沙漠大冒险、有贪心的强盗、还有友好的印第安人，\n\n还有充满魅力的主角——帮助了矿工一家、打败了坏人、和印第安人分享金矿的英雄秃鹰奥兰多。\n\n不过，奥兰多并不是大家印象里那种典型的，凶狠又神气的秃鹰。\n\n他的大眼睛让他看起来有点呆呆的，而且还有些滑稽。\n\n当他戴着花环，成为墨西哥最受欢迎的英雄秃鹰时，\n\n甚至还显得有些手足无措呢！\n\n不得不承认，秃鹰在童书中，并不属于非常受欢迎的动物，\n\n但这并不妨碍人们感受到奥兰多的可爱和善良，\n\n也不妨碍他成为孩子们最喜爱的“动物英雄”。\n\n我们每个人小时候都崇拜英雄，\n\n都喜欢听有关英雄机智地打败坏人、救助路人，最后收获成功的故事。\n\n除此之外，故事里的奥兰多也通过自己的爱心和热心告诉孩子，\n\n帮助别人的时候，你就是英雄。', '成长道理', 1, NULL, NULL, NULL, NULL, NULL, '家庭成员', '能力', NULL, '朝夕轮转', '城镇,沙漠', 0);
INSERT INTO `data` VALUES (50, 384, '小个子先生找新家', '马克斯·维尔修思', '蓝色,黄色', NULL, 36, '5-8岁', 1, '本书讲述的是关于找新家的故事：小个子先生的家遭遇大雨后毁于一旦，由此他开始了一段找新家的历程。他先后待过小鸟、黄蜂和兔子的家，都不适合，最终他接受小个子女士的诚意邀请，在她家长久地住了下来。', '情感体验', 1, '兔子,青蛙,蜗牛,蜜蜂', NULL, NULL, NULL, NULL, NULL, '外貌', '朋友关系', '朝夕轮转', NULL, 1);
INSERT INTO `data` VALUES (51, 386, '从来没人去过的雾岛', '汤米·温格尔', '灰色,蓝色', '《纽约时报》2013年度最佳儿童图画书奖', 48, NULL, 1, '在遥远的海边，有一个从来没人去过的雾岛。\n\n传说中的雾岛惊险可怕，所有去探险的船从来没有回来过。\n\n有一天，费恩和卡拉兄妹俩的小船被汹涌的波涛冲到了雾岛,却发现......', '趣味解密', 1, NULL, NULL, NULL, NULL, NULL, '儿童', NULL, '家庭关系', NULL, '农牧场', 1);
INSERT INTO `data` VALUES (52, 388, '辉夜姬', '清水玲子', '黑色,紫色', NULL, 0, NULL, 1, '岡田晶是個身世迷離的少女，嬰兒時期被人埋在竹林裏，而後才被救出。有一天被兩名叫由和碧的少年強行帶到美軍基地去…', '成长道理', 1, NULL, NULL, '王子,老人', NULL, NULL, '神仙', '身份', '家庭关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (53, 389, '画家与颜色', '贾尼·罗大里（文）/ 瓦莱丽亚·佩特罗尼 （图）', '绿色', NULL, 35, NULL, 1, '《画家与颜色》一贫如洗的画家穷得买不起颜料和画笔，只能拔自己的头发，做了三支画笔。有了画笔还缺颜料，画家于是就向掌管颜色的主人寻求帮助。他的姿态是如此低声下气，他的身体又是那么渺小。各种颜色的主人高大又霸道，面对驱赶，画家只能无趣的走开。但他并未因此放弃，他决定用身体里流淌的血，作为自己绘画的颜料。', '成长道理', 1, NULL, NULL, NULL, NULL, NULL, '精灵', '身份', NULL, NULL, NULL, 1);
INSERT INTO `data` VALUES (54, 390, '三只小猫头鹰', '昆廷·布莱克', '蓝色', NULL, 40, NULL, 1, '三只特立独行的小猫头鹰，带我们来了一场环球旅行，从圣诞节直到第二年的圣诞节，他们钓鱼、跳舞、打盹儿，有时啥也不干。诗歌般活泼灵动的语言配上布莱克奇妙传神的插画，这个故事无疑会令小读者和大读者们百读不厌的。', '艺术修养', 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '家庭关系', '节日', NULL, 0);
INSERT INTO `data` VALUES (55, 392, '彼得与狼', '海茵茨·雅力士', '蓝色,绿色', NULL, 32, '3-6岁', 1, '这是一套让儿童体会古典音乐的故事绘本，包含分册《动物狂欢节》《胡桃夹子》《魔笛》《彼得与狼》。精彩的故事的配以生动的图画，音乐与故事相得益彰，让儿童加深对音乐的理解，让艺术再也没有距离感。随时可以上的音乐课，认识乐器特点，足不出户就有大剧场的体验。', '艺术修养', 1, '狼', NULL, NULL, NULL, NULL, '儿童', '性格', NULL, '早晨', NULL, 0);
INSERT INTO `data` VALUES (56, 393, '老鼠的房子', '伊戈尔•欧尼科夫 / 伊戈尔·奥列伊尼科夫', '蓝色', NULL, 64, '3岁以上', 1, '《老鼠的房子》是一则典型的童话故事。故事讲述洞主小老鼠盖了一栋小房子，好心收留无家可归的小动物们，却不小心引狼入室，令自己也落得无家可归。从文字来看，小动物们相亲相爱，因为丧失对“恶”的警觉性，放狼进屋，导致小屋被夺，所幸灰熊出手相助，然而，也付出“小屋被毁”这一代价。 仅仅从文字理解，这是一个类似于“小红帽”母题的童话故事，向天真烂漫的孩子们提示关于“恶”的存在，和善良的“边界”。然而，如果从画面来看，这又是一个关于童话的现实主义演绎，流淌其中的，是俄罗斯文学一脉相承的人道主义传统和现实主义精神。阔大的都市，城市的边缘，被偷走行李的无依无靠的异乡人小蜥蜴，被老板解雇、孤苦伶仃的清洁工小青蛙，被一把火夺去住所的贫穷画家小松鼠，被房东扫地出门的自由职业者（热爱考古）小兔子，被同是边缘人的洞主小老鼠收容，漂在这所都市的他们，相互慰藉，相互尊重，共同取暖。现实主义是俄罗斯文学艺术的永恒特点，生活永远是一个难解的秘密，而非一盒甜美的巧克力。这样一本图画书，同样让我们感受到了，那流淌在俄罗斯文学血脉中的，对“小人物”命运的同情与关切，那许多“被屈辱和被损害的人们”，默默无闻的崇高的品质。', '成长道理', 1, '鼠,兔子,狐狸,松鼠,熊,狼,青蛙,变色龙', NULL, NULL, NULL, NULL, NULL, NULL, '邻居关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (57, 394, '熊，熊，熊！', '马丁·韦德尔', '黄色,棕色', NULL, 0, NULL, 1, '可爱的画风，趣味的表达，原来密集的熊，竟是满屋狼籍、欲望狂魔的真实写照。孩子需要在不断拥有与放弃中学会成长。', '成长道理', 1, '熊', NULL, NULL, NULL, NULL, '儿童', NULL, '朋友关系', NULL, '家', 0);
INSERT INTO `data` VALUES (58, 395, '下雪天的声音', ' 梅子涵（文）/ 伊戈尔·奥列伊尼科夫（图）', '灰色', '2020陈伯吹国际儿童文学奖到到年度图书（绘本）奖，大鹏自然童书奖', 0, '3-6岁', 0, '下雪的时候，没有一点儿声音。\n天上没有飘的声音，地上没有落的声音。太阳没有照耀的声音，月亮没有微笑的声音…… 总说雪落无声，你听过下雪天的声音吗？那是一种什么样的声音？请你静静感受，用心聆听……', '自然生物', 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '冬', NULL, 0);
INSERT INTO `data` VALUES (59, 396, '勇敢的小象', '塞斯利·乔斯林（文）/ 雷欧纳德·威斯伽德（图）', '红色,黑色,黄色', '年意大利安徒生奖', 0, '3-6岁', 0, '勇敢的小象要开始一次独自的历险了，为此，他做足了准备工作：先穿上足够保暖的衣服，再带上一盏灯，佩戴好一柄剑……一切准备就绪了，勇敢的小象要出发了。只见他毫不畏惧地离开父母，有生头一次，在没有任何人陪伴的情况下走上楼梯，上床睡觉啦。\n\n“独自睡觉”在幼儿成长发育过程中，是一个标志性的重要事件。本书故事展开的方式非常独特，以一种带有悬疑性质的、幽默的游戏形式呈现，而绘者雷欧纳德·威斯伽德所描绘的大象一家的温馨互动，更为故事增添了无限的幽默与乐趣。', '成长道理', 1, '大象', NULL, NULL, NULL, NULL, NULL, NULL, '家庭关系', '冬,夜晚', '家', 0);
INSERT INTO `data` VALUES (60, 397, '我的床边怪兽', '安内莉斯·施瓦茨（文）/ [捷克]柯薇塔·巴可维斯卡（图）', '绿色', NULL, 48, NULL, 1, '《我的床边怪兽》是一个非常富有想象力的故事：小莉琪想要一只怪兽宠物，这只宠物要能飞、能游泳，还有很大很大的力气；可以随意变大或变小，还能做很多很多事情！爸爸妈妈说，这样的怪兽根本不存在。可是，当晚莉琪的床边真的出现了这么一只怪兽——这是一只类似于独角兽的河马——长着人的四肢，一对友好的、亮晶晶的眼睛，还有翅膀和尾巴。怪兽宠物带着莉琪在陆地、空中和海底之间自由地穿梭冒险，完成了一场既刺激、又有点可怕的难忘旅行。\n\n莉琪的怪兽宠物就像一个和富于关怀力量的成年人，它知道如何足够令人兴奋，轮流与莉琪一起兜风，并时刻守护着她。', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, '儿童,怪兽', '性格', '宠物关系', '夜晚', '家', 0);
INSERT INTO `data` VALUES (61, 398, '我的新玩具', '莫·威廉斯 ', '粉色,紫色', NULL, 64, '3-6岁', 1, '本系列一共8本。插图简约，对话幽默，符合低幼读者的心理，这套书通过诙谐有趣的对话描绘了小猪和小象这对好朋友生活中发生的故事，淋漓尽致地展现了孩子的心理，让孩子在笑声中学会珍视友谊，结交挚友。\n\n \n\n《我的新玩具！》 小猪有一个新玩具，可是不知道怎么玩。小象说，这肯定是个沙包吧，于是把它高高地丢到了半空中，结果，咔嚓，玩具碎了！小猪伤心得哭了，小象也有些不好意思了，结果呢？这个玩具真的坏了吗？\n\n \n\n小猪和小象的友谊还能保持吗？', '成长道理', 1, '猪,大象', NULL, NULL, NULL, NULL, NULL, '外貌', '朋友关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (62, 399, '小猪害我打喷嚏！', '莫·威廉斯 ', '绿色,灰色', NULL, 64, '3-6岁', 1, '小象很细心，小猪却大大咧咧。\n\n小猪总是笑嘻嘻，小象可就不一定。\n\n小象总是东操心、西操心，有了他，小猪可以很安心。\n\n小象和小猪是最好的好朋友。\n\n小朋友知道小象是怎么打喷嚏的吗？在《小猪害我打喷嚏！》里，小象将为我们带来传神的表演。想知道小象打喷嚏的威力有多么惊天动地、不同凡响吗？想看小猪被小象的超大喷嚏吹得在空中转体360 度吗？翻开这本可爱的绘本，绝对能够满足你的想象，更让你忍不住想来几个模仿动作，跟着小象一起阿阿阿──嚏！\n\n在潜移默化中培养孩子良好卫生习惯\n\n当小朋友像小象一样不停地打喷嚏时，可能是过敏，也可能是感冒了，这时，该如何让孩子认识这些身体不适的症状呢？或许可以通过阅读《小猪害我打喷嚏！》，告诉孩子如何正确地面对感冒。感冒时带上口罩，打喷嚏时用手帕或卫生纸捂住口鼻，这样才能避免病菌随着飞沫散播，不会把感冒传染给家人朋友，否则就不好玩了！', '成长道理', 1, '猪,大象', NULL, NULL, NULL, NULL, NULL, '外貌', '朋友关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (63, 400, '小猪节快乐！', '莫·威廉斯 ', '粉色,白色', NULL, 64, '3-6岁', 1, '小象很细心，小猪却大大咧咧。\n\n小猪总是笑嘻嘻，小象可就不一定。\n\n小象总是东操心、西操心，有了他，小猪可以很安心。\n\n小象和小猪是最好的好朋友。\n\n对小猪来说，小猪节是一年里最棒的日子，因为是属于小猪的日子。可以大声唱小猪歌，尽情跳小猪舞，还可以大口大口地吃小猪食物！但是，看到小猪和她的猪猪朋友们玩得那么开心，小象心里却很别扭。原来，小象觉得自己不是小猪，小猪节不属于自己，看到小猪和其他小猪玩得那么开心，就觉得自己被冷落了。难道就因为小象长得和大家不一样，就不能一起庆祝小猪节了吗？', '成长道理', 1, '猪,大象', NULL, NULL, NULL, NULL, NULL, '外貌', '朋友关系', '节日', NULL, 0);
INSERT INTO `data` VALUES (64, 401, '一起出去玩吧！', '莫·威廉斯 ', '绿色,灰色', NULL, 64, '3-6岁', 1, '小象很细心，小猪却大大咧咧。\n\n小猪总是笑嘻嘻，小象可就不一定。\n\n小象总是东操心、西操心，有了他，小猪可以很安心。\n\n小象和小猪是最好的好朋友。\n\n小象和小猪约好出去玩，却遇上下雨天，这真是件扫兴的事情！小猪不但很失望，还大声抱怨起来：“这么大的雨，怎么出去玩啊？！”眼见这场突如其来的大雨就要毁掉他们的好心情，这时，只见两条蚯蚓居然在雨中能玩得很开心，小猪小象于是受到了启发，谁说下雨天就一定只能闷闷不乐，晴天才能玩得开心呢？', '成长道理', 1, '猪,大象', NULL, NULL, NULL, NULL, NULL, '外貌', '朋友关系', '雨', NULL, 0);
INSERT INTO `data` VALUES (65, 402, '长鼻子折了！', '莫·威廉斯 ', '黄色,蓝色', '2012年苏斯博士银奖', 64, '3-6岁', 1, '象很细心，小猪却大大咧咧。\n\n小猪总是笑嘻嘻，小象可就不一定。\n\n小象总是东操心、西操心，有了他，小猪可以很安心。\n\n小象和小猪是最好的好朋友。\n\n小象的鼻子受伤了，他非但不觉得伤心，反而非常得意。原来，小象今天的表现简直比特技演员还厉害，他竟然用鼻子举起一头犀牛、两只河马和一架钢琴！举起这么重的东西，并没难倒小象，可小象的鼻子又是怎么受伤的呢？原因绝对出乎你的意料！小猪在听完这个的故事之后，居然也发生了同样的事情！', '成长道理', 1, '猪,大象', NULL, NULL, NULL, NULL, NULL, '外貌', '朋友关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (66, 404, '最爱的，是我', '宫西达也', '绿色', NULL, 39, '3岁以上', 1, '很久以前的一天，霸王龙在小山上发现了几个蛋，当他张开大嘴要把它们吃掉的时候……咔吧咔吧，几只甲龙宝宝出生了。甲龙宝宝把霸王龙当成了爸爸……当甲龙宝宝遇到危险时、当甲龙宝宝间相互产生矛盾时，霸王龙都肩负起一个“父亲”的责任。甲龙宝宝与霸王龙之间的感人的亲情让人为之动容，孩子在听故事之余，更可以潜移默化地体验到爱与被爱的快乐。', '情感体验', 1, '恐龙', NULL, NULL, NULL, NULL, NULL, '外貌', '家庭关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (67, 405, '我是霸王龙', '宫西达也', '绿色,黑色', NULL, 0, '3岁以上', 1, '以前，以前，很久以前，有一只还不太会飞的小翼龙。有一天，他遇到了一头可怕的霸王龙……看看小翼龙和粗暴可怕的霸王龙温馨的故事吧。', '成长道理', 1, '恐龙', NULL, NULL, NULL, NULL, NULL, '外貌', '敌人关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (68, 406, '你真好', '宫西达也', '绿色,蓝色', NULL, 0, '3岁以上', 1, '从前，有一只粗暴的、坏心眼的、狡猾的、任性的霸王龙，他总是欺负弱小。但他掉到海里，被善良的薄片龙救出来后，他就跟薄片龙交上朋友。他们天天在一起，霸王龙想到永远跟薄片龙在一起。可是，一天薄片龙被海里的坏家伙咬……', '成长道理', 1, '恐龙', NULL, NULL, NULL, NULL, NULL, '外貌', '朋友关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (69, 407, '遇见你，真好', '宫西达也', '绿色,黑色', NULL, 39, '3岁以上', 1, '这一集的故事发生在海边的红果树林：棘龙宝宝来摘红果子，遇到了来捕食的霸王龙，当霸王龙张开大口扑向棘龙宝宝时，一阵地动山摇，大地裂开了，他们被困在了分裂出去的小岛上。一段被迫的海上漂泊，使一对狭路相逢的天敌变为了孤岛上的互助伙伴。巧妙的故事安排告诉我们：残酷的生存法则+意外的海上漂泊=弱肉不一定强食。\n\n这是日本绘本大师宫西达也恐龙系列中文版引进的第六集。本集延续了前书的叙事风格，生动的文字，夸张的图画，作者用故事传递着浓浓的爱！', '成长道理', 1, '恐龙', NULL, NULL, NULL, NULL, NULL, '外貌', '朋友关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (70, 408, '永远永远爱你', '宫西达也', '绿色,白色', NULL, 40, '3岁以上', 1, '《蒲蒲兰绘本馆:永远永远爱你》慈母龙妈妈从森林里捡回了一个蛋宝宝。令人惊讶的是，蛋里跳出来的竟是可怕的霸王龙宝宝！善良的慈母龙妈妈不忍心把霸王龙宝宝扔掉，把他留了下来，并给他取名叫良太。在妈妈的关爱下，良太一天天长大了。一天，良太独自去采红果子，遇到了另一个霸王龙，发现了自己的身份……\n\n宫西达也用一贯独特的绘画手法，讲述着慈母龙妈妈的伟大。也让孩子感受到，真情无价，爱可以超越一切。', '成长道理', 1, '恐龙', NULL, NULL, NULL, NULL, NULL, '外貌', '家庭关系', '朝夕轮转', NULL, 0);
INSERT INTO `data` VALUES (71, 409, '我爱你', '宫西达也', '绿色,蓝色', NULL, 40, '3岁以上', 1, '以前，以前，很久以前，恐龙们住在世界上各个地方。住在北方的恐龙和住在南方的恐龙完全不一样，颜色、长相，甚至说的语言都不一样。住在北方的霸王龙在下雪天里找不到吃的，饿的实在不行了，被一只它蓓翼龙骗去南方，它蓓翼龙咬伤后，看见了三只正在吃红果子的三只平头龙……', '成长道理', 1, '恐龙', NULL, NULL, NULL, NULL, NULL, '外貌,能力', '朋友关系', '冬,雪', NULL, 0);
INSERT INTO `data` VALUES (72, 414, '鸭子骑车记', '大卫·香农 ', '蓝色,白色', '美国图书馆协会好书奖，《纽约时报》年度优秀绘本', 35, '3-6岁', 1, '鸭子骑车？这可真是个疯狂的想法！但有一只鸭子真的办到了！农场里的马、牛、羊、鸡、狗、老鼠……对鸭子骑车这件事有什么看法呢？在一个平平常常的下午，动物们干了一件很不平凡的事情……作者通过这个让人兴奋又愉悦的故事，生动地描绘出孩子勇于探险的心理特征，让孩子读后产生极大的满足感。书中各种动物的语言和表情都十分传神，和孩子一起朗读，会让孩子兴致勃勃地融入其中。而大胆的笔触和鲜艳的色彩，既吸引孩子的目光，更深入孩子的心灵，让他们萌发大胆尝试的勇气。', '情感体验', 1, '鸭', NULL, NULL, NULL, NULL, NULL, NULL, '群体关系', NULL, '农牧场', 1);
INSERT INTO `data` VALUES (73, 416, '小仙女爱丽斯', '大卫·香农 ', '白色', NULL, 30, '3-6岁', 1, '几乎是《大卫，不可以》的女孩版。艾丽斯有无穷无尽的魔法，将爸爸的饼干“变”成她的，之后再补偿性地为公爵大人（爸爸）变出新的衣服和王冠；用魔棍指挥树叶飘落下来，并用魔棍在水中画出神奇的图画；让自己隐形，还能将难吃的燕麦粥变成香喷喷的蛋糕……。看完这本书，你或许能从书中找到自己小时候或小女儿的影子。', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, '精灵', '能力', NULL, NULL, NULL, 1);
INSERT INTO `data` VALUES (74, 424, '大卫，圣诞节到啦！', '大卫·香农 ', '绿色,黄色', NULL, 29, '3-6岁', 1, '永远顶着几根不乖顺头发的大卫在圣诞节里有点儿忘乎所以，那么多好吃的、好玩的，“淘气榜”第一名的大卫实在没办法不闯祸。他试图偷偷吃掉饼干、为了等圣诞老人不肯睡觉、积雪尚未融化光着屁股就跑出去，还在地上边尿尿边画出自己的名字!不得不感叹大卫旺盛的精力和挑战妈妈底线的勇气。\n\n不过，圣诞节与以往最大的不同在于有“圣诞老人”作为妈妈的杀手锏，一封署名“爱你的圣诞老人”的来信让忘乎所以的大卫哭得好伤心。信里写着“没有你的礼物，因为你淘气来着。”捧着信哭泣的大卫真让人心疼，难道大卫真的要过一个没有礼物的圣诞节吗？\n\n当然不会啦！不管多么淘气的孩子，爸爸妈妈都会让他在一年最重要的节日里，享受到最大的欢乐。', '成长道理', 1, NULL, NULL, NULL, NULL, NULL, '儿童,圣诞老人', NULL, NULL, '节日', '家', 1);
INSERT INTO `data` VALUES (75, 431, '飞天大面包', '深见春夫', '蓝色', NULL, 36, NULL, 1, '隧道里飘来一股好闻的香味,原来是一根面包从里面钻了出来，小伙伴们坐到了面包里，快乐的旅行开始了。面包飞上天空，来到了面包王国。没想到，一头怪兽冒了出来，把大家全吞到肚子里了。这下坏了，小伙伴们还能回去吗？', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, '儿童', '能力', NULL, NULL, '交通工具', 1);
INSERT INTO `data` VALUES (76, 432, '了不起的面包怪', '深见春夫', '白色,黄色', NULL, 0, '3-6岁', 1, '隧道里钻出来一群面包怪，它们邀请小朋友们去面包王国玩耍。面包怪带小朋友们钻进了一大朵云彩里，里面就是面包王国啦!脸蛋面包的脸蛋可舒服了，大家一起开心地跳起了舞·.....突然，抓斗直升机抓走了一个小朋友，了不起的面包怪能把小朋友救回来吗?', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, '儿童,怪兽', '能力', '朋友关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (77, 433, '捣蛋猫米乐', '深见春夫', '黑色,白色', NULL, 36, '3-6岁', 1, '深见春夫编著的《捣蛋猫米乐》是“深见春夫睡得香图画书系列”系列丛书其中一本，本书故事情节有趣活泼，很有游戏性，画风想象力丰富，色彩明丽，造型可爱又有趣，非常符合幼儿心理特点和审美趣味。是一本激发幼儿想象力和创造力的游戏绘本，适合3-6岁儿童阅读。', '趣味解密', 1, '猫', NULL, NULL, NULL, NULL, '儿童', NULL, '宠物关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (78, 434, '会走路的丸子小弟', '深见春夫', '棕色', NULL, 36, '3-6岁', 1, '被吃掉的一串丸子从桌上溜下来，去公园、马路游玩，结识了人类小伙伴，通过管道走进了丸子王国。这本比较悲伤，找到同类的丸子们还是回到现实世界，口号是乘着还没被吃尽情玩耍吧。虽然最后丸子们没回家，但想到它们总会结束旅行就有点惆怅。', '成长道理', 1, NULL, NULL, NULL, NULL, NULL, '儿童', '外貌', NULL, NULL, '公园', 0);
INSERT INTO `data` VALUES (79, 445, '怪兽云', '深见春夫', '蓝色', NULL, 36, '3-6岁', 1, '有一天，城市的上空飘来了一块巨大的黑云。突然，黑云变成了一头怪兽，琢磨着要吃城里的孩子。它觉得不喜欢吃蔬菜的男孩、每天玩一小时以上电子游戏的男孩以及屁股蛋上各有一颗痣的男孩最好吃。满足这三个条件的男孩只有一个了。怪兽云能抓到这个小男孩吗', '趣味解密', 1, NULL, NULL, NULL, NULL, NULL, '家庭成员,儿童,云朵', '能力', NULL, NULL, NULL, 0);
INSERT INTO `data` VALUES (80, 446, '快乐面包王国', '深见春夫', '棕色', NULL, 0, '3-6岁', 1, '巨大的云朵里头，有一个面包王国。一天，城堡公主的生日到了，很多小朋友都收到了公主的邀请，大家坐上面包火车去参加公主的生日派对。可这一路并不顺利，火车要穿过一座大桥的时候，淘气的恐龙宝宝把桥咬断了，大家全都掉进了牛奶河里……', '趣味解密', 1, '兔子,大象,狮子,蟹', NULL, '公主', NULL, NULL, '儿童', NULL, '家庭关系,朋友关系', '生日', '城堡', 1);
INSERT INTO `data` VALUES (81, 447, '奇幻森林', ' [英] 拉迪亚德·吉卜林 著 / [比] 昆汀·格里班 绘 / [英国] 鲁德亚德·吉卜林', '绿色', NULL, 0, NULL, 0, '本书节选自英国著名诗人、作家，诺贝尔文学奖获得者吉卜林的经典著作——《奇幻森林》，讲述了少年毛克利曲折的成长历程，既有原始丛林的野性之美，又有群体社会的权力之争，一个个出人意料又顺理成章的情节架构起一部属于孩子们的丛林史诗。', '成长道理', 1, NULL, NULL, NULL, NULL, NULL, '儿童,怪兽,衣物', '外貌,能力', '朋友关系,群体关系', '夜晚', '天空', 0);
INSERT INTO `data` VALUES (82, 448, '睡得好香', '深见春夫', '蓝色', NULL, 36, '3-6岁', 1, '“睡得香”系列是深见春夫为家长和小朋友创作的睡前故事图画书。这些故事将在现实中活动了一整天的孩子带入另一个充满想象力的世界。开头惊险刺激，中间热闹有趣，最后归于平静……十分符合儿童睡前大脑皮层反射及发育特点，在日本学界有较高评价，被誉为“不仅是美好一天的结束曲，更是开启孩子美梦的金钥匙”。\n\n高志刷完牙准备睡觉，但他还不困，于是让妈妈给他来读图画书。读了一会儿，妈妈的声音就听不到了。他睁开眼睛一看，床边居然站着一只小老虎。小老虎邀请高志到外边走走。他们和其他几个孩子一起在公园里踢球，在树林里飞翔，甚至在云朵里散步……高志会不会高兴得忘记回家呢？', '情感体验', 1, '大象,老虎,鲸鱼', NULL, NULL, NULL, NULL, '儿童', '能力', '朋友关系', '夜晚', '公园,天空', 0);
INSERT INTO `data` VALUES (83, 449, '拼图王国', '深见春夫', '粉色,蓝色', NULL, 0, '3-6岁', 1, '“想得美”系列是深见春夫**代表性的图画书作品，包含五个张扬着想象力和富于游戏精神的小故事。这些故事把孩子们带进天马行空的自由想象空间，在那里，有可怕的妖怪，也有善良的伙伴；有未知的危险，也有战胜困难逃离危险的喜悦，充满了刺激和挑战。这种突破现实、看似荒诞不经的想象，是对孩子的现实世界与心理世界的必要补充，更是对未来世界的延伸与探索，给他们带来成长的勇气和力量。\n\n裕太在家里玩猩猩拼图。还差一块就拼好了，可是那块拼图却不见了。裕太在房间里找啊找，没想到竟然来到了拼图王国，更奇怪的是连他自己也变成了拼图。经历了这段奇遇，裕太能找到鼻子拼图，并且变回原来的样子吗？', '趣味解密', 1, NULL, NULL, NULL, NULL, NULL, '儿童', NULL, NULL, NULL, '家', 1);
INSERT INTO `data` VALUES (84, 450, '小蛇餐厅', '深见春夫', '粉色', NULL, 0, '3-6岁', 1, '小蛇在森林里开了一家餐厅，但是小蛇没有手，需要光顾的客人自己动手做菜。第一位客人是馋嘴小猪，做菜的过程真是难倒了小猪:要一边唱歌一边做菜;一边念咒语一边做菜;还要一边哈哈大笑一边做菜··..··后来，小猪能吃到好吃的让人感觉飞上天的美味吗', '情感体验', 1, '猪,蛇', NULL, NULL, NULL, NULL, NULL, '能力', '邻居关系,群体关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (85, 451, '怪物们快出来', '深见春夫', '蓝色', NULL, 36, '3-6岁', 1, '“睡得香”系列是深见春夫为家长和小朋友创作的睡前故事图画书。这些故事将在现实中活动了一整天的孩子带入另一个充满想象力的世界。开头惊险刺激，中间热闹有趣，最后归于平静……十分符合儿童睡前大脑皮层反射及发育特点，在日本学界有较高评价，被誉为“不仅是美好一天的结束曲，更是开启孩子美梦的金钥匙”。\n\n树洞里飞出一个小妖怪。他飞过菜地，穿过蛋糕店，召集了所有的小妖怪们。大家相约到一所房子里去，并给房子里的棉花糖妖怪画了一张脸。可是这时候来了一台大吸尘器，把小妖怪们一个个都吸了进去……小妖怪们能逃出来吗？', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, '怪兽', '外貌,能力', NULL, '夜晚', NULL, 0);
INSERT INTO `data` VALUES (86, 452, '奇妙迷宫', '深见春夫', '绿色', NULL, 0, '3-6岁', 1, '“想得美”系列是深见春夫代表性的图画书作品，包含五个张扬着想象力和富于游戏精神的小故事。这些故事把孩子们带进天马行空的自由想象空间，在那里，有可怕的妖怪，也有善良的伙伴；有未知的危险，也有战胜困难逃离危险的喜悦，充满了刺激和挑战。这种突破现实、看似荒诞不经的想象，是对孩子的现实世界与心理世界的必要补充，更是对未来世界的延伸与探索，给他们带来成长的勇气和力量。\n有一次，庆太挑战一个非常难走的迷宫。想不到的是，奇妙的事情发生了。迷宫渐渐地变宽了，两边还竖起了墙壁。迷宫前面，是一片大森林，庆太在大森林里彻底迷路了……他在这里遇见了谁，*后能否走出迷宫呢？\n', '情感体验', 1, '熊', NULL, NULL, NULL, NULL, '儿童', NULL, NULL, NULL, NULL, 1);
INSERT INTO `data` VALUES (87, 453, '云朵怪兽', '深见春夫', '蓝色', NULL, 0, '3-6岁', 1, '天那边出现了一个云朵怪兽，云朵怪兽特别喜欢小孩它带孩子们飞上了天，一会儿变成水母，一会儿变成太阳，会儿又变成了秋干.....·突然对面来了一个打着响雷的超级云朵怪兽，想把云朵怪兽和孩子们吃掉。云朵怪兽能带孩子们安全离开吗', '趣味解密', 1, NULL, NULL, NULL, NULL, NULL, '儿童,云朵', '外貌,能力', NULL, '朝夕轮转', '公园,天空', 0);
INSERT INTO `data` VALUES (88, 455, '奇怪的妈妈', '白希那', '棕色', NULL, 40, '3-6岁', 1, '昊昊生病了，老师让他早点回家休息，还给昊昊妈妈打电话说明情况。\n\n妈妈没办法立刻赶回家，她打电话给自己的妈妈，让老人先去照顾一下昊昊。\n\n阴差阳错，电话打给了制造云雨的仙女婆婆。\n\n善良的仙女把生病的昊昊照顾得很周到，很快，昊昊就安心地躺下休息了。\n\n昊昊妈妈忙完也急忙赶回家，给休息中的昊昊一个大大的拥抱。妈妈和昊昊都没有看到仙女奶奶离开的身影。\n\n不过，两个人吃了一顿仙女奶奶留下的美味晚餐。', '成长道理', 1, NULL, NULL, NULL, NULL, NULL, '儿童,神仙', '能力', '家庭关系', '雨', '家', 0);
INSERT INTO `data` VALUES (89, 456, '小叽的妈妈', '白希那', '棕色', NULL, 44, '3-6岁', 1, '故事讲述了一只叫喵呜的猫，它恶名昭著，它不仅很胖，爱吃，而且还喜欢欺负弱小的动物。那些刚下的鸡蛋，是喵呜喜欢的零食。有一个春天的早上，喵呜路过鸡笼，偷吃了一个鸡蛋。后来，猫的肚子一天天变大，它居然生出一只小小的、黄色的、非常可爱的小鸡“小叽”。从此以后，这只猫就成为小叽的妈妈，猫不仅给它美味的食物，还保护小叽不被恶狗欺负，喵呜变得特别温柔。', '成长道理', 1, '猫,鸡', NULL, NULL, NULL, NULL, NULL, '性格', NULL, '春,早晨', NULL, 0);
INSERT INTO `data` VALUES (90, 457, '梦里尝到的苍蝇', '白希那', '棕色', NULL, 48, '3-6岁', 1, '一个纯净、善良的儿童世界。\n\n池塘里有一个青蛙大哥哥，\n\n在照顾蝌蚪弟弟妹妹们。\n\n嗖——啪！\n\n他一整天都在抓苍蝇，抓了一只又一只。', '自然生物', 1, '青蛙', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '家庭关系', NULL, '水域', 1);
INSERT INTO `data` VALUES (91, 458, '奇怪的客人', '白希那', '灰色', '德国慕尼黑国际青少年图书馆“白乌鸦奖”', 48, '3-6岁', 1, '一个雨天的午后，\n家里来了一位奇怪的客人，\n他吃了面包会放屁，\n吃了冰激凌会下雪，\n大哭大闹引得雷雨交加……\n姐弟俩要怎么送走这个小家伙？', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, '儿童,水', '能力', '家庭关系', '早晨,夜晚', '家', 0);
INSERT INTO `data` VALUES (92, 461, '昨天晚上', '白希那', '粉色', NULL, 28, '3-6岁', 1, '一栋热闹的公寓里，\n住着狐狸先生、羊阿姨、狗狗夫妇、\n鸭子奶奶、斑马、山羊……\n大家都在为过节做准备。\n原本没有任何交集的他们，\n因为一只失而复得的毛袜子，\n度过了一个温暖的夜晚。', '情感体验', 1, '猫,狗,兔子,狐狸,羊,鸭,鸟', NULL, NULL, NULL, NULL, NULL, NULL, '邻居关系,群体关系', '早晨', '城镇', 0);
INSERT INTO `data` VALUES (93, 462, '我是狗', '白希那', '白色', NULL, 48, '3-6岁', 1, '我是狗，刚一断奶，就来到东东家，\n\n虽然也会想念妈妈和其他兄弟姐妹，\n\n但是更重要的是一起生活的家人。\n\n尤其是五岁的东东，耍赖，哭鼻子，还尿裤子，\n\n没办法，我要一直保护他……', '情感体验', 1, '狗', NULL, '老人', NULL, NULL, '儿童', NULL, '宠物关系', '朝夕轮转', '家', 0);
INSERT INTO `data` VALUES (94, 463, '云朵面包', '白希那', '蓝色', '年度文学类插图奖', 35, '3-6岁', 1, '不是每个孩子都可以从雨树上摘下云朵，不是每个妈妈都能将云朵加糖发酵，也不是每个爸爸都可以在沙丁鱼罐头一样的车上吃到云朵早餐。就是那么一点点，一点点从天而降的想象，生活就像云朵一样轻盈地飘了起来，飞过城市的上空，小心翼翼而惊喜非常。这就是被童话照亮的瞬间啊，此刻你有没有发觉，自己的心正在变得好柔软，并且发出美丽而轻微的叹息呢？\n\n作为用图画书来演绎的童话，《云朵面包》还别具一种新颖的魅力。它混合了多种艺术的表现手法，除了绘画外，还大量采用了摄影、剪纸、布艺的方法，通过精心的拼贴来构图，加以暖暖的灯光照射，让画面充满了质感，富有浓浓的生活气息。', '成长道理', 1, NULL, NULL, NULL, NULL, NULL, '云朵', '能力', '家庭关系', '早晨,雨', '家,城镇', 1);
INSERT INTO `data` VALUES (95, 468, '募捐时间', '方素珍/徐开云', '红色,灰色', NULL, 48, '3-8岁', 1, '地震破坏了一座美丽的村子。小珍珠想为灾区的小朋友募捐，但是大街上人来人往，没有人往她的募捐箱里投钱。她垂头丧气地走进一座森林，遇见了大象、小浣熊、狮子、长颈鹿……', '自然生物', 1, '猴子,猪,熊,鹿,大象,狮子,乌龟', NULL, NULL, NULL, NULL, '儿童', '性格', '朋友关系,群体关系', NULL, '乡村', 1);
INSERT INTO `data` VALUES (96, 469, '当鳄鱼遇见熊猫', '方素珍', '绿色', NULL, 0, '3-6岁', 1, '“花婆婆”方素珍新刊的原创绘本《当鳄鱼遇见熊猫》讲述的是：大树下，一只鳄鱼正在看杂志。可爱的熊猫走过来跟她聊天。忽然，熊猫看见鳄鱼对着杂志上的女士先生叫妈妈和爸爸，他觉得很奇怪。鳄鱼伤心地说，女士手上的包包和先生腰上的皮带，是人类抓鳄鱼去做成的皮件。熊猫也难过地诉说家族被送去动物园的坎坷遭遇。他们很有默契地一起叹气：唉！到底发生了什么事呢？请大家一起来阅读这本可爱幽默，又略带揶揄的“花婆婆”*创作的绘本吧。\n绘本特别请三叶草故事家族发起人李迪撰写了 “绘本赏析”《我们还能走多远》，请小学语文教师倾情设计了“导读手册”，为绘本课堂教学和亲子共读设计生动有趣的阅读活动，还请画家设计了有趣好玩的“视觉游戏”，内容丰富、形式多彩，把绘本打造成可读、可学、可玩的“三合一”*个性和阅读张力的原创绘本。\n\n大树下，一只鳄鱼正在看杂志。\n可爱的熊猫走过来跟她聊天。', '自然生物', 1, '鳄鱼', NULL, NULL, NULL, NULL, NULL, '外貌,性格,能力', '朋友关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (97, 470, '咚咚咚', '方素珍', '橙色', NULL, NULL, '3-6岁', 1, '咚、咚、咚！\n　　是谁在敲门？\n　　……\n　　咚、咚、咚！\n　　这家的小主人回来了，\n　　它会是谁呢？\n　　有趣的谜题请你猜一猜。', '趣味解密', 1, '鹿,大象,老虎,乌龟,鳄鱼', NULL, NULL, NULL, NULL, NULL, '外貌', '群体关系', NULL, '家', 0);
INSERT INTO `data` VALUES (98, 471, '哼，我生气了', '方素珍', '蓝色', NULL, 0, '3-6岁', 1, '因为月亮吵架了，最后他们俩因为想念对方又和好了，各自说着为月亮的变化。', '情感体验', 1, '鼠,恐龙', NULL, NULL, NULL, NULL, NULL, '外貌', '朋友关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (99, 472, '家', '方素珍', '蓝色', NULL, 0, '3-6岁', 1, '天空是谁的家？\n　　白云是谁的家？\n　　大树是谁的家？\n　　……\n　　这颗心是谁的家？\n　　你能猜到吗？', '自然生物', 1, '青蛙,蝴蝶,毛毛虫,鸟', '花朵,树', NULL, NULL, NULL, '儿童,太阳,云朵', NULL, NULL, NULL, NULL, 0);
INSERT INTO `data` VALUES (100, 474, '你想要一颗星星吗', '方素珍', '蓝色,黑色', NULL, 0, '3-6岁', 1, '翻开《你想要一颗星星吗》，故事在书名的追问中一点点展开，“你想要一颗星星吗？我把整片夜空送给你。你想要一朵玫瑰吗？我把整个花园送给你。你想要一片枫叶吗？我把整座森林送给你……”在三个相同结构的句子之后，作家将语言的叙述方式一转，变成了“如果你想要一朵白云，我请清风送过来……”出人意料的描述，带来如清风般明澈的阅读世界，同时充满爱的表达，给孩子爱的启蒙。', '情感体验', 1, '熊', '花朵,树', NULL, NULL, NULL, '星星,风,云朵', NULL, NULL, NULL, NULL, 0);
INSERT INTO `data` VALUES (101, 476, '我和小溜溜坐一起', '方素珍', '绿色,橙色', NULL, 28, '3-6岁', 1, '小兔子和小溜溜坐一起，\n　　他想和小溜溜做朋友，\n　　却不敢和小溜溜说话。\n　　他该怎么办呢？\n　　他和小溜溜会成为好朋友吗？', '成长道理', 1, '兔子,猪', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '朋友关系,同学关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (102, 477, '我们去火星', '方素珍', '蓝色,黄色', NULL, 0, '3-6岁', 1, '2015年9月28日，美国国家航空和宇宙航行局（NASA）宣布,在火星表面发现了存在液态水的强有力证据。太空探索的终极梦想就是寻找到外太空的生命，而寻找液态水则是步。火星上真的会有火星人吗？地球人会开启火星探险之旅吗？你想去火星吗？让我们一起来读一读这本关于火星的绘本。\n《我们去火星》是一本有趣的科普童话绘本。这本书就像是一本侦探小说！有很多线索在等待着读者们去拼凑出答案：羊咩咩因为懂得看书找资料，所以知道去火星要带什么东西；猪皮皮因为迟到又拖拖拉拉，冰箱塞不进宇宙飞船里，所以留在地球上；更大的秘密是，三个好朋友都没有冷静地弄清楚到底怎么回事儿，就慌慌张张地“逃哇！去火星”，哪里晓得这一切都是地洞里的地鼠搞的鬼！\n绘本特别请国家图书馆少儿馆馆长王志庚教你如何在绘本阅读过程中《与孩子玩一场问答游戏》，请小学语文教师倾情设计了“导读手册”，为绘本课堂教学和亲子共读设计生动有趣的阅读活动，还请画家设计了有趣好玩的“火星迷宫”游戏，内容丰富、形式多彩，把绘本打造成可读、可学、可玩的“三合一”*个性和阅读张力的原创绘本。', '科学知识', 1, '猪,熊,羊', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '朋友关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (103, 478, '小蜗牛', '方素珍', '蓝色,白色', NULL, 0, '3-6岁', 1, '《小蜗牛》收录了作家方素珍创作的18首童谣。这些童谣用字简洁，富有韵律，符合幼儿的审美习惯，而且描写的都是可爱的动物，有小蜗牛、小黄狗、小猪、大白鹅、大嘴鸟等，可以让孩子在朗朗上口的朗诵中享受阅读乐趣，同时了解各种动物的有趣的特点和习性。', '自然生物', 1, '猫,狗,兔子,猴子,猪,牛,鳄鱼,蜗牛,蚂蚁,蜻蜓,蜜蜂,鸡,蟹', NULL, NULL, NULL, NULL, NULL, '外貌,能力', '群体关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (104, 479, '音乐在我的口袋里', '方素珍', '紫色,黄色', NULL, 0, '3-6岁', 1, '《音乐在我的口袋里》讲述了一群热爱音乐的小动物的故事。因为希望自己更有气质，大熊和一群好朋友去报名参加森林气质非凡音乐学校的合唱团。在听了大家的发声练习之后，黄莺老师评价不是说大熊五音不全，就是说青蛙舌头太长，单音都唱不准……大家又去报考山羊老师的乐器班，一样是乘兴而去，败兴而回。音乐学校也招不到一个学生。大熊和青蛙、兔子、狐狸另辟蹊径，成立了四角酷音乐经济公司，筹划了“气质冲天音乐会”，让森林里的小动物们聆听音乐名师的美声和旋律。', '艺术修养', 1, '兔子,狐狸,熊,羊,青蛙', NULL, NULL, NULL, NULL, NULL, '外貌,职业,能力', '邻居关系,群体关系', '夜晚', NULL, 0);
INSERT INTO `data` VALUES (105, 480, '拼拼凑凑的变色龙', '艾瑞·卡尔', '彩色', NULL, 40, '2-8岁', 1, '艾瑞•卡尔用美丽的拼贴画讲述了一个变色龙的故事：它会抓苍蝇，也会变换自己身上的颜色，可是它还觉得不够好玩。直到有一天，它来到一个动物园，突然发现自己的外形也能够变得像其它动物一样。于是，它变得像熊一样大，变出了火烈鸟的翅膀，狐狸的尾巴，鹿角，还有长颈鹿的脖子……最后，它成了一个滑稽的四不像。但是，他快乐吗？一点儿也不……', '情感体验', 1, '变色龙', NULL, NULL, NULL, NULL, NULL, '外貌,能力', NULL, NULL, NULL, 0);
INSERT INTO `data` VALUES (106, 482, '袋鼠也有妈妈吗？', '艾瑞·卡尔', '红色,绿色,白色', NULL, 24, '0-3岁', 1, '我们人类都有妈妈，那么猫、狗、企鹅这些小动物们也有妈妈吗？这可能是每个小朋友曾经有过的疑问。翻开艾瑞•卡尔的纸板书《袋鼠也有妈妈吗？》，我们就会得到答案了。《信谊•宝宝起步走:袋鼠也有妈妈吗?》的故事非常简单，一个没有出场的小孩一遍又一遍地问着同一个问题：“袋鼠/狮子/猴子/海豚也有妈妈吗？”而回答也是始终如一的：一个大大的、鲜艳的“有！”接着，作者还用具有抚慰性的语调告诉孩子：“就像我有你也有”，每个孩子都有妈妈。艾瑞•卡尔用他的图画，为小朋友介绍了十一种可爱动物的外型和迷人的神态，小读者不但从图画中得到视觉上的快乐，也从作者对母爱的一再重申之中，得到了亲情的抚慰。\n\n海报：', '情感体验', 1, '狐狸,猴子,熊,羊,大象,狮子,长颈鹿,海豚', NULL, NULL, NULL, NULL, NULL, NULL, '群体关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (107, 483, '棕色的熊', '比尔 马丁（图）/ 艾瑞·卡尔（文）', '棕色', NULL, 32, '0-3岁', 1, '“棕色的熊、棕色的熊，你在看什么？”“我看见一只红色的鸟在看我。”“红色的鸟、红色的鸟，你在看什么？”“我看见一只黄色的鸭子在看我。”……随着这样一问一答的句子、重复的韵律与节奏，一本充满各种动物与色彩的图画书在孩子面前慢慢展开。这是一本备受全世界小朋友喜爱的图画书，整本书都是用艾瑞·卡尔一贯具有创意性的贴画构成，色调鲜明、活泼。幼小的孩子可以从这本书中学习语法、欣赏预测的节奏性，并学会各种动物的命名，更可以认识颜色，增广视野。', '线条色彩', 1, '猫,狗,熊,马,羊,青蛙,鸭,鸟,鱼', NULL, '老师', NULL, NULL, '儿童', '外貌', '师生关系,群体关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (108, 485, '1，2，3到动物园', '艾瑞·卡尔', '棕色', '意大利博洛尼亚童书展插画大奖', 34, '0-3岁', 1, '书的开篇，驶来了一列火车，列车上载满了各种动物，1头大象、2只河马、3只长颈鹿、4只狮子……来了，都来了，让我们坐上艾瑞•卡尔的快乐列车去动物园吧！一路上数啊数，最后数来了十只彩色的鸟儿。如果细心一点，你会发现每一页都有一只小小的老鼠藏在角落里，在书页的底下，列车的车厢和动物也会不断增加，当载满动物到动物园后，车厢就空了。', '自然生物', 1, '猴子,熊,大象,狮子,长颈鹿,蛇,鳄鱼,鸟', NULL, NULL, NULL, NULL, NULL, '外貌', '群体关系', NULL, '动物园', 0);
INSERT INTO `data` VALUES (109, 486, '小种子', '艾瑞·卡尔', '红色', NULL, 32, '3-8岁', 1, '秋天来了，大风把种子们高高地扬起来，带到远方。在这些种子中间，有一粒特别细小，比别的所有种子都小。这粒小种子能跟上别的种子呢？这些种子又会被带去哪里呢？\n\n《小种子》是一本寓教于乐的图画书，趣味性、知识性兼具，作者艾瑞·卡尔借由许多小种子乘风离开果荚，直到落地生根、开花的旅程，以简单的文字，明朗的图画，清楚地呈现生命之息息相关、延续不易。这是一本不需要太多解说，孩子便能明确感受的图画书，“生命”的价值与延续，不言而喻。', '自然生物', 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '四季轮转', '自然,沙漠', 0);
INSERT INTO `data` VALUES (110, 487, '画一个星星给我', '艾瑞·卡尔', '蓝色', '台湾“好书大家读”年度少年儿童读物奖', 20, '3-8岁', 1, ' 《画一个星星给我》书中借由隐形的第三人，向画家要求一个星星，紧接着星星又要求画一个温暖的太阳；太阳要求画一棵可爱的树……如此描绘出一个个多彩多姿的生命，诉说宇宙生生不息的创造力与生命的循环。\n\n    在看似极为简单的文图表现里，蕴涵着细腻的设计与深意。书里的画家彩笔一挥，像变魔术一样，创造出一个又一个“很棒的”、“温暖的”、“可爱的”、“漂亮的”生命、东西和自然景物。这么多不同的形容词，表达了画家描述与看待世界的方式。创造的过程有条有理，各个独立个体之间有生命互相依存的需求，也有和谐共处的关系。情节一步步推展，到了画面*丰富的无字跨页图画时，世界整体看来多么生气蓬勃，充满喜悦，让人不能不赞叹生命真是奇妙的恩典。\n\n    而创造的过程也伴随着人生的周期，画家从小孩变成青年、再变成中年人，*后变成老人随着星星飞向空中，显示他对艺术创作持续不懈的热情和努力，也让人看到艺术的生命如何自由飞翔与照亮黑夜。艺术、世界与人生三者之间，就创造的意义而言原来互有关联。', '自然生物', 1, '猫,狗,蝴蝶,鸟', '树', NULL, NULL, NULL, '太阳,月亮,星星', NULL, '群体关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (111, 488, '兔儿爷', '熊亮', '黑色', NULL, 53, '3-10岁', 1, '自从现代化以来，强势者大谈全球化、地球村与竞争力。至21世纪，这种现象──所谓全球视野、国际运作能力，更是蔚为单一的主流。于是乎，全球皆沦为单一的麦当劳，没有历史，没有记忆，更没有身份的认同。\n\n其实，自1960年代末期，有越来越多的作家、学者开始注意另一种殖民作为──新殖民主义，尤其是美国好莱坞文化及商品的侵略。针对新旧殖民经验，如何界定自己本土文化，珍视传统文化再生的契机及其不同之处，这是刻不容缓之事。\n\n我们看到：全球越来越成为一个单一的经济体，而生活方式也越来越全球化，而我们却也清楚的看到一个相反的运动，即是「文化国家主义」。\n\n当世界越来越接近所谓的「地球村」，经济也越来越相互依赖时，我们反而越来越讲求人性化，且越来越强调彼此间的差异，越来越坚持自己的传统，越来越积极的守住自己的「根」与文化。\n\n如今见熊氏兄弟，尝试以图画书的图像来诠释传统的民间技艺，呈现出对「传统」重新认同的觉醒。因为，传统不是可以继承的遗产，必须下一番苦功才能得以延续。最重要的是传统含有历史的意识，这种「历史的意识」，包含着一种认知──传统不仅仅具有「过去性」，同时也具有「现在性」；历史意识使一个作家在写作时，不仅仅在骨髓中深切的感受到自己的时代，同时也感觉到自己与传统文化同时并存。\n\n在此思想基础上，我推荐熊亮先生的本土图画书《兔儿爷》——\n\n每一个兔儿，都会属于一个孩子。\n\n作为「中秋礼品」的玩具兔儿，殷切希望着能找到他的小主人，知道自己的新家。然而，一阵风吹来，地址不见了，玩具兔儿的期盼也落空。他的新家，变成了囤积旧货物的阁楼。\n\n在盒子里，玩具兔儿静默而寂寞。\n\n于是，玩具兔儿决定捅破盒子，从阁楼出走。走过漫长的台阶，走过黎明的街道，走向自己的小主人。最后，找到自己的归属，也让孩子有了新的陪伴者。\n\n走出故事，每个孩子，一定也都希望自己拥有个陪伴者，或许是玩具，或许是宠物。\n\n或许，是更亲近的亲情。\n\n生活的忙碌，工作的牢笼，让我们成为了旧阁楼中出不了盒子的玩具兔儿，也让我们的孩子缺少陪伴而失望。\n\n所以，让我们从故事，从图画书，陪伴着我们的孩子，成为他们需要的玩具兔儿；陪伴着需要陪伴的孩子，也让自己回家，既是归属，也是无微不至的陪伴。', '成长道理', 1, '兔子', NULL, NULL, NULL, NULL, '儿童', '外貌,身份', '朋友关系', '早晨,朝夕轮转', '城镇', 0);
INSERT INTO `data` VALUES (112, 489, '武松打虎', '熊亮', '蓝色', NULL, 36, '3-6岁', 1, '每天晚上，剧场空无一人时，京剧猫们就登上舞台，表演一出出精彩的京剧故事。今晚表演的节目是《武松打虎》，白色的是武松猫，黄斑纹的是老虎猫。演员们都卖力地表演，为大家奉献了一场精彩的演出。', '历史知识', 1, '猫', NULL, NULL, NULL, NULL, NULL, '职业', '朋友关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (113, 490, '长版坡', '熊亮', '灰色,绿色', NULL, 0, '4-6岁', 1, '三更半夜，剧场里没有人，只有猫。\n　　这是京剧猫的世界，脸上有花纹的，就是京剧猫。\n　　没有花纹的，就是观众。\n\n　　\"锵！锵！锵！\"曹操猫带着猫兵猫将，浩浩荡荡出了场。\n　　黑脸猫大叫一声：\"张飞在此，谁敢和我决一死战？\"\n　　那声音响得像一声炸雷，曹操猫脸都白了……\n　　猫剧场午夜开演，不收门票，人类止步，限猫入场哟，谢谢。', '艺术修养', 1, '猫', NULL, NULL, NULL, NULL, NULL, '外貌,职业', '朋友关系', '夜晚', NULL, 0);
INSERT INTO `data` VALUES (114, 491, '梅雨怪', '熊亮', '灰色', NULL, 80, '3-6岁', 1, '梅雨怪住在城郊的大雨山。\n　　山里每天每夜下着大雨，从来没有一秒钟停过。\n　　梅雨怪的家里没一个角落不是湿答答的，\n　　他的身上也没一块地方不是潮乎乎的，\n　　他就这样每天生活在雨里，\n　　除了一年的*后一天……', '自然生物', 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '朝夕轮转', NULL, 0);
INSERT INTO `data` VALUES (115, 492, '小石狮', '熊亮', '灰色', NULL, 74, '3-6岁', 1, '欢迎来到金刚师山里的讲堂，这里讲的不仅仅是学习，还有——人生。\n\n金刚师问：学习的目的是什么？\n\n风水火石、毛虫叶子一一回答，\n\n答案就变得无穷多……\n\n这是一堂人生课，你的答案呢？', '成长道理', 1, NULL, NULL, NULL, NULL, NULL, NULL, '外貌,身份', NULL, '四季轮转', '城镇', 0);
INSERT INTO `data` VALUES (116, 493, '金刚师', '熊亮', '灰色', NULL, 74, '3-6岁', 1, '欢迎来到金刚师山里的讲堂，这里讲的不仅仅是学习，还有——人生。\n\n金刚师问：学习的目的是什么？\n\n风水火石、毛虫叶子一一回答，\n\n答案就变得无穷多……\n\n这是一堂人生课，你的答案呢？', '成长道理', 1, '蝴蝶', '花朵,树', NULL, NULL, NULL, '魔法师', NULL, '群体关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (117, 494, '和风一起散步', '熊亮', '绿色,白色', NULL, 48, '3-6岁', 1, '如果一片原始山林数百年间未受惊扰，\n　　未被砍伐和迁移，就会产生\"木客\"--他们是一群小小的人。\n　　无论老幼，木客看起来都是孩子模样，\n　　唱歌和哭笑的声音也和我们相仿，只是非常非常小。\n\n　　有一天，风悄悄溜进小木客的房间，摸摸他的脸，在耳边轻轻说：\n　　\"快起来……快起来……我陪你出去散步。\"', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, '风', NULL, '朋友关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (118, 495, '看不见的马', '熊亮', '白色', NULL, 32, '3-6岁', 1, '这本书里有一匹马，可一开始啊，你好像看不见它。\n　　在京剧的舞台上，演员拿起一根马鞭，做出各种动作，就能表现出奔驰、战斗、爬山、过河等各种情景。\n　　说起来简单，演起来可不容易。\n　　你看，京剧猫的戏班子又开演了。\n　　别看舞台很简陋，行头也不全，但他们演得还挺认真呢！\n　　来呀，带马！', '艺术修养', 1, '猫', NULL, NULL, NULL, NULL, NULL, '职业', NULL, NULL, NULL, 0);
INSERT INTO `data` VALUES (119, 496, '什么猫都有用', '熊亮', '黄色,白色', NULL, 32, '3-6岁', 1, '在京剧猫的世界里，有锅底黑脸猫、赤红脸猫、粉白脸猫，还有三块瓦花脸猫。他们都是好角色。\n\n故事的主角则是一只没有点的猫。\n\n一张大脸又平又圆，眼睛、鼻子、朵都那么小，也没有斑纹，真是一点儿点都没有。\n\n这样的猫，有什么用呢？\n\n露天戏场就要开演了，谁也没想到，这只没有点的猫派上了大用场！', '艺术修养', 1, '猫', NULL, NULL, NULL, NULL, NULL, '职业', NULL, '夜晚', NULL, 0);
INSERT INTO `data` VALUES (120, 497, '蝈蝈和蛐蛐', '熊亮', '白色,绿色', NULL, 32, '3-6岁', 1, '我是蝈蝈！我是蛐蛐！\n\n我是哥哥！我是弟弟！\n\n南山坡守着那块青草地，\n\n吃饱了肚子就吹牛皮。\n\n蝈蝈对着蛐蛐笑眯眯：\n\n老弟呀！嘿嘿，我说了你可别着急！\n\n天下的动物大小我全管，\n\n叫谁朝东他不敢向西。\n\n蛐蛐对着蝈蝈笑嘻嘻：\n\n老哥呀！哈哈，我说了你可别生气！\n\n天下的动物生杀我来定，\n\n不管是恐龙呀，还是大鲸鱼。', '自然生物', 1, NULL, NULL, NULL, NULL, NULL, NULL, '性格', '朋友关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (121, 498, '屠龙族', '熊亮', '棕色', NULL, 40, '3-6岁', 1, '　很久很久以前，生活着屠龙一族。\n　　那儿的每一个孩子都被培养成屠龙斗士，\n　　他们不断找龙挑战，\n　　在深深的海底、黑黑的地缝、高高的天上……\n\n　　龙越来越少了，孩子们开始问：\"爸爸，什么是龙？\"\n　　找龙成了每一个屠龙族孩子的梦想，\n　　龙是不是真的消失了呢？', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, '龙', '身份,能力', NULL, NULL, NULL, 0);
INSERT INTO `data` VALUES (122, 501, '不要和青蛙跳绳', ' 彭懿，九儿', '白色', '2016年度“大众喜爱的50种图书”', 56, '3-6岁', 1, '星期三下午，壳壳放学回到家，发现自己最宝贝的跳棋被妈妈扔掉了。他气呼呼地喊道：“我最讨厌妈妈了！”，扭头跑出屋外。\n\n这时，一件神奇的事情发生了……\n\n大象、狮子、鳄鱼、长颈鹿、鸵鸟、青蛙……一个又一个动物出现了，它们听说壳壳讨厌妈妈，都想来问壳壳要妈妈，壳壳该怎么办呢？\n\n幻想朋友永远悄悄等在你的童年里，就看你会不会去召唤它们。\n\n这部作品与彭懿、九儿前一部作品《妖怪山》比起来，表现的心理伤害更日常，也更顽固。很多时候，孩子必须学会独自承受这样的心理伤害，就像每个离开妈妈的动物一样，在一个人的游戏中（不管是真实的还是想象的）恢复自己的情绪，并学会独自长大。', '情感体验', 1, '大象,狮子,长颈鹿,乌龟,青蛙,鳄鱼,鸟', NULL, NULL, NULL, NULL, '儿童', '性格', NULL, '朝夕轮转', '家', 1);
INSERT INTO `data` VALUES (123, 517, '驯鹿人的孩子', '彭懿', '棕色', NULL, 60, '5-8岁', 1, '小男孩托克寻今年5岁，他是驯鹿人的孩子。驯鹿人生活在遥远的蒙古大草原深处，他们没有房子，他们住帐篷；他们没有汽车，他们骑驯鹿；他们没有各式各样方便的家电，他们砍柴、钓鱼、挤鹿奶……\n\n外面的世界变化很快，但他们依然坚守、延续着古老而传统的生活方式。\n\n也许，他们将是最后的驯鹿人，但我们却将永远记得：在这片荒蛮而壮美的土地上，有一群驯鹿人认真而又浪漫地生活过……', '成长道理', 1, NULL, NULL, NULL, NULL, NULL, '家庭成员', '职业', NULL, '四季轮转,雪', NULL, 0);
INSERT INTO `data` VALUES (124, 518, '山楂村和狗獾村', '彭懿 著 / 李海燕 绘', '绿色', NULL, 44, '4-7岁', 1, '我奶奶的村子有两个名字，一个叫山楂村，一个叫狗獾村。我喜欢山楂村的名字，听起来酸酸甜甜的；可狗獾村的名字就怪怪的，真不明白为什么会有这样的名字！\n\n村里面，家家户户都种满了山楂树，每年秋天山楂成熟时，我都会和妈妈一起来帮奶奶做山楂酱。山楂酱又酸又甜真好吃，可奶奶却说，用山楂树王的果子做的山楂酱才好吃呢，但人找不到山楂树王，只有狗獾才能找到。\n\n不过，我想到了个好主意。这天晚上，怪事果然发生了，一只小手偷偷拿走了我放在窗户上的山楂酱……\n\n和奶奶在一起的快乐秋假，与神奇动物温暖邂逅。想象让童年更精彩，也让生命更丰盈。图画书大咖彭懿、李海燕，献给童年那些如宫崎骏《龙猫》般纯真美好的相遇。', '情感体验', 1, NULL, NULL, '老人', NULL, NULL, '儿童', '外貌,能力', '邻居关系', '秋', '乡村', 1);
INSERT INTO `data` VALUES (125, 519, '萤火虫女孩', '彭懿 著 / 李海燕 绘', '蓝色,黄色', NULL, 64, '3-6岁', 1, '公交车司机老周明天就要退休了，今天是他最后一次开着班车来到这个位于老林深处的车站。他将手伸出车窗摆了摆了，向这个仿佛陪伴了自己一生的小村和车站告别。\n\n老周发动汽车，正准备离开。突然，奇怪的事情发生了……\n\n黑乎乎的林子里突然跑出来一个“迷路”的小男孩，空无一人的车子里突然多出20个长得一模一样的小女孩，连森林里的大熊都等在路边，给了老周一个大大的熊抱……\n\n这究竟是怎么一回事呢？\n\n生命的脚步不息，生活的奇迹就不会终止。图画书大咖彭懿携手知名插画家李海燕，邀您共赴神秘而瑰丽的奇幻森林之旅，故事温暖欢快', '自然生物', 1, '狐狸,熊,萤火虫', NULL, '驾驶员', NULL, NULL, NULL, '职业', '朋友关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (126, 520, '我用32个屁打败了睡魔怪', '彭懿（文）/  田宇（绘）', '黑色,绿色', NULL, 48, '4-8岁', 1, '只要一做到那个噩梦，我就会被吓醒。\n\n在那个噩梦里，总有一个睡魔怪大王从壁橱里溜出来，一把揪住我，塞进他那个黑咕隆咚的瓶子里。然后，十几个睡魔怪就会呼啦啦地扑上来！\n\n我向妈妈求助，可妈妈在噩梦里跑得比我还快！\n\n大人靠不上，我要自己想办法从噩梦里逃出来。我穿上了最好的跑鞋，拿上了爸爸的强光手电，甚至还把半个榴莲抱进了被窝里，可是仍旧没能打败睡魔怪。直到有一天，我想到了一个绝妙的主意……', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, '儿童,怪兽', '外貌,身份', NULL, '夜晚,朝夕轮转', '家,梦境', 0);
INSERT INTO `data` VALUES (127, 521, '我用32个睡魔怪打败了我妈妈', '彭懿（文）/  田宇（绘）', '彩色', NULL, 56, '4-8岁', 1, '本书是畅销幽默图画书《我用32个屁打败了睡魔怪》的续集，讲述了一个因辅导孩子写作业而引发的爆笑故事。\n\n我只要一写作业，妈妈就“发疯”；妈妈一发疯，我就会做噩梦……\n在噩梦里，妈妈变得又高又大，不管我逃得有多快，她都能追上我，把我装进一个瓶子里，逼我写作业。\n我是不是世界上命苦的小孩？不仅白天要写作业，连晚上做噩梦都要写！\n不行，我得反抗！\n我想了好多好多办法：穿上轮滑鞋、把卷子藏进爸爸的袜子里、钻进奶奶的被窝里……可全都没有用！\n甚至，我请来专门让人噩梦的专业睡魔怪也不行！它们一个不落，全都被妈妈抓住，写了三百三二十年的作业……\n我该怎么办呢？', '成长道理', 1, NULL, NULL, NULL, NULL, NULL, '儿童,怪兽', '外貌,身份', '家庭关系,朋友关系', '夜晚,朝夕轮转', '家,梦境', 0);
INSERT INTO `data` VALUES (128, 522, '守林大熊', '彭懿 著 / 李海燕 绘', '黄色', NULL, 56, '3-6岁', 1, '在遥远的大山里，住着一个又高又大又黑的守林员，大家都亲切地叫他“大熊”。可是，已经有十几年没人这样叫过他了，因为人们都搬走了，谁愿意住在深山老林里过苦日子呢？只有大熊留了下来，因为这里是他的故乡。他想，万一哪天有人想回家呢？总得有个地方可以回呀。       这年秋天，奇怪的事情发生了：大半夜，无人的车站小屋突然亮起了灯；很久没人用过的电话，忽然叮铃叮铃响个不停；荒废了十多年，长满野草的小火车居然开动了，司机还是一只山猫？！这是怎么回事呢……       一个关于故乡、关于守护的温暖故事，一幅色彩斑斓、充满生命力的秋日画卷，一部如吉卜力动画电影《百变狸猫》般妙趣横生的森林幻想曲。', '自然生物', 1, '狐狸,熊', NULL, NULL, NULL, NULL, NULL, '外貌,身份', '朋友关系,邻居关系', '秋', NULL, 0);
INSERT INTO `data` VALUES (129, 523, '看，脱光光了', '五味太郎', '棕色', NULL, 32, '0-3岁', 0, '小狮子，要洗澡了，快点脱光光吧！咦？怎么回事，狮子的鬃毛也可以像衣服一样脱下来吗？脱光之后原来是——小熊！小熊，这样还不行哦，快点脱光光吧！小熊脱光后又会变成什么样呢？不断带来惊喜的有趣绘本，让孩子在快乐阅读的过程中养成良好的生活习惯。', '习惯养成', 1, '熊,狮子', NULL, NULL, NULL, NULL, NULL, '外貌', NULL, NULL, '家', 0);
INSERT INTO `data` VALUES (130, 525, '小牛的春天', '五味太郎', '粉色,白色', '意大利博洛尼亚国际儿童书展插画奖', 31, '2-6岁', 1, '五味太郎创意绘本代表作，用一头小牛的成长，串联起气象万千的四季，充满智慧的隽永诗篇，带来对生命的别样感悟。荣获世界绘本大奖——意大利博洛尼亚国际儿童书展插画奖。', '成长道理', 1, '牛', '花朵,草', NULL, NULL, NULL, NULL, '外貌', NULL, '春,夏,秋,冬', NULL, 0);
INSERT INTO `data` VALUES (131, 526, '巴士到站了', '五味太郎', '白色,棕色', NULL, 31, '3-8岁', 1, '《巴士到站了》以“巴士到站了（バスガきた），某某该下车了（……おりた）”构成回复旋律。人们乘坐巴士到达属于自己的目的地，每个人都在自己的那一站下车去做自己的事情。我们能否猜出每一站是什么地方？而巴士的下一站又会停在哪里呢？一位色彩魔术师——五味太郎，用非常鲜艳美丽的色彩，规划了巴士的路线：从海边、山边、小区、新市镇、大都会、游乐场、运动场、医院、市场、垃圾场、制片场……到停车场。透过色彩，不仅将不同停靠站所代表的生活气氛表露无遗，也透露了巴士服务的各种人的职业，以及服务时间。最重要的，缤纷的色彩也让巴士每天的行程变得非常愉快。小朋友，你来看一下，巴士都到了什么地方呢？下来的人们又都是什么职业的呢？想不想跟着爸爸妈妈一起去乘巴士，然后把《巴士到站了》的故事说给他们听呢？', '成长道理', 1, NULL, NULL, NULL, NULL, '公交巴士', NULL, NULL, NULL, '早晨,中午,傍晚', NULL, 0);
INSERT INTO `data` VALUES (132, 532, '黄色的……是蝴蝶！', '五味太郎', '绿色', NULL, 36, '2-4岁', 0, '黄色的，是蝴蝶……看，草地上有只黄蝴蝶！可是哎呀，原来不是蝴蝶，是一朵小黄花！黄色的，是蝴蝶……瞧，树上有只黄蝴蝶。哎呀呀，又不是蝴蝶，是树上的大果子！一心想扑到黄蝴蝶的小朋友，为什么总是会搞错？原来，是作者五味太郎的巧思在变魔术。在书中，他挖出了很多蝴蝶形状的小洞洞，小朋友们可以和爸爸妈妈一起猜猜看，小男孩捕到的“蝴蝶”到底是什么，共同体会发现带来的乐趣。更神奇的是，同样一个形状的洞洞，竟然还可以变身为小猪鼻子、侦探墨镜，甚至是张大的嘴巴，一个接一个的惊喜，让孩子在翻阅的过程中获得源源不断的快乐。', '情感体验', 1, '鸡', '花朵,树', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `data` VALUES (133, 535, '子儿，吐吐', '李瑾伦', '黄色', '台湾“信谊幼儿文学奖”首奖 ', 22, '3-8岁', 1, '《子儿,吐吐》是一本美妙精致的图画书。故事很简单，来自幼儿的日常生活经验：小猪“胖脸儿”吃起东西来总是又快又多——这一次。吃瓜不吐子儿违背生活常识，可通常也无甚大碍：缺乏生活知识的幼儿就很容易犯下这样小小的错儿。这是一个富有张力的取材，它一头联系着生活中实实在在的日常经验，另一头则悄悄打开了文学创造中奇特有趣的想像空间。', '习惯养成', 1, '猪', NULL, NULL, NULL, NULL, NULL, '外貌', NULL, '朝夕轮转', NULL, 1);
INSERT INTO `data` VALUES (134, 536, '那只深蓝色的鸟是我爸爸', '魏捷 （文）/ 何耘之（图）', '黑色,蓝色', '信谊图画书奖', 30, '3-8岁', 1, '“树上有20只鸟，第一次飞走5只，第二次飞走4只，树上的鸟比原来少了几只？”这个故事从一个孩子的数学题开始，爱孩子的爸爸，为了教会孩子一道简单的数学题，用无比的爱心与耐心，把自己当做鸟儿，一遍又一遍地为孩子努力飞翔着……\n\n父亲对孩子浓厚的爱，那么温暖，那么令人感动。作者魏捷借着故事，阐释了一个现代父亲对孩子朴实却真诚的爱。希望天底下的父母亲，都能够了解孩子，与孩子快乐相处，让孩子能够感受父母亲的爱，在爱的滋养下茁壮成长。', '数学知识', 1, '鸟', '树', NULL, NULL, NULL, '家庭成员', '身份', '家庭关系,邻居关系', '朝夕轮转', NULL, 0);
INSERT INTO `data` VALUES (135, 539, '跑跑镇', '亚东 文 / 麦克小奎 图', '蓝色', '信谊图画书奖', 0, '3-8岁', 1, '跑跑镇上的居民都喜欢快跑，快跑就免不了撞在一起：小猫和小鹰撞在一起出现了什么？苹果和红宝石撞在一起？全书充满生活中幽默诙谐的小情趣。故事有想象力，构思巧妙，以出人意料的结果，让人忍俊不禁。\n\n本书是一本创意游戏书，但是创意并不是天马行空的想象，而是通过两个不相关的东西快跑、碰撞在一起后，变成“新”事物；孩子可以从两个不同的对象上观察趣味的元素，进而联想出两个角色碰撞后可能产生的新变化。整本书充满了创意和趣味，跟着哒哒哒的脚步声，一起享受猜测的游戏乐趣吧！', '情感体验', 1, '猫,熊,鱼,海豚', '苹果', '公主', '包子,肉丸', NULL, NULL, '外貌,身份', NULL, NULL, NULL, 0);
INSERT INTO `data` VALUES (136, 540, '妈妈，买绿豆！', '曾阳晴 （文） / 万华国 （图）', '绿色', '信谊幼儿文学奖', 40, '3-8岁', 1, '阿宝和妈妈上街买绿豆，一连串奇妙的情节就此展开……阿宝专注于绿豆，从买绿豆、煮绿豆汤、吃绿豆到种绿豆，作者把整个过程处理得十分生活化和趣味化，与母子浓郁的亲情相互映照，插画唯妙唯肖，细腻写实，牵动每个人的童心。', '情感体验', 1, NULL, '豆子', NULL, NULL, NULL, '家庭成员', NULL, NULL, '朝夕轮转', '家', 1);
INSERT INTO `data` VALUES (137, 541, '青蛙与男孩', '萧袤（文）/陈伟 黄小敏 （图）', '绿色', '丰子恺儿童图画书奖”优秀儿童图画书奖”', 0, '3-6岁', 0, '男孩遇见青蛙。青蛙对男孩说我会蹲，男孩说我也会；青蛙说我会跳，男孩说我也会；青蛙说我会洗澡，男孩说我也会……男孩仿佛和青蛙较上了劲，还是孩子都有模仿的天性？一番比较后，青蛙竟以为找到了失踪的王子，男孩这才发现“糟了”。孩子虽然是孩子，但独自遇上这样的事情，也表现得聪明，能反过来说出无数理由证明自己不是青蛙……\n　　哈，很少有人会遇见这种青蛙吧，可真的所有孩子都经历过一样的事情。首先，轻易地发现自己这里、这里和这里跟别人如此地相似，然后又反念想到——一般这更难些，自己那里、那里和那里跟别人多么地不一样。大人好像喜欢把这样的过程叫做“自我认识”。而作为自我认识的重要组成部分，发现“一样”很重要，发现“不一样”更重要。同时，相比单纯的家庭生活，这种认识更容易发生在广阔的天地中、各种不同的际遇里。\n　　经历了春荣秋枯、夏荷冬雪，过了一年那么久啊，男孩好不容易——真的好不容易才又在田埂上飞奔而回。亲爱的爸爸妈妈还在原处，刚铺开粉红色的垫子，回头看见孩子蹦跳雀跃的小身影，只是不知和去时相比，多了“一整年”的经历。男孩会把青蛙的事儿告诉爸爸妈妈吗？可是大人也许还没有陪伴在身边的小蝴蝶了解呢。那成长发生的一瞬间，有多少漫长的等待和复杂的感受哟。', '情感体验', 1, '青蛙', NULL, NULL, NULL, NULL, '儿童', '外貌', '朋友关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (138, 542, '池上，池下', '邱承宗', '白色,绿色', '第一届丰子恺优秀儿童图画书奖，荣获第三十三届金鼎奖——最佳科学类图书奖', 40, '3-10岁', 1, '这本书是作者邱承宗对于麻斑晏蜓的一生、池边昆虫物种，甚至对于众生命的细腻写实观察，以他如相机镜头般的眼，捕捉生命中的各种变化与光影；以画笔描绘蜻蜓的动态线条，记录美丽的“池上池下生态”。\n\n他以蜻蜓的一生为《池上池下》的主线，以情景交融的文字和细腻写实的画，辅以蜻蜓观察笔记，为读者创造一个值得微观细品的小小生态世界。\n\n一个年年在池塘里上演的生命戏码，在作者邱承宗用心观察多年之后，以细腻的画笔、优美的文字，另外辅以本身珍贵的观察记录资料谱出了这样一本弥足珍贵的蜻蜓生态绘本！\n\n池塘，也许是不起眼的小地方，却是许多童趣回忆的原点！读过了本书，也许你也能重拾属于你自己的池塘记忆。《池上池下》呈现给读者的，就是这样一个美好而缤纷的小世界。\n\n春逝、夏至、秋去、冬来，已经习以为常的时节变化，由翩然降落的一只小白鹭打破了时间的静默，同时串连起池上池下的生命故事。在我们不注意时，池下一颗小小的卵，已悄悄开启麻斑晏蜓的生命轨迹，经由蜕变、羽化、巡飞、交尾，到产卵传承，甚或死亡……', '自然生物', 1, '蜻蜓', NULL, NULL, NULL, NULL, NULL, '外貌', '群体关系', '春,夏,秋', '水域', 0);
INSERT INTO `data` VALUES (139, 545, '乌龟一家去看海', '张宁', '蓝色', '丰子恺儿童图画书奖', 42, '3-6岁', 1, '春天来了，小乌龟壳壳一家从冬眠中醒来，他们想去看看大海。大海远不远呢？大海是什么样子的？听说大海里有长翅膀的大鱼、有漂来漂去的小伞兵，还有害羞的大海怪……\n\n小乌龟壳壳勇敢地迈开脚步向前走，越过草地、渡过池塘、翻过大山……在爸爸妈妈和朋友们的陪伴下，一步一步实现自己去看大海的梦想。', '自然生物', 1, '乌龟,蚂蚁,鸟', NULL, NULL, NULL, NULL, NULL, '能力', '家庭关系,群体关系', '春,夏', NULL, 0);
INSERT INTO `data` VALUES (140, 546, '外婆家的马', '谢华（文） / 黄丽（图）', '粉色', '丰子恺儿童图画书奖，“第三届小凉帽国际绘本奖”专业组全场大奖', 0, '3-6岁', 0, '《外婆家的马》是谢华老师近些年创作的生活随笔里面的一篇，文章描写了外孙小东西在外婆家度过的一段快乐的假期时光。全篇从男孩子喜欢的养马游戏入手，细腻地刻画了慈祥的外婆和调皮又可爱的小外孙形象，生活的点点滴滴无不展示出祖孙之间浓浓的爱与关怀。\n\n中国式家庭特有的养育方式，中国式家庭特有的亲情表达，展现出当代中国的百姓生活，真实、朴素、感人。\n\n外婆家的马的创作者  · · · · · ·', '成长道理', 1, '马', NULL, '老人', NULL, NULL, '儿童', '身份', '家庭关系', NULL, '家', 0);
INSERT INTO `data` VALUES (141, 548, '六十六头牛', '改编自民间歌谣 / 王祖民（图）', '黄色,灰色', NULL, 27, '3-8岁', 1, '六十六岁的陆老头，盖了六十六间楼，买了六十六篓油，养了六十六头牛，栽了六十六棵垂杨柳……六十六的不断累积，堆叠起一派富足！但是一阵狂风起……楼倒了，油翻了，牛跑了，柳折了……所有的一切又回到起点。《六十六头牛》是一首绕口令，更是一篇趣味盎然、动感十足、画面具有大美的故事。尤其，当一切被狂风摧毁后，原本颓坐在地的陆老头，又重新起身，盖楼、买油、养牛、栽柳，歌谣的音韵声音再起，更展现了一位长者遇事洒脱、不被挫折打倒的智慧风范。 王祖民绘的《六十六头牛》是文字幽默夸张，富有想象力，念起来别有韵律。', '自然生物', 1, '牛', '树', '老人', NULL, NULL, NULL, NULL, '群体关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (142, 549, '天啊，错啦！', '徐萃 / 姬炤华', '紫色,黄色', NULL, 32, NULL, 0, '这本书的灵感来自于《庄子·齐物论》“庄周梦蝶”的故事：有一天庄周梦见自己变成了一只翩翩起舞的蝴蝶，悠游自在，根本不知道自己原来是庄周。突然梦醒了，却发现自己分明是庄周，而不是蝴蝶。那么，是庄周做梦变成了蝴蝶，还是蝴蝶做梦变成了庄周呢?书中无意要指出谁对谁错，我们希望这本书给孩子带来欢乐的同时，也能够让他们思考，世间没有一样东两是一成不变的，对不同的人来说，只要适合自己就是对的。\n\n德国哲学家康德曾说“启蒙，就是使每一个人能够独立运用自己的理性”，而创造力正是来源于“能够独立运用自己的理性”。把自己的思想依附在别人的大脑上，随波逐流，人云亦云的人，是不可能有创造力的。然而在当下的社会巾，我们从小就被各种各样的声音所淹没，早已忘记了如何倾听自己的声音，运用自己的理性。\n\n为此，我们在画面的框线之外安排了一个画外音，画外音总是和画l衔里的动物们意见相左，那是故事讲述者的声音，在以往任何‘本书里，讲述者都是人物命运的主宰，是书中的“权威”。如果孩子比较小，町以先看线框里面的故事，读第二遍时再加入画外音也不迟。我们可以问问孩子： “你认为那个声音是谁?”相信孩子们会给出各种各样有趣的答案。', '哲理思考', 1, '兔子,刺猬,猪,羊,大象,鳄鱼,鸡', NULL, NULL, NULL, NULL, NULL, '外貌', '群体关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (143, 553, '鲁拉鲁先生的自行车', '伊东宽', '蓝色,黄色', NULL, 36, '2-5岁', 1, '《鲁拉鲁先生的自行车》内容简介：在前面的故事里，鲁拉鲁先生开放院子，和小动物们做了朋友，后来又宴请他们，现在他越发随和，更加能享受意想不到的事情了。星期天，他有独自骑车出游的惯例。小老鼠要求加入，他笑嘻嘻地欣然接受；谁知小老鼠身后带着一大群小动物，好过分的请求，他也答应了；闸失控了，车冲到河里啦！他们却笑成一团，在河里玩了个痛快！鲁拉鲁先生的心已经和小动物朋友们融成一片，像他们一样嘻嘻哈哈，像他们一样从乱乱的，不合常规的事情里找到乐趣。\n\n从庭院到厨房到自行车三本书里，鲁拉鲁先生的表情渐渐变柔和，变丰富；最初他的身体语言绷得直直的，后来增添了动感；画面的颜色也显得越来越热闹。鲁拉鲁先生变成一个通融的、乐呵呵的老头儿了。他特别能理解小朋友们那些看似无理的、天真烂漫的小愿望。', '情感体验', 1, '鼠', NULL, NULL, NULL, '自行车', NULL, '外貌,性格', '朋友关系', '朝夕轮转', '水域', 1);
INSERT INTO `data` VALUES (144, 557, '神奇糖果店', '宫西达也', '粉色,白色', NULL, 0, '3-6岁', 1, '糖果店里卖着各种神奇的糖果，有黄的、绿的、红的、白的，糖含在嘴里就能变成大力士、发出狮子一样的吼声、还能隐身！可是，只要糖没了，就又恢复到原来的样子。小猪买了神奇糖果，先变成大灰狼吓唬大家，可却误打误撞地来到大灰狼镇上！眼看危险逼近，小猪急中生智，把狗獾叔叔送他的白色糖果放进嘴里……', '线条色彩', 1, '猪,狼', NULL, NULL, NULL, NULL, NULL, '外貌,能力', '群体关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (145, 560, '嘘！这里睡了一阵风', '王落（文）/吕秋梅（图）', '紫色', '冰心儿童文学奖，“中国童书榜”年度百家童书', 0, NULL, 1, '冬天的风，连心都是冷的，它们狂躁而冷漠、自私而麻木，恪守着“所有的冬风都\n\n应该是一个样儿，都应该讨厌和春天有关的一切”的准则。但是，有一阵冬风却不太一样，它身上有一股淡淡的太阳花的香味儿。对，是太阳花的香味儿，虽然很淡很淡，却温暖而清香。凡是它飞过的地方，都会开出一朵朵洁白美丽的花…\n\n\n\n当一阵风，不一定非得是狂躁冷漠的风，也可以是温暖的带有淡淡的香味儿的风；\n\n当一个小朋友，也不一定非得是和别人一模一样的小朋友，你也可以是带着自己独有的“香味儿”的小朋友…', '情感体验', 1, '鸟', NULL, NULL, NULL, NULL, '风', '身份', NULL, '春,冬', '自然', 0);
INSERT INTO `data` VALUES (146, 562, '木木和木儿', '严晓萍（文）/李俊红（图）', '棕色', '第七届“中国童书榜”年度百佳童书，冰心儿童文学奖', 0, '6-8岁', 1, '木匠爷爷做了两把漂亮的小椅子—木木和木儿。木匠爷爷把它们带到集市上出售，木木和木儿非常担心，因为它们本属于同一段木墩儿，实在不愿意分开。幸运的是，原本分别被买走的木儿和木木，竟然前后进了同一个家门。然而，接下来发生的事，让重聚的木木和木儿又将面临分离…', '成长道理', 1, NULL, NULL, NULL, NULL, NULL, NULL, '外貌,身份', '朋友关系', '朝夕轮转', NULL, 0);
INSERT INTO `data` VALUES (147, 564, '盘子，筷子，碗', '米吉卡', NULL, '陈伯吹儿童文学奖', 0, '6-8岁', 1, '一天，三个外星人来到地球，他们想要霸占地球，把地球上的事物据为己有。他们能如愿吗？', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, '儿童,外星人', '外貌,身份', '朋友关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (148, 568, '河马的蓝宝石戒指', '肖定丽', '黄色,紫色', '冰心儿童文学奖', 0, '6-8岁', 1, '这是一篇讲述真心与诚意的故事。\n\n河马黑泥真心诚意地爱着河马小六，他觉得只有拿世界上最闪亮最耀眼的蓝宝石戒指送给小六，才足够表达自己的诚挚心意、才足以显示他对小六的珍视。\n\n\n\n蓝蜗牛真心诚意地想帮助自己的邻居黑泥，所以心甘情愿地冒充蓝宝石戒指，也心甘情愿地让自已的儿子小蓝蜗牛继承自己的使命，继续冒充蓝宝石戒指。那么，小六真的是近视得瞧不清手上的蓝宝石戒指是由蓝\n\n蜗牛装扮而成的吗？也许，小六早就晓得了其中的奥妙哩。\n\n\n\n只有真诚，才能叩开别人的心灵；也只有真诚，才能换来别人的温暖回应。人只有在怀着单纯的真诚去爱别人、去爱这个世界的时候，才能品得生活的真味，方能拥有那诗意、色彩浪漫和幸福。\n\n', '情感体验', 1, '蜗牛', NULL, NULL, NULL, NULL, NULL, '外貌', '朋友关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (149, 573, '偷梦的妖精', '刘兴诗', '紫色', '海峡两岸中华儿童文学创作奖', 174, '6-8岁', 1, '0', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, '儿童,精灵', '能力', NULL, '夜晚', '梦境', 0);
INSERT INTO `data` VALUES (150, 587, '老鼠牙医----地嗖头', '史塔克', '黄色', '纽伯瑞佳作奖', 28, '3-6岁', 0, '《老鼠牙医:地嗖头》里老鼠牙医地嗖头夫妇好心为狐狸医牙，但狐狸逐渐露出贪婪的面目……地嗖头夫妇遇到生命威胁，依然有始有终将工作完成，并运用机智化解危机，他们的爱心和责任心、冷静以对的聪明才智，都让孩子印象深刻！画面中各种动物身材比例大小悬殊，非常吸引孩子，地搜头这只小小的老鼠要如何为高大狡猾的狐狸看牙呢？', '成长道理', 1, '鼠,狐狸', NULL, NULL, NULL, NULL, NULL, '性格,身份', NULL, NULL, NULL, 0);
INSERT INTO `data` VALUES (151, 588, '100万只猫', '婉达盖格', '蓝色,黄色', '纽伯瑞儿童文学奖银奖，刘易斯卡罗尔书架奖', 40, '3-6岁', 1, '这是一组绘本，历经时间的考验，证明是优秀的儿童读物。\n\n故事内容童趣满满，加上优美的语言，让人不忍卒读。\n\n这样生动的动物形象和有趣的故事融合在一起，让孩子耳目一新，不仅产生阅读的欲望和快感，也会从主题鲜明的每则故事中，悟出一些为人处事的基本道理。\n\n《100万只猫》\n\n从前，一位老爷爷外出寻找一只*美的猫，来给自己和妻子做伴。可他带回来了上百只、上千只、上百万只、上亿只、上万亿只猫。哪只才是*美的猫呢？', '成长道理', 1, '猫', NULL, '老人', NULL, NULL, NULL, '外貌,身份', '家庭关系,宠物关系', NULL, '乡村', 0);
INSERT INTO `data` VALUES (152, 590, '美丽的螺旋', '乔伊斯·西德曼（文） / [美]贝斯·克罗姆斯（图）', '绿色', '美国图书馆协会好书奖', 40, '3-6岁', 0, '蜗牛的壳，圆蛛结的网，向日葵的花盘，潮汐的漩涡，甚至是人的耳蜗……仔细观察就会发现，自然界中有许许多多的东西都是呈螺旋形存在的。螺旋这种形状为什么如此常见？它有什么作用？作者用精炼优美的诗文，极富韵味的刮版画，将螺旋世界的美丽与奇妙展现在我们眼前，带我们感受蕴涵于大自然中的无限美感与力量。', '自然生物', 0, NULL, NULL, NULL, NULL, NULL, '图形', NULL, NULL, NULL, NULL, 0);
INSERT INTO `data` VALUES (153, 592, '开心小猪和大象哥哥系列-----大象不会跳舞', '莫·威廉斯', '青色,灰色', NULL, 0, '3-6岁', 1, '勇于尝试，自信自在地接纳自己。开心小猪教大象哥哥跳舞，但大象哥哥似乎怎么也跳不对。是不是像书里说的，大象跳不了舞？', '成长道理', 1, '松鼠,猪,大象', NULL, NULL, NULL, NULL, NULL, '外貌,能力', '朋友关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (154, 595, '开心小猪和大象哥哥系列-----今天我要飞！', '莫·威廉斯', '蓝色', NULL, 0, '3-6岁', 1, '敢想敢做，给生活加点料。开心小猪想要飞，大象哥哥可不觉得她会飞。小猪尝试了很多方法，到底有没有飞起来呢？', '成长道理', 1, '狗,猪,大象,鸭', NULL, NULL, NULL, NULL, NULL, '外貌', '朋友关系', '早晨,中午', NULL, 0);
INSERT INTO `data` VALUES (155, 609, '小卡车系列绘本-----小红和小绿', '宫西达也', '蓝色,黑色', NULL, 40, '3-6岁', 1, '宫西达也小卡车系列绘本的第五集，这次小红他们的目的地可是一个好远远的地方呢。\n\n有多远？？？你猜……', '趣味解密', 1, NULL, NULL, NULL, NULL, '卡车,飞机', '宇宙飞船', '外貌,能力', '朋友关系', NULL, '博物馆', 1);
INSERT INTO `data` VALUES (156, 610, '小卡车系列绘本-----小红去送货', '宫西达也', '棕色,红色', NULL, 20, '3-6岁', 1, '嘟嘟——！\n\n卡车小红要去送货啦！\n\n这次来到车水马龙的大城市，看到那么多比自己速度快、能力强的“车前辈”，他觉得自己又小又没用。小红能重拾信心，顺利完成任务吗？', '情感体验', 1, NULL, NULL, NULL, NULL, '公交巴士,挖掘机,卡车,消防车', NULL, '外貌,能力', '朋友关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (157, 611, '小卡车系列绘本-----小红和小黑', '宫西达也', '棕色', NULL, 36, '3-6岁', 1, '嘟嘟——！\n\n卡车小红有了新伙伴——爱耍威风的小黑！\n\n他们要给猪先生家送建筑材料。小黑喜欢飙快车，一路上，小红担心坏了。速度快就一定好吗？两个性格迥然不同的小伙伴能顺利地完成任务吗？', '情感体验', 1, NULL, NULL, NULL, NULL, '挖掘机,小汽车,卡车', NULL, '外貌,能力', '朋友关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (158, 612, '宫西达也小卡车绘本------小红和小黄', '宫西达也', '棕色', NULL, 36, '3-6岁', 1, '嘟嘟——！\n\n小红和小黄要送货去水族馆。\n慢性子的小黄总是喜欢绕远路，让小红好担心。一路上，他们欣赏到了美丽的花田、壮丽的富士山，真像小黄说的：“只有慢慢开，才能欣赏到美丽的风景！”可是，他们的任务还能顺利完成吗？\n', '情感体验', 1, NULL, NULL, NULL, NULL, '卡车', NULL, '外貌,能力', '朋友关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (159, 613, '西游记绘本之三打白骨精', '吴承恩 原著/狐狸家 编著', '黑色,橘色', NULL, 56, '3-9岁', 1, '这是一套专门为3-9岁小朋友绘制的西游取经故事集，一共包含3个耳熟能详的故事，比如《偷吃人参果》《三借芭蕉扇》《三打白骨精》等，本册为《三打白骨精》。唐僧师徒西行取经，途经一座黑山。黑山的妖精白骨精发现了唐僧，她想害唐僧，就先后变化成两种不同身份的人迷惑唐僧。幸好孙悟空识破白骨精的变化，将她打走。唐僧不知真相，怪悟空乱打人，念紧箍咒惩罚悟空。这时白骨精又来了，她变成一个老头骗唐僧，这次悟空能识破她吗？唐僧还会误会悟空吗？', '传统文化', 1, '猴子,猪', NULL, '和尚', NULL, NULL, '怪兽', '身份,能力', NULL, NULL, NULL, 0);
