INSERT INTO `data` VALUES (287, 1003, '火山有颗热脑袋', '特拉尼 ', '蓝色,黄色', NULL, 29, '3-6岁', 1, '海底为什么会升上来气泡？水先生以前从没见过。原来，那里有一座小火山刚刚出生了……小朋友，你知道什么是火山吗？火山是怎样形成的？快和水先生相约，展开一次惊险的旅程吧！', '科学知识', 1, NULL, NULL, NULL, NULL, NULL, '水', '外貌,身份', '邻居关系', '四季轮转', '水域', 0);
INSERT INTO `data` VALUES (288, 1004, '美妙色彩变变变', '特拉尼 ', '蓝色,黄色', NULL, 0, '3-6岁', 1, '画家卡米诺一边画风景，一边教水先生有关色彩的魔法，他们成了很好的朋友。当卡米诺居住的城市被黑云笼罩时，水先生和他的朋友们赶来帮忙……结果如何呢？', '科学知识', 1, NULL, NULL, NULL, NULL, NULL, '水', '外貌,身份', '群体关系', '朝夕轮转', NULL, 1);
INSERT INTO `data` VALUES (289, 1005, '树先生成长记', '特拉尼 ', '绿色', NULL, 0, '3-6岁', 1, '阿果和皮诺买了一张用樱桃木做的新床。想不到他们就在梦中遇到了树先生。小朋友，你知道高大粗壮的树先生小时候是什么样吗？他是怎样从一棵树变成一张床的呢？快快跟随《水先生的奇妙之旅科学图画书系：树先生成长记》里的阿果和皮诺，一起去解开树先生成长的秘密吧！', '科学知识', 1, '鸟', NULL, NULL, NULL, NULL, '儿童', '外貌,身份', '朋友关系', '春,夏,秋,冬', '梦境', 1);
INSERT INTO `data` VALUES (290, 1011, '孤独的灰熊卡普', '西顿', '绿色、棕色、白色', NULL, 0, NULL, 1, '因为和人类之间发生的冲突，卡普失去了家人，从小就孤零零地生活在森林里，没有任何人帮助它。勇敢的卡普克服了一个又一个困难，终于成长为了密兹谷地的霸主。虽然如此，它也从不会主动去攻击人类。它只对那些向自己开枪，或给它设置陷阱的人发威。最终，年老的卡普被竞争者驱逐出了自己的领地。在对过去美好生活的回忆和对亲人们的思念中，它永远地进入了梦乡……其实卡普只是一头普通的灰熊，它渴望的无非是和家人一起在大自然中自由地生活。可是，即使是这样简单的愿望，最终也没有得到实现。', '成长道理', 1, '熊,狼', NULL, '猎人', NULL, NULL, NULL, '外貌,身份', NULL, '春,夏,秋,冬', NULL, 0);
INSERT INTO `data` VALUES (291, 1012, '令人敬佩的信鸽', '西顿', '白色', NULL, 0, NULL, 1, '城市里常常可以见到鸽子的身影，人们都很喜欢这种可爱的鸟类。但是，你们知道吗？鸽子有一种特殊的天性，就是归巢。从很久很久以前，人们就利用鸽子归巢的本能对鸽子加以训练，通过它们来传递信息，这样的鸽子就是信鸽。这个故事的主人公阿诺克斯就是一只优秀的信鸽。有些信鸽在执行送信任务途中会忍不住口渴和饥饿，在路上停下来休息，甚至贪玩。但是阿诺克斯从不这样，它总是尽职尽责，一次又一次出色地完成任务，并因此而出名。但是，它得到的荣誉反而给它带来了不幸——它被一个贪婪的养鸽人关了起来。阿诺克斯一心想回家，它日夜思念着自己的故乡，还有妻子特特和对它爱护有加的饲养员比利。两年之后，阿诺克斯成功地逃了出来。然而，回家的路途危险重重，为此，阿诺克斯拼上了年轻的生命。阿诺克斯虽然死了，但是人们像怀念英雄一样深深怀念着它。它腿上那个刻着“2590C”的闪闪发光的银环成了人们心中永远的传奇。', '自然生物', 1, NULL, NULL, NULL, NULL, NULL, NULL, '外貌,身份,能力', NULL, '四季轮转', '城镇', 1);
INSERT INTO `data` VALUES (292, 1014, '留守的公雁', '西顿', '白色', NULL, 0, NULL, 1, '一只翅膀受伤的大雁因为无法飞翔，在每年冬天来临之前只能目送着自己的家人飞走，然后期盼着春天的到来。', '自然生物', 1, NULL, NULL, NULL, NULL, NULL, NULL, '外貌,身份', '家庭关系', '四季轮转', '水域', 0);
INSERT INTO `data` VALUES (293, 1015, '危险的母猴吉妮', '西顿', '绿色', NULL, 0, NULL, 1, '一只脾气暴躁的母猴；在饲养员波纳米的悉心照料下，渐渐敞开了心扉。可是，意外发生了……', '自然生物', 1, '猴子', NULL, NULL, NULL, NULL, NULL, '外貌,身份', '朋友关系', '朝夕轮转', '动物园', 0);
INSERT INTO `data` VALUES (294, 1016, '乌鸦领袖银斑点', '西顿', '白色', NULL, 0, NULL, 1, '主人公是一个乌鸦小队的队长，带领自己的鸦群在加拿大多伦多附近溪谷的上空来来回回地飞了二十多年！聪明的他能很好地指挥鸦群，能用丰富的人生经验判断出敌人和险境，能用非凡的勇气指挥鸦群和天敌战斗。正是在银斑儿的带领下，乌鸦群体越来越壮大了，慢慢地发展到了二百多只。后来，他们的族群受到了游隼的攻击，银斑儿不顾自身安危勇敢地和游隼展开了战斗……', '自然生物', 1, NULL, NULL, '猎人', NULL, NULL, NULL, '外貌,身份', NULL, '四季轮转', NULL, 0);
INSERT INTO `data` VALUES (295, 1017, '淘气的小浣熊阿嘉', '西顿', '白色、灰色', NULL, 0, NULL, 1, '淘气的小浣熊阿嘉没有听妈妈的话，一不小心落入了猎人盼手中，来到了人类的世界。它会有哪些新的认识……', '自然生物', 1, NULL, NULL, '猎人', NULL, NULL, '儿童', '外貌,身份', '宠物关系,群体关系', '四季轮转', '水域', 0);
INSERT INTO `data` VALUES (296, 1018, '小猪唏哩呼噜和蛇', '孙幼军 著;裘兆明,阿波 绘', '蓝色,白色', NULL, 47, NULL, 1, '小猪去做客吃多了，肚子涨断了裤腰带，他发现了一段绳子一样的东西，就当了裤腰带。没想到那是一条蛇。小蛇花花愿意帮小猪这个忙，让他系着回到家。他们就成了好朋友，可是猪太太非常怕蛇啊，小猪和小蛇的友谊出现了麻烦。', '成长道理', 1, '猫,鼠,猪,蛇', NULL, NULL, NULL, NULL, NULL, '外貌,身份', '家庭关系,朋友关系,邻居关系', '朝夕轮转', NULL, 1);
INSERT INTO `data` VALUES (297, 1019, '小猪唏哩呼噜--卖橘子', '孙幼军 著;裘兆明,阿波 绘', '橙色,橘色', NULL, 44, NULL, 1, '小猪唏哩呼噜为了能够得到一辆和小猴一样的自行车，决定自己打工赚钱，开始卖橘子，结果橘子把马太太的牙都酸掉了，去了大象诊所治疗，可小猪淘不出治疗费，只好去大象医生家当保姆作补偿，后来他开了一家咨询公司，教会了鸭太太卖咸鸭蛋赚了钱，终于如愿以偿买了辆自行车。', '成长道理', 1, '刺猬,狐狸,猴子,猪,马,大象', NULL, NULL, NULL, NULL, NULL, '外貌,身份', '朋友关系,群体关系', '朝夕轮转', NULL, 1);
INSERT INTO `data` VALUES (298, 1020, '小猪唏哩呼噜--上学记', '孙幼军 著;裘兆明,阿波 绘', '绿色、白色', NULL, 0, NULL, 1, '小猪去上学，大狼哇呜老师很和气，小狐狸说如果他们不来上学，大狼就挣不到钱，所以小狐狸说是他们在养大狼一家。同学们总是惹麻烦，小狐狸和小猪因为一道数学题争辩起来，小狐狸威胁大狼老师如果不算他对，就让同学们都不来上学了。结果哇呜老师被气跑了，可是小猪却挺想他的。', '成长道理', 1, '狐狸,猪,熊,马,狼,羊', NULL, NULL, NULL, NULL, NULL, '外貌,身份', '师生关系,同学关系', '朝夕轮转', NULL, 0);
INSERT INTO `data` VALUES (299, 1021, '小猪唏哩呼噜--历险记（上）', '孙幼军', '绿色、白色', NULL, 0, NULL, 0, '小猪唏哩呼噜为什么叫唏哩呼噜呢？因为他吃东西的时候，总是头也不抬地“唏哩呼噜、唏哩呼噜”一片响，所以就叫了这个名字。唏哩呼噜是一只不平凡的小猪，别看他小，经历过的事情可不少。唏哩呼噜被大狼哇呜先生叼走，要给小狼宝宝当晚餐，唏哩呼噜并没有害怕，而是巧妙地逃脱了大狼的手心，还和八哥小姐一起战胜了要吃掉小狼们的月牙熊，成功地保护了小狼们。', '成长道理', 1, '猪,熊,狼', NULL, NULL, NULL, NULL, NULL, '外貌,身份', NULL, '早晨,中午,夜晚', '乡村', 0);
