INSERT INTO `data` VALUES (357, 1121, '奇奇兔蒙蒙熊--去果园', '成功图文工作室，成红军，成红歌 ', '绿色', '新闻出版总署2009年第六次向全国青少年推荐的百种优秀图书；2009年“冰心儿童图书奖”', 0, '3-6岁', 1, '蒙蒙熊想坐消防车去果园，消防车说：“对不起，我要去救火。”奇奇兔想坐救护车去果园，救护车说：“对不起，我要去接病人。”蒙蒙熊想让警车带它去果园，警车说：“对不起，我要去办案。”……蒙蒙熊和奇奇兔最终到果园了吗？', '成长道理', 1, '兔子,熊', NULL, NULL, NULL, '挖掘机,卡车,火车,消防车', NULL, '外貌,身份', '邻居关系,群体关系', NULL, '家', 1);
