INSERT INTO `data` VALUES (360, 1124, '艾特叔叔安全特攻队--一心两用的小栗子', '邹立晖', '橙色', NULL, 16, '3-6岁', 1, '课堂上，小栗子一只耳朵听课，一只耳朵跑到操场上去听小朋友们堆雪人、打雪仗；晚饭后，小栗子一边写作业，一边画画；冰场上，小栗子一边溜冰，一边看漫画书。小栗子认为一心二用，不仅节省时间，而且很有趣。小朋友们你们认为我们做事情的时候可以一心二用吗？', '习惯养成', 1, '狗', NULL, NULL, NULL, NULL, '儿童', '身份,能力', '朋友关系,群体关系', '冬,朝夕轮转', '城镇', 0);
INSERT INTO `data` VALUES (361, 1125, '艾特叔叔安全特攻队--阿莫奇卡沙漠历险记', '邹立晖', '橙色', NULL, 16, '3-6岁', 1, '淘淘独自一人去参加糖果节，可是当走到阿莫齐卡沙漠时却迷路了，并且背包里只剩下半壶水，指南针乱转一气，卫星定位仪没有一点信号，淘淘能走出沙漠吗，一路上他有哪些经历呢，我们一起去看看吧！', '趣味解密', 1, '鼠,狐狸', NULL, NULL, NULL, NULL, '儿童', '外貌,身份', '朋友关系,群体关系', NULL, '沙漠', 0);
INSERT INTO `data` VALUES (362, 1126, '艾特叔叔安全特攻队--惊心动魄的糖果盛宴', '邹立晖', '橙色', NULL, 16, '3-6岁', 1, '淘淘和沙漠狐来到糖果节，哇！这里有卖全世界最出名的糖果、神奇的火焰雪糕、朱古力喷泉、芝士火锅，红色、黄色、蓝色、橙色……就像一个巨大无比波板糖！淘淘和沙漠狐这里看看，那里尝尝，他们在糖果节会有哪些奇遇呢？', '成长道理', 1, '狐狸,蜜蜂', NULL, NULL, NULL, NULL, '儿童', '外貌,身份,能力', '朋友关系,敌人关系,师生关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (363, 1127, '艾特叔叔安全特攻队--拯救金丝猴', '邹立晖', '橙色', NULL, 16, '3-6岁', 1, '出事啦！奥普森林里唯一一只金丝猴不见了！怎么办呢，金丝猴肯定碰到了危险！艾德叔叔和淘淘、小栗子分成两队，徒步走进郁郁葱葱的奥普森林，寻找失踪的金丝猴，一路上，他们会遇到什么事情呢，最终会找到金丝猴吗……', '成长道理', 1, '猴子,蛇', NULL, NULL, NULL, NULL, '儿童', '外貌,身份,能力', '朋友关系,敌人关系,师生关系', '中午,傍晚,夜晚', NULL, 0);
INSERT INTO `data` VALUES (364, 1128, '艾特叔叔安全特攻队--谁才是冠军救援犬', '邹立晖', '橙色', NULL, 16, '3-6岁', 1, '小朋友们，你知道什么叫责任吗？责任，就是无论如何，即使放弃一切也要完成的事情！勇敢的卡卡做到了一只救援犬应尽的责任——拯救生命，而赢得了世界救援犬的冠军！卡卡是怎样赢得冠军的呢？', '成长道理', 1, '狗', NULL, NULL, NULL, NULL, '儿童', '外貌,身份,能力', '朋友关系,群体关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (365, 1129, '艾特叔叔安全特攻队--海龟岛历险记', '邹立晖', '橙色', NULL, 16, '3-6岁', 1, '淘淘和小栗子在海龟岛度假。淘淘为妈妈准备的礼物被小栗子一口咬碎，淘淘从鲨鱼口中救出小栗子，淘淘热心帮助腿抽筋的卡卡，可是有些事情却是小栗子故意设计的，这是怎么回事呢，他们在海龟岛经历了什么呢？', '成长道理', 1, '狗', NULL, NULL, NULL, NULL, '儿童', '外貌,身份', '朋友关系,群体关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (366, 1130, '肚脐，你好吗？', '七尾纯.小林雅子', '白色,橙色', NULL, 42, '3-6岁', 1, '轩轩常常因为自己的肚脐鼓出来难为情，并且觉得它一点儿用处都没有，老是去抠它，那样可不对哦。原来在轩轩出生之前，肚脐可起到了非常重要的作用呢。', '健康问题', 1, NULL, NULL, NULL, NULL, NULL, '儿童', '身份', NULL, NULL, NULL, 1);
INSERT INTO `data` VALUES (367, 1132, '谁是蛀虫的朋友', '七尾纯.小林雅子', '白色,橙色', NULL, 42, '3-6岁', 1, '本书讲述了小朋友为什么一定要刷牙，让孩子在模仿和游戏中学会刷牙。', '习惯养成', 1, NULL, NULL, NULL, NULL, NULL, '儿童', '外貌,身份,能力', '敌人关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (368, 1135, '血液兄弟好样的！', '七尾纯.小林雅子', '白色,橙色', NULL, 42, '3-6岁', 1, '本书讲述了血液的作用和成分，有红细胞、白细胞、血小板等等。他们的作用可大了。', '科学知识', 1, NULL, NULL, NULL, NULL, NULL, NULL, '身份,能力', '群体关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (369, 1136, '眼泪小精灵，谢谢你', '七尾纯.小林雅子', '白色,橙色', NULL, 42, '3-6岁', 1, '眼泪会一点儿一点儿地把眼睛打扫干净，还会把营养输送给眼睛，然后通过一个小孔，流到鼻子里。眼泪的作用可大了。', '科学知识', 1, NULL, NULL, NULL, NULL, NULL, '儿童', '外貌,身份,能力', '朋友关系', NULL, '公园', 1);
