INSERT INTO `data` VALUES (478, 1353, '笨笨驴系列--这里的河边静悄悄', '曹文轩，王祖民', '黄色', NULL, 0, '3-6岁', 1, '本系列讲述了一头憨态可爱的小毛驴身边发生的各种有趣的事情，幽默智慧，富有哲理。小驴子被美术学院的老师看中，请它去给学生做模特，它却为了救一个在水里面假装喊“救命”的小男孩，模特没做成；它喜欢奔跑，主人训练它去参加驴赛，它却因为别的驴子骗它说它身上有块杂毛而输了比赛……书中“小驴”看似笨笨的行为也许正是很多成长中的孩子或者大人的行为，在引人发笑的同时又让人深思。', '成长道理', 1, '牛,羊', NULL, NULL, NULL, NULL, '儿童', '外貌,身份,能力', '师生关系,群体关系', '朝夕轮转', '乡村', 0);
