INSERT INTO `data` VALUES (506, 1419, '咕咚来了', '幼儿画报》编辑部/改编', '绿色', NULL, 36, '3-7岁', 0, '本书是经典民间故事的再创作，画面温暖细腻、栩栩如生，更符合3～6岁幼儿的欣赏水平。 本书以一只小兔为线索，突出小兔、狐狸、猴子、狗熊、狮子等动物的动作、神态。改编后的情节、语言更简洁，符合图画书的特点。', '习惯养成', 1, '兔子,狐狸,猴子,大象', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '群体关系', '中午', NULL, 1);
