INSERT INTO `data` VALUES (611, 1872, '爸爸的魔力抱抱', '方锐', '黄色,蓝色', NULL, 36, '3-6岁', 1, '《爸爸的魔力抱抱》：小灰熊艾米*喜欢和爸爸抱抱了，他觉得爸爸的抱抱有让他充满精神的魔力。这天，爸爸出差了，没有了爸爸抱抱的艾米觉得做什么都提不起精气神。正当他郁闷的时候，却收到了来自爸爸的神秘礼物，这个神秘礼物是什么？它会让艾米开心起来吗？', '情感体验', 1, '熊', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '家庭关系,朋友关系,邻居关系', '夜晚,朝夕轮转', '家,公园', 1);
