INSERT INTO `data` VALUES (613, 1874, '熊爸爸不怕', '保罗·布莱特', '黄色,红色', NULL, 0, '3-6岁', 0, '保罗·布莱特创作的《熊爸爸不怕》讲述了：一 天晚上，狂风大作，电闪雷鸣，熊爸爸一家正在睡觉。熊爸爸的三个儿子一个接一个说很害怕，要和爸爸一起睡，他们还说外面有怪物，大儿子甚至说自己看到了怪物的影子，', '情感体验', 0, '熊', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '家庭关系,邻居关系', '夜晚,雨,打雷闪电,大风', '家', 1);
INSERT INTO `data` VALUES (614, 1876, '爸爸太忙了', '老渔', '白色', NULL, 0, '3-6岁', 1, '老渔著的，郭淑玲绘画的《爸爸太忙了(精)》讲述了孤独的安琪的愿望就是想要一只小兔子，这样她就有朋友了，就可以和它分享自己的小秘密了。 但是这个愿望一直未能实现，原因是：爸爸总是忘记给她买小兔子！ 他把所有时间都放在了工作和社交上，直到有，熟睡的爸爸在梦中变成了兔子，安琪像爸爸照顾小时候的安琪那样无微不至地照顾小兔子，给它做蛋糕，给它讲故事，给它变换不同的漂亮衣服，与它分享自己的快乐和囧事，抱怨爸爸的忙碌等等。 直到这时，变成兔子的爸爸才意识到女儿的孤独，自己对女儿的忽视。', '情感体验', 1, '兔子', NULL, NULL, NULL, NULL, '家庭成员', '外貌,性格', '家庭关系,宠物关系', '朝夕轮转,生日', '家,梦境', 0);
INSERT INTO `data` VALUES (615, 1877, '爸爸很忙', '铁皮人科技', '绿色,黄色', NULL, 40, NULL, 1, '《爸爸很忙》爸爸总是忙于各种各样的事情，没有时间陪伴叫叫。叫叫伤心极了，他向妈妈提出，要找一个可以陪自己玩、给自己的新爸爸。这可急坏了爸爸。叫叫和爸爸该怎样修复他们之间的亲子关系呢？', '情感体验', 0, '鸡', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '家庭关系', '朝夕轮转', '家,商店', 0);
INSERT INTO `data` VALUES (616, 1878, '爸爸不见了', '方锐', '绿色,蓝色', NULL, 36, '3-6岁', 1, '《爸爸不见了》：放假*天，小河马达米是和爸爸一起度过的。他们一起下棋、折纸、锄草，好玩极了！吃饱午饭的达米听着爸爸讲的故事打起了瞌睡，他想听第二个的时候，却没有回应，达米睁眼一看，咦，爸爸不在这里，他去哪里了？达米找遍了厨房、卧室、储物间、客厅、院子，都没有发现老爸的身影，爸爸不见了吗？', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, NULL, '外貌,性格', '家庭关系', '夏,早晨,中午,朝夕轮转', '家', 0);
INSERT INTO `data` VALUES (617, 1879, '我好喜欢爸爸', '方锐', '蓝色', NULL, 36, '3-6岁', 1, '《我好喜欢爸爸》：小猪辛西娅很喜欢和爸爸，他们总是一起逛公园，一起骑车兜风。这天，她收到了两条消息，坏消息是爸爸要去另一个城市工作，他们不得不短暂地分离；好消息是每个周末爸爸都会回来。和爸爸分开的时间真漫长，辛西娅好想爸爸，周末爸爸会回来吗？要怎么度过这个周末呢？', '情感体验', 1, '猪', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '家庭关系', '早晨,中午,傍晚,夜晚,朝夕轮转', '公园,图书馆', 0);
INSERT INTO `data` VALUES (618, 1880, '不一样的好爸爸', 'G.V.西纳顿', '黑色', NULL, 0, '0-3岁', 1, '《彩虹色的小白鱼》是一套给婴幼儿看的图画书，这套书文字简单、图画简洁，非常适合婴幼儿阅读。0-3岁是孩子视觉观察与认知能力快速发展时期，在这套书中，黑色的底色和彩色的人物形象形成鲜明对比，促进宝宝视觉发育。书中的故事温馨易懂，内容涵盖情绪、情感、方位、数数等方面，朗朗上口的问答形式增加了参与感，有利于提升幼儿的表达能力。快来看看这套静静的彩色小书，让我们和孩子一起，走进小白鱼的世界，体验小白鱼的情绪，看看小白鱼遇到了什么，学会了什么。通过看小白鱼的故事，孩子也会有很大收获。', '线条色彩', 1, '蟹,鱼,鲸鱼', NULL, NULL, NULL, NULL, NULL, '外貌,身份', '群体关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (619, 1881, '戴眼镜的龙爸爸', '郑渊洁', '绿色', NULL, 30, '7-10岁', 1, '好好吃饭、认真刷牙、爱护眼睛、安全用电与出行、保护自己……好习惯，就得从小养成！听童话大王郑渊洁讲有意思的12生肖动物故事，在快乐阅读的同时，养成好习惯！每一本书的结尾处都加入了郑渊洁的“教子秘籍”，帮助家长掌握与孩子沟通，教育孩子的正确方法。', '习惯养成', 1, NULL, NULL, NULL, NULL, NULL, '龙', '外貌,性格', '家庭关系,朋友关系', '生日', '家,城镇,医院', 1);
INSERT INTO `data` VALUES (620, 1882, '我爸爸最酷', '罗茜·史密斯', '蓝色,白色', NULL, 22, '1-3岁', 1, '《我爸爸最酷》：无论是大还是小，无论是有羽毛还是毛茸茸，爸爸总是知道如何逗我们发笑。我的爸爸是最酷的，因为他对我而言是那么的有趣和疯狂。你的呢？', '情感体验', 1, '猪,羊,狮子,鸡,鱼', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '群体关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (621, 1883, '你看起来好像很好吃', '宫西达也', '白色,绿色', '剑渊绘本乡绘本奖大奖', 0, '4岁以上', 1, '很久以前，在一个晴朗的日子里，山嘭嘭嘭地喷火，地咚咚咚地摇晃。这时，甲龙宝宝出生了。一天，它遇到了一头霸王龙，霸王龙流着口水，想要猛扑过去。就在这时，霸王龙忽然听到“爸爸”，原想吃掉小甲龙的霸王龙被小甲龙的一句“我想早点长得像爸爸一样！”击中了心底的柔情，从而改变了小甲龙和霸王龙之间的关系。小甲龙与霸王龙之间的感人的亲情让人为之动容，孩子在听故事之余，更可以潜移默化地体验到爱与被爱的快乐、人与人之间交往的真谛。', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, NULL, '外貌,性格', '群体关系', '朝夕轮转', '自然', 0);
INSERT INTO `data` VALUES (622, 1884, '我爸爸超厉害', '宫西达也', '绿色', NULL, 24, '3-6岁', 1, '《我爸爸超厉害》是启发精选国际大师名作绘本系列之一。“呜——呜——”小猪在草原上哭得好伤心！小伙伴们纷纷赶来，想知道到底出了什么事……这是一个温暖的故事，通过小花豹、小企鹅、小象以及小老鼠的分享，表现出孩子与爸爸之间的浓浓情感。书里的小动物们描述自己心中的爸爸，满怀爱意地说出爸爸*厉害的地方。通过本书，表现出孩子对爸爸的崇敬，以及“爸爸”这个角色在家庭中给孩子带来的影响和重要性。', '情感体验', 0, '鼠,猪,豹子', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '家庭关系,朋友关系', NULL, '家', 0);
INSERT INTO `data` VALUES (623, 1887, '我不想离开你', ' G.V. 西纳顿', '黄色', NULL, 32, '3-6岁', 0, '小袋鼠一天天长大了，袋鼠妈妈鼓励小袋鼠离开育儿袋，自己去探索美妙的世界。她想了各种办法诱导小袋鼠，但小袋鼠却总是不愿意离开妈妈……如何克服*初的分离焦虑，是父母和孩子都会面临的难题。请读这本书吧，答案就在那儿等着你！', '情感体验', 1, '大象,长颈鹿,蝴蝶,鸟', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '家庭关系,群体关系', '朝夕轮转', '自然', 0);
INSERT INTO `data` VALUES (624, 1889, '不再害怕和妈妈分开', '英格·迈尔', '蓝色', NULL, 33, '3-6岁', 1, '小羊富丝害怕看不见他的妈妈。妈妈到哪儿他都要跟着。如果妈妈需要自己出去不带着他，他就会伤心地咩咩叫。在幼儿正常的成长过程中，常常会担心与父母分开。在*的情况下，有的孩子甚至会因为待在和父母不同的房间而感到极度焦虑。英格·迈尔编写的这本《不再害怕和妈妈分开》提供了一系列的观点和方法，帮助孩子用积极的方式去看待事情，克服他们对未知的恐惧。《不再害怕和妈妈分开》是这个系列的第—本。', '情感体验', 1, '羊', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '家庭关系', '朝夕轮转', '农牧场', 0);
INSERT INTO `data` VALUES (625, 1892, '野兽国', '莫里斯·桑达克', '蓝色', '科迪克金奖', 48, '3-6岁', 0, '《野兽国》以简单诗意的语言讲述了调皮男孩迈克斯的故事：他与妈妈大闹了一场，没吃晚饭被关进了自己的房间。像孤身一人的奥德修斯，迈克斯旋即开始了远航：一波波的海浪为迈克斯带来一艘小船，他驾着小船出发，过了晚上，到了白天，过了一周有一周，过了几乎一整年，终于来到野兽国。　　在那里，迈克斯统领了那些暴躁狂乱的野兽，在疯过闹过之后，他开始想念那些*他的人，*后他放弃了野兽国国王的王位，回到了*他的亲人那里，他的怒气已散，发现晚饭摆在那儿。', '哲理思考', 1, NULL, NULL, NULL, NULL, NULL, '家庭成员,儿童', '外貌,性格', '家庭关系', '夜晚,朝夕轮转', '家,梦境', 0);
INSERT INTO `data` VALUES (626, 1894, '把坏脾气收起来', ' 卡罗尔•罗思', '红色', NULL, 24, '3-6岁', 0, '小老虎的脾气很大！如果事情没按他的想法来，他就大哭、大叫、或是跺脚，有时候还三招一起用。但是这次，当妈妈说“把你的坏脾气收起来，否则……”的时候，小老虎不得不有所改变了。他应该把坏脾气收到哪里去呢？放到口袋里，还是小裤衩里？', '哲理思考', 1, '老虎', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '家庭关系', '朝夕轮转', '家', 1);
