INSERT INTO `data` VALUES (671, 1971, '查理与劳拉--我特别了解豚鼠', '罗伦·乔尔德', '红色', NULL, 0, '3-6岁', 1, '《我特别了解豚鼠》劳拉喜欢班里的小豚鼠，她特别希望在假期的时候把它带回家去照顾。可班里的小朋友都比劳拉了解豚鼠，汉森老师会同意劳拉的计划吗？查理哥哥会怎样帮助劳拉将喜好变成一份完美计划？动手参与、亲身感受，可以激发孩子自身的学习能量。', '成长道理', 1, NULL, NULL, NULL, NULL, NULL, '儿童', '外貌,性格', '家庭关系,宠物关系,同学关系', '朝夕轮转', '家,学校', 1);
INSERT INTO `data` VALUES (672, 1973, '小熊兄妹快乐成长系列--第一次过河', '陈晖', '绿色', NULL, 0, '2-4岁', 1, '这是一本北京师范大学陈晖教授写给2-4岁孩子的快乐成长绘本，侧重于讲述关注二娃同样给大娃关心爱护的主题。夏天来了，果果第一次过河。壮壮为果果加油，小松鼠也陪在她身边，果果还是害怕得不得了！', '成长道理', 1, '熊', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '家庭关系', NULL, NULL, 0);
