INSERT INTO `data` VALUES (676, 1978, '小熊兄妹快乐成长系列--天黑了', '陈晖', '蓝色,黄色', NULL, 0, '2-4岁', 1, '这是一本北京师范大学陈晖教授写给2-4岁孩子的快乐成长绘本，侧重于讲述关注二娃同样给大娃关心爱护的主题。壮壮长大了，要自己睡觉了，可他实在睡不着，他去找妈妈，妈妈却在哄小妹妹睡觉；他想到外面走走，小伙伴却都睡着了。壮壮决定自己玩，玩一会儿捉迷藏，吃一点儿好吃的，看一会儿书……这个夜晚，壮壮学会了自己入睡。', '成长道理', 1, '熊', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '家庭关系', '夜晚', '家', 0);
