INSERT INTO `data` VALUES (717, 2053, '工程师麦克--去麦克家卡工具房', '乔治·约翰逊', '绿色', NULL, 24, '3-6岁', 1, '麦克准备给布法一个惊喜。工具箱里有他需要的各种工具:折尺、锯子、锤子、冲击钻和螺丝刀。有了这些，他就能变出一个漂亮的小木屋!', '科学知识', 1, '狗', NULL, NULL, NULL, NULL, NULL, '外貌,职业,能力', '朋友关系,宠物关系', NULL, '家', 1);
