INSERT INTO `data` VALUES (719, 2055, '艾玛', '大卫·麦基', '彩虹色', NULL, 0, '3-6岁', 1, '象都是灰色的，但是在大卫?麦基的童话故事里有一头彩色格子的大象，他叫艾玛，艾玛和别的大象都不一样，他是那么特别，所以在他身上发生了许多特别有趣的故事，在这些故事里，他认识了许多好朋友，他和好朋友们一起在故事里慢慢地长大。一开始，艾玛总为自己的花格子自卑，但后来，他发现自己是特别的！', '情感体验', 1, '大象', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '家庭关系,群体关系', '早晨,夜晚,朝夕轮转', NULL, 1);
INSERT INTO `data` VALUES (720, 2056, '艾玛，小玫和超人小飞象', '大卫·麦基', '粉红色', NULL, 32, '3-6岁', 1, '安静的雨林里，突然传来了震天动地的响声，到底怎么啦？原来，是粉红色小象小玫的象群正在给老象爷爷过生日呢。他们边喊着“生日快乐”，边使劲儿地跺脚，大地也跟着颤抖。突然，老象爷爷脚下的地面裂开了，他被独自困在了石柱上，谁能帮帮忙呢？\n', '情感体验', 1, '大象', NULL, NULL, NULL, NULL, NULL, '外貌,性格,能力', '家庭关系,朋友关系,群体关系', '生日', NULL, 0);
INSERT INTO `data` VALUES (721, 2057, '艾玛踩高跷', '大卫·麦基', '蓝色', NULL, 36, '3-6岁', 1, '叢林裡，突然出現一群獵人。\n　　大象們很擔心，拼命想辦法逃過這一劫。\n　　鬼點子*多的花格象艾瑪，\n　　想出了一個踩高蹺的妙點子……\n\n　　本書附掃碼聽雙語故事\n　　特邀資深兒童戲劇工作者羅北安\n　　為孩子歡喜獻聲，生動活潑的旁白\n　　搭配優美俏皮的配樂，精采詮釋\n　　好聽有趣的《艾瑪踩高蹺》。', '情感体验', 1, '大象', NULL, '猎人', NULL, NULL, NULL, '外貌,性格', '敌人关系,群体关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (722, 2058, '艾玛打雪仗', '大卫·麦基', '白色', NULL, 24, '3-6岁', 1, '天气真冷呀！为了让大象们暖暖身子，艾玛带着大家去爬山，到了山上，大象们见到了从没见过的白雪。他们兴奋地滑冰、打雪仗，谁都没注意到艾玛悄悄地走开了。可是，当他们找到艾玛时，他好像是被冻住了，变成了一头雪白的大象……', '情感体验', 1, '大象', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '朋友关系,群体关系', '早晨,雪', NULL, 0);
INSERT INTO `data` VALUES (723, 2059, '艾玛飞起来了', '大卫·麦基', '橙色', NULL, 36, '3-6岁', 1, '大象到底会不会被风吹到天上？狂风呼啸的天气里，为了证明“大象真的不会被风吹走”，艾玛顶着风朝山洞外走去。可没过多久，天上传来了艾玛的呼救声，大象们都哈哈大笑，以为他又在开玩笑……\n', '情感体验', 1, '大象,鸟', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '家庭关系,朋友关系,群体关系', '大风', NULL, 1);
INSERT INTO `data` VALUES (724, 2060, '艾玛和彩虹', '大卫·麦基', '蓝色', NULL, 24, '3-6岁', 1, '暴风雨停了，天空中出现了一道刷白的弧线。原来，是彩虹的颜色没有了！艾玛要把自己的颜色给它，他找呀找呀，终于在瀑布后面找到了彩虹的尽头。艾玛不假思索地往瀑布后面走去，彩虹一点一点地有了颜色，可是，艾玛怎么样了呢？', '情感体验', 1, '兔子,猴子,大象,狮子,老虎,长颈鹿,鳄鱼,鸟,鱼', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '朋友关系,群体关系', '雨,打雷闪电,彩虹', NULL, 1);
INSERT INTO `data` VALUES (725, 2061, '艾玛和超人小飞象', '大卫·麦基', '红色', NULL, 32, '3-6岁', 1, '安静的雨林里，突然传来了震天动地的响声，到底怎么啦？原来，是粉红色小象小玫的象群正在给老象爷爷过生日呢。他们边喊着“生日快乐”，边使劲儿地跺脚，大地也跟着颤抖。突然，老象爷爷脚下的地面裂开了，他被独自困在了石柱上，谁能帮帮忙呢？', '情感体验', 1, '兔子,猴子,大象,狮子,老虎,蛇,鳄鱼,鸟', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '家庭关系,朋友关系,群体关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (726, 2062, '艾玛和大鸟', '大卫·麦基', '红色', NULL, 32, '3-6岁', 1, '咦，今天为什么一只蓝鸟都看不见？艾玛到处寻找，终于在山洞里找到了他们。原来，近来了一只凶巴巴的大鸟，总是欺负他们，怎样才能赶走这个横行霸道的家伙呢？的艾玛悄悄地把自己的计划告诉了朋友们……', '情感体验', 1, '大象,鸟', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '家庭关系,朋友关系,敌人关系,群体关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (727, 2063, '艾玛和丢失的泰迪熊', '大卫·麦基', '深蓝色', NULL, 32, '3-6岁', 1, '呜……呜……”宁静的夜晚，从哪里传来了哭声？原来是小象，他把心爱的泰迪熊弄丢了。艾玛把自己的泰迪熊借给他，天一亮，就出发去找小象的泰迪熊，可是快到傍晚还是没有找到。突然，艾玛看见大树下有一只泰迪熊，奇怪的是，这只泰迪熊竟然开口说话了……', '情感体验', 1, '大象,狮子,老虎', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '家庭关系,朋友关系,群体关系', '夜晚,朝夕轮转', NULL, 1);
INSERT INTO `data` VALUES (728, 2064, '艾玛和河马', '大卫·麦基', '灰色', NULL, 36, '3-6岁', 1, '哎呀，本来就很拥挤的河里，搬来了一群河马，这是怎么回事呢？原来，他们栖居的河干了，因为河水流出的缺口被一堆大石头堵住了！河马们没有长鼻子，怎么才能把这些石头弄走呢？没关系，聪明的艾玛有办法！', '情感体验', 1, '大象', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '家庭关系,朋友关系,群体关系', '早晨,朝夕轮转', NULL, 1);
INSERT INTO `data` VALUES (729, 2065, '艾玛遇见怪家伙', '大卫·麦基', '橙色', NULL, 16, '3-6岁', 1, '花格子大象艾玛遇见了一个怪家伙，他喜欢跳上跳下，跳得别提有多高了！可是他闷闷不乐，认为自己跳得既不高也不远，不够资格参加跳跃大赛。艾玛为了帮助他，想出了一个好主意……', '情感体验', 1, '大象,狮子,老虎', NULL, NULL, NULL, NULL, NULL, '外貌,性格', '家庭关系,朋友关系,群体关系', '早晨,朝夕轮转', NULL, 1);
INSERT INTO `data` VALUES (730, 2066, '艾玛节真快乐', '大卫·麦基', '黄色', NULL, 0, '3-6岁', 1, '艾玛节要来了，大象们一觉醒来，却发现每一头象身上都有了花格子，根本不知道哪个是艾玛！为了寻找真正的艾玛，他们扑通扑通跳进河里，把颜色洗掉，却发现每一头象都是灰色的！ 更令人吃惊的是，河的对岸有一头灰色的象，说自己就是原来的花格子大象艾玛……', '情感体验', 1, '大象', NULL, NULL, NULL, NULL, NULL, '外貌,性格,能力', '朋友关系,群体关系', '早晨,夜晚,朝夕轮转', NULL, 1);
INSERT INTO `data` VALUES (731, 2067, '艾玛和红色象爷爷', '大卫·麦基', '紫色', NULL, 0, '3-6岁', 1, '圣诞节到啦！小象们都想见见坐着雪橇送礼物的红象爷爷，他们围着艾玛，叽叽喳喳问个不停。艾玛带着他们去找圣诞树，还叮嘱他们藏好，千万别让人发现，最后，小象们见到红象爷爷了吗？', '情感体验', 1, '大象', NULL, NULL, NULL, NULL, NULL, '外貌,性格,能力', '朋友关系,群体关系', '早晨,夜晚,朝夕轮转,雪,节日', NULL, 1);
INSERT INTO `data` VALUES (732, 2069, '艾玛和韦伯', '大卫·麦基', '红色', NULL, 14, '3-6岁', 1, '艾玛的表弟韦伯会口技，能让自己的声音听起来像是从很远很远的地方传来的。今天，韦伯要来看望艾玛，大象们出发去接他，可是，每当他们走到声音传来的地方，就发现韦伯根本不在那儿，突然，树上传来了韦伯的呼救声，可这次，大象们再也不想找了……', '情感体验', 1, '兔子,大象,老虎', NULL, NULL, NULL, NULL, NULL, '外貌,性格,能力', '家庭关系,朋友关系,群体关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (733, 2070, '艾玛和佐达婶婶', '大卫·麦基', '蓝色,黄色,绿色', NULL, 32, '3-6岁', 1, '艾玛和表弟韦伯去看望佐达婶婶，她的耳朵有点儿背，总是把他们的话听错：“在等”听成“在疼”，“逗笑”听成“泡澡”，“再见”听成“闪电”……天哪！不过，艾玛和韦伯还是喜欢跟佐达婶婶在一起，因为他们是一家人，还都是那么特别！', '情感体验', 1, '大象', NULL, NULL, NULL, NULL, NULL, '外貌,性格,能力', '家庭关系,朋友关系,群体关系', NULL, '家', 1);
INSERT INTO `data` VALUES (734, 2081, '奇先生妙小姐系列  忙碌小姐', '罗杰·哈格里维斯', '白色', NULL, 0, '3-6岁', 1, '忙碌小姐认为她没有时间使自己吃得好一点。每天吃快餐可以节省很多时间，可以有更多的时间去奔波！然而，当忙碌小姐开始感到不舒服时，阳光小姐能教她腾出时间来吃健康的食物吗？ 《忙碌小姐学做水果沙拉》探索了如何腾出时间吃均衡和多样化的饮食，可以帮助你过上幸福和健康的生活。包括一个有趣和简单的奇先生妙小姐食谱，这个故事鼓励读者去尝试新事物，享受新鲜的水果和蔬菜。', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, '精灵', '外貌,性格,身份,能力', '群体关系', '朝夕轮转', NULL, 0);
