INSERT INTO `data` VALUES (782, 2202, '小桃子开火车', '丰田一彦', '桃红色', NULL, 0, '0-2岁', 1, '小桃子和金鱼、仙人掌一起玩开火车的游戏。上坡、下坡，前面有个山洞，三个人钻进山洞。出来的时候，他们竟然推着一辆真的小火车…..', '成长道理', 1, NULL, '仙人掌', NULL, NULL, NULL, '儿童,精灵', '外貌,性格', '朋友关系', NULL, NULL, 1);
