INSERT INTO `data` VALUES (826, 2278, '便便恐龙之魔法秋千', '汤姆·弗莱彻，道基·波伊特', '红色', NULL, 32, '3-6岁', 1, ' 奶奶一千零八岁生日这天，丹尼和便便恐龙吃了一顿超级难吃的蛋炒西蓝花大餐。\n\n  饭后，他们荡起了魔法秋千，一不小心到了久远的侏罗纪时代。\n\n  在这里，他们结识了“恐龙三公子”，遭遇了火山大爆发。\n\n  丹尼想返回地球，但人员超重，魔法秋千无法荡起来了。\n\n  怎么办？聪明的便便恐龙马上想到了他的大屁股……', '情感体验', 1, '恐龙', NULL, '老人', NULL, NULL, '儿童', '外貌,性格,能力', '家庭关系,朋友关系', '生日', NULL, 1);
