INSERT INTO `data` VALUES (888, 13, '晚安小鸡球球', '入山智', '黄色', NULL, 48, '3-6岁', 0, ' 小鸡球球成长绘本系列共6册，以一只绒毛小鸡为主角，讲述了小小孩的生活中最常见的问题。每个故事都讲述了宝宝的一次成长经历。1-3岁的宝宝会在故事里找到自己，更容易产生认同感。随处可见的“藏与找”的游戏，让宝宝充满期待，伸出手去上下翻动、左右查看，调动感官去探索，和小鸡球球一起感受立体阅读。', '情感体验', 0, '鸡', NULL, NULL, NULL, NULL, NULL, '性格,能力', '群体关系', '春,夏,秋,冬', '乡村,自然,农牧场,动物园', 1);
INSERT INTO `data` VALUES (889, 22, '爸爸抱抱我吧', '卢庆实', '蓝色', NULL, 12, '3-6岁', 0, '  卢庆实著、金荣坤绘的《爸爸抱抱我吧（精）》主要讲述小女孩彩媛和爸爸的相处故事。全书其实是彩媛的一个梦，在她的梦中妈妈经常要抱她，而彩媛的爸爸却从来不抱彩媛，彩媛多么渴望爸爸温暖的拥抱啊，彩媛幻想了很多爸爸从来不拥抱自己的各种理由。彩媛越想越伤心，最终她从睡梦中哭着醒了过来，发现爸爸就在身边，他们俩来了一个大大拥抱，彩媛再也不哭了，她知道爸爸确实是爱她的。', '情感体验', 1, '熊', NULL, NULL, NULL, NULL, NULL, '外貌', '家庭关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (890, 35, '森林动物', '乔纳森·利顿', '紫色', NULL, 12, '3-6岁', 0, '本书稿展现了森林里20种动物行走、啄食、嚎叫等场景。不仅有朗朗上口的语言，宝贝们可以一边念一边翻找；还通过一问一答的形式展示了丰富的科普小知识，让孩子探索大自然、了解动物的生活习性。此外，在每个跨页的右侧折页上，教孩子认知数字以及寻找隐藏的小动物。画风色彩明艳丰富，刺激宝宝视觉神经，色调搭配和区分清楚，培养孩子的审美力。小小个的灰田鼠、脖子灵活的猫头鹰，淘气包小狐狸、臭屁王臭鼬……动物们其乐融融地在森林生活，一起去看看吧！本书稿使小读者身临其境地培养认知能力，促进智力发育。', '自然生物', 1, '鼠,兔子,刺猬,狐狸,松鼠,熊,鹿,狼,羊,蚂蚁,蚯蚓,鸟', NULL, NULL, NULL, NULL, NULL, '外貌', '群体关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (891, 36, 'go，cub', 'Susan', '绿色,黄色', NULL, 23, '3-6岁', 0, 'Join the little lion cub on his day of adventures on the African savanna. Watch him run, jump, play, eat, and sleep, cuddle with mom, and roll around with his brother and sister. Told in simple yet lively text built with sight words and using picture icons to aid reading, National Geographic Pre-readers will enchant kids just beginning their journey with books.', '自然生物', 1, '狮子,老虎', NULL, NULL, NULL, NULL, NULL, NULL, '群体关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (892, 37, 'at the beach', 'Alexa', '黄色,白色', NULL, 0, '3-6岁', 0, 'Have fun at the beach!\n\n\nIn this easy reader, kids will have fun seeing photographs and reading about the fish that live in the ocean, sand castles on the beach, and birds in the sky.', '情感体验', 1, '乌龟,鸟,蟹', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `data` VALUES (893, 40, 'buzz bee', 'Szymanski', '黄色', NULL, 24, NULL, 0, 'Bzzz! Learn all about honey bees in the next pre-reader from National Geographic Kids!Through text features such as a vocabulary tree and a wrap-up activity, kids will be introduced to new words and concepts – helping them expand their understanding of the world.', '自然生物', 1, '蜜蜂', NULL, NULL, NULL, NULL, NULL, '能力', '群体关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (894, 43, 'peek，otter', 'Shira Evans', '绿色', NULL, 0, NULL, 0, NULL, '情感体验', 1, '蟹,鱼', NULL, NULL, NULL, NULL, NULL, '能力', NULL, NULL, NULL, 0);
INSERT INTO `data` VALUES (895, 44, '谁住在花园里', '帕芙拉·哈娜科夫娃,艾琳·高夫', '蓝绿色', NULL, 0, '3-6岁', 1, '本书为“好奇宝宝认知立体绘本”系列中的一本，向孩子们介绍了居住在花园里的动物们，让孩子了解它们的住所和生活习性，给孩子带来丰富的情感体验和生命教育。集科普性、艺术性、故事性于一体，将启蒙认知的功能和绘', '自然生物', 1, '狗,蚯蚓,蜜蜂,鸡', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `data` VALUES (896, 45, '门兽', '刘畅', '灰色', NULL, 0, '3-8岁', 0, '门兽镇守王府的石台上已有百年之久，想当初，那时候的他可威武了！然而，随着时代变迁，王府的人搬走了，宅院荒废了，原本繁华的光荣盛景，在一夕之间黯然失色，连同站在对面的那只门兽，也消失不见了！独留门兽自己孤守断壁颓垣，以及沉陷于另一只门兽莫名消失的落寞……时间像水一样慢慢流走了，门兽也渐渐被人遗忘……直到有一个冬天的早晨，一位小姑娘来了。她的童言稚语，融化了门兽内心潜沉多年的寂寞。随着物换星移，王府渐渐被修缮，有了新气象，独坐石台几十年的门兽，也即将有另一只门兽坐在他对面…… 本书以黑白铅笔素描，道尽门兽从盛世守宅护院的威武，到王府颓败的落寞，直到物换星移、世代转变，毅然守候在原岗位，通过新宅院、新门兽的注入，重燃对新生的美好与向往。', '传统文化', 1, NULL, NULL, NULL, NULL, NULL, '儿童', '外貌,性格,身份', '朋友关系', '四季轮转,夜晚', '家', 1);
INSERT INTO `data` VALUES (897, 46, '吃掉黑暗的怪兽', '乔伊斯·邓巴', '黄色', NULL, 0, '3-6岁', 0, '小男孩球球不喜欢黑漆漆的床底下，他总觉得那里藏着什么怪兽。这一回，真的有个小小的怪兽哦！这只怪兽总觉得身体里有个大洞，让它饿得不得了！不过，除了黑暗，它什么也不吃。可是吃光了所有黑暗后，怪兽还是觉得好饿好饿......', '成长道理', 1, NULL, NULL, NULL, NULL, NULL, NULL, '能力', NULL, NULL, '家', 1);
INSERT INTO `data` VALUES (898, 56, '小王子', '郑克鲁', '蓝色', NULL, 0, '7-10岁', 0, '外星人在哪里？小王子就是呀！纯洁、忧郁的小王子，来自宇宙间某个不为人知的小星球。他与一位飞行员在撒哈拉沙漠相遇，攀谈中，小王子的秘密逐渐揭开，他是因为与美丽而骄傲的玫瑰发生了感情上的矛盾，才负气出走的。星际漫游中，小王子造访了六个星球，却没有找到一个朋友。在地理学家的建议下，他来到了地球，收获了狐狸的友谊，也从狐狸那里学会了人生真谛。*重要的是，他终于懂得了玫瑰的花招背后所隐藏的无限柔情，于是决定回归星球，回到玫瑰身边。在一条毒蛇的帮助下，小王子消失天际，飞行员再也没有见过这个可爱的小人儿了……', '哲理思考', 1, '大象', NULL, '王子', NULL, NULL, NULL, '性格,情绪', '朋友关系,群体关系', '朝夕轮转', '宇宙', 1);
INSERT INTO `data` VALUES (899, 58, '动物明星排行榜. 神奇动物排行榜', '司艳琛', '紫色,黄色', NULL, 0, '7-10岁', 1, '俏皮活泼、简单通俗的语言，为孩子们描绘了一个生动的动物王国，让人读起来更加津津有味。水中的高压线电鳗，擅长飞行的鼯鼠，什么都说知了的蝉……当你在榜单上看到这些熟悉的名字，或许会大感意外，甚至无法置信。没想到这些普通，毫不出彩的动物身上，也掩藏着众多鲜为人知的秘密。赶快来发掘这些神奇动物身上的奥秘吧！', '自然生物', 1, '狗,马,蝉,鱼', NULL, NULL, NULL, NULL, NULL, '外貌,能力', '敌人关系,群体关系', NULL, '自然,动物园', 0);
INSERT INTO `data` VALUES (900, 59, '动物明星排行榜. 珍稀动物排行榜', '司艳琛', '黄色,绿色', NULL, 0, '5-8岁', 1, '本书属于中低年级拼音读物。俏皮活泼、简单通俗的语言，为孩子们描绘了一个生动的动物王国，让人读起来更加津津有味。传说中的美人鱼，外号色狼的非洲猎犬，久居长江的活化石*……它们，是这片土地千百年变迁的见证者，可现在，因为栖息地的减少，人类的肆意捕杀……令它们的处境越来越危险。请你张开自己的双手，去保护这些珍稀动物吧！ ', '自然生物', 1, '狐狸,老虎,乌龟,鳄鱼,海豚', NULL, NULL, NULL, NULL, NULL, '外貌,能力', '敌人关系,群体关系', '春,夏,秋,冬', '自然,水域', 0);
INSERT INTO `data` VALUES (901, 60, '故宫', '上尚印像', '红色', NULL, 48, '4-8岁', 1, '本套丛书是为4~8岁儿童制作的中国地理百科绘本，共10分册，分别是《长城》《故宫》《兵马俑》《圆明园》《丝绸之路》《长江三峡》《敦煌》《泰山》《西湖》《夫子庙》。本书以地理知识为切入点，以具有代表性的城市、建筑、文物为出发点，分别介绍与之相关的历史、文化、传说、政治、经济、生活等方面。', '历史知识', 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '四季轮转,朝夕轮转', NULL, 1);
INSERT INTO `data` VALUES (902, 182, '船舶100立体书', '沐渔文化', '白色', NULL, 24, '3-10岁', 0, '《超好玩的立体翻翻书·船舶动起来》是一本给3-10岁小朋友的、带有可活动零部件的趣味科普游戏书，可以向爱好船舶的小朋友全面介绍船舶结构和运转的秘密。书中包含100个活动部件，包括翻页、滑道、抽拉、铆钉、转盘、立体结构等多种游戏形式，从构造到应用，借助备受小朋友喜爱的12大跨页主题，从造船与修船讲起，通过真实还原繁忙的造船现场、海上石油工厂、航空母舰编队等热闹场景，让小朋友抠开翻页、拨动机关，就能了解船舶结构和动力学原理，熟悉船舶发展历史，认识货轮、滚装船、渔业加工船、挖泥船、破冰船、消防船等功能多样的海上船只，以及航空母舰、驱逐舰、军用直升机等军事编队的相关知识，吸引小朋友在惊喜中掌握知识，全面满足小朋友的好奇心和求知欲。', '科学知识', 1, NULL, NULL, NULL, NULL, '船,潜艇', NULL, '能力', '群体关系', NULL, '水域', 1);
INSERT INTO `data` VALUES (903, 185, '小美人鱼：海洋探秘', '美国迪士尼公司', '蓝绿色', NULL, 0, '3-6岁', 1, '《小美人鱼-海洋探秘》根据迪士尼同名经典电影改编，融入了精心选编的小知识介绍，让孩子在享受经典电影故事的同时，轻松愉快地学习到科普知识。为了增加阅读的趣味性，书中还装上了一些“小窗口”，翻开就可以看到与讲解知识相配合的小图片，既神秘又有趣，让孩子兴趣倍增，更容易理解和掌握知识，有效地增强了求知欲与探索欲。', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, NULL, '外貌,身份,能力', '群体关系', '朝夕轮转', '水域', 1);
INSERT INTO `data` VALUES (904, 193, '童眼识天下奇妙植物', '童心', '绿色,红色', NULL, 0, '3-6岁', 1, '《童眼识天下：奇妙植物》是一本以3—6岁儿童为读者对象，介绍趣味植物知识的少儿科普书。本书用简洁生动的语言，配以精美图片和轻松的知识讲解，为大家生动形象地介绍了各种各样的趣味植物，能够帮助小朋友快速熟悉常见的植物，开拓眼界，边看边学，轻松掌握植物常识。', '自然生物', 1, NULL, '花朵,萝卜,树,苹果,草莓,番茄,茄子', NULL, NULL, NULL, NULL, '外貌,能力', '群体关系', '四季轮转', '自然', 0);
INSERT INTO `data` VALUES (905, 196, '童眼识天下认百花', '童心', '彩色', NULL, NULL, '3-6岁', 1, '《童眼识天下》是给孩子的一套图画百科书，适合学龄前儿童亲子共读、小\n学低年级拓展视野.\n《童眼识天下--认百花》充分考虑到知识性、科学性、教育性、可行性及\n互动性的融合，符合孩子的认识特点，图书配以大幅摄影图片，图画色彩鲜\n明，真实呈现事物的本来面目，让孩子直观轻松地认识自然界的花朵，学习\n多种花朵的特点特色等知识，文字简洁生动。同时，开发孩子的观察力、空\n间想象力和初步的思维逻辑能力。', '自然生物', 1, NULL, NULL, NULL, NULL, NULL, NULL, '外貌,能力', '群体关系', '四季轮转,朝夕轮转', '自然,公园,花园,沙漠', 1);
INSERT INTO `data` VALUES (906, 197, '恐龙当家：恐龙帝国', '恐龙当家：恐龙帝国', '绿色', NULL, 60, '3-6岁', 1, '《恐龙当家-恐龙帝国》根据迪士尼同名经典电影改编，融入了精心选编的小知识介绍，让孩子在享受经典电影故事的同时，轻松愉快地学习到科普知识。\n\n为了增加阅读的趣味性，书中还装上了一些“小窗口”，翻开就可以看到与讲解知识相配合的小图片，既神秘又有趣，让孩子兴趣倍增，更容易理解和掌握知识，有效地增强了求知欲与探索欲。\n\n\n\n《迪士尼百科翻翻书书》系列共20本，每本书包含1个精彩的故事，均改编自迪士尼经典电影故事。小朋友在阅读精彩故事的同时，可以以故事主人公为榜样，逐步养成受益终身的好品格，成为趣味高雅、思维丰富的阅读者！\n每本书都包含精彩剧情和科普知识点，配有大量精心设置的游戏及趣味翻翻，让孩子在阅读的过程中，近距离感受科普的魅力，让阅读成为一件有趣的事情；在益智游戏的过程中,开动脑筋,全面提升思维力！', '科学知识', 1, NULL, NULL, NULL, NULL, NULL, '儿童', '外貌,性格,身份,能力', '宠物关系', '四季轮转,朝夕轮转,雨,打雷闪电', '自然', 1);
INSERT INTO `data` VALUES (907, 204, '童眼识天下缤纷四季', '童心', '彩色', NULL, NULL, '3-6岁', 1, '《童眼识天下》是一套专为学龄前儿童和低年级小学生精心设计打造的科普类图书。\n季节，是自然创作出的美好的画卷。春天如画、夏天似火、秋天像诗、冬天若梦，一年中，四季轮番展现着它的风华，带给我们不同的期待和惊喜。\n《缤纷四季》娓娓道来的语言和精美细致的绘图讲述有趣的关于四季的科普知识。带着孩子们一起聆听自然，细数自然的美好，感受四季轮回的魅力', '自然生物', 0, '松鼠,瓢虫,蜜蜂,萤火虫,鸟', NULL, NULL, NULL, NULL, NULL, NULL, '群体关系', '四季轮转,朝夕轮转', NULL, 0);
INSERT INTO `data` VALUES (908, 210, '幼儿十万个为什么可爱的动物', '沐渔文化', '橘色', NULL, 140, '3-6岁', 1, '幼儿十万个为什么（精编版）》是一套专为3~6岁幼儿打造的趣味幼儿小百科，共五分册：《可爱的动物》《奇妙的植物》《我们的身体》《远古的恐龙》《无边的宇宙》。\n本套书知识面涵盖广泛，所选问题从幼儿调查问卷中征选出来，是一套真正满足幼儿求知欲的亲子读物。动物、植物、宇宙、恐龙、人体，种种孩子们感兴趣的和生活中能碰到的问题，这套书都涵盖了；文字精练，通俗有趣，有利于亲子共读；插图呆萌可爱，小读者一看就会喜欢。文字量适中，高年龄段儿童可以独立阅读。', '科学知识', 1, '恐龙', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '自然,水域,天空', 1);
