INSERT INTO `data` VALUES (1017, 1712, '听听身体的声音', '韩丽', '黄色', NULL, 23, '3-6岁', 1, '这是一套适合3~6岁儿童阅读的双语绘本。3~6岁是孩子性格养成的关键期，在这一阶段，他们会面对各种成长问题和烦恼。本套图书通过生动有趣的故事和精美的图片，让孩子们学会正确地认识自己的身体，养成良好的生', '习惯养成', 1, NULL, NULL, NULL, NULL, NULL, '儿童', '外貌,性格,情绪,身份,能力', NULL, NULL, '学校', 1);
