INSERT INTO `data` VALUES (1037, 2074, '我逃跑了', '伊娃·林德斯特伦', '黄色', '该作品的作者伊娃·林德斯特伦是瑞典*杰出绘本大奖的获得者，其作品六次获得国际安徒生奖提名奖。', NULL, NULL, 0, '      《活了100万次的猫》“姐妹篇”，一样的感动，不一样的收获，和孩子一起学习更好地爱。', '成长道理', 1, '羊', NULL, NULL, NULL, NULL, NULL, '情绪', '朋友关系,群体关系', NULL, '自然,农牧场', 1);
INSERT INTO `data` VALUES (1038, 2223, '拔萝卜', '阿·托尔斯泰', '绿色、粉色', NULL, NULL, '6-12岁', 0, '老爷爷种了一棵萝卜，越长越大的萝卜谁都拔不动，*后全家人连同小耗子一起终于将大大的萝卜拔出来了。一个简单但不枯燥的故事，一幅生动而又温馨的生活画卷，不仅愉悦了孩子的心情，也让他悟出他自己才能懂得的道理。本书精选了阿?托尔斯泰的童话，配合小学一年级拼音阅读。', '成长道理', 1, '猫,狗,鼠', NULL, '老人', NULL, NULL, '儿童', NULL, '家庭关系,宠物关系', '春,秋', NULL, 1);
