INSERT INTO `data` VALUES (1040, 2240, '小鸟和鳄鱼', '邓正祺', '绿色', '第十届“信谊图画书奖”图画书创作奖入围奖\n入选第二届“童阅中国”原创好童书30种荣誉称号书目\n入围2022全国家庭亲子阅读活动百部导读书目', NULL, '3-6岁', 0, '过是一个小小的玻璃房子。有一天，豆丁在蛞蝓的鼓励', '成长道理', 1, '鳄鱼,鸟', NULL, NULL, NULL, NULL, NULL, '外貌', NULL, NULL, '自然', 0);
