INSERT INTO `data` VALUES (1042, 2351, '世界上最聪明的老鼠', '伊芙·泰特斯', '蓝色', '凯迪克大奖作品，美国图书馆协会推荐童书，出版人周刊、纽约时报等知名媒体强烈推荐 ，改编成动画片，在“迪士尼频道”播放，也曾被改编成迪斯尼电影《妙妙探》。', NULL, '3-6岁', 0, '的路上，它慢慢学会独立和坚强，学会应对所有的危险', '成长道理', 1, '鼠', NULL, NULL, NULL, NULL, NULL, '能力', '家庭关系,朋友关系', '夜晚', NULL, 1);
