INSERT INTO `data` VALUES (1050, 2717, '我最好的朋友小麦', '汉娜·E.哈里森(Hannah E.Harrison)', '黄色', NULL, NULL, NULL, NULL, '汉娜·E.哈里森文图的《我优选的朋友小麦》包括：宝拉和小麦从小就是朋友。宝拉觉得，小麦是很棒的。可是当其他孩子认为小麦又大又蠢，宝拉的想法开始动摇……直到小麦让宝拉明白，什么是真正的朋友。', '情感体验', 1, '鼠,大象', NULL, NULL, NULL, NULL, NULL, '外貌', '朋友关系', NULL, '学校', 1);
INSERT INTO `data` VALUES (1051, 2720, '盼望', '马修·科德尔(Matthew Cordell)', '黄色', NULL, 48, NULL, 0, '一对大象夫妻开始共同生活，盼望有一只小象能够来到他们的世界。\n他们准备，他们建造，他们旅行，但是小象一直没有来……一切都静止了。\n经历了挫败、失望和苦痛，他们内心很深处的渴望，伴随着一阵轰隆隆的声音，来了……', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `data` VALUES (1052, 2733, '小狗恩佐', '加思·斯坦', '红色、黄色', '纽约时报畅销书', NULL, '3-6岁', 1, '对于恩佐来说，独立日意味着3件事情：烟花、家人和赛车！这一年卓伊终于可以去参加儿童卡丁车比赛了。可是听到一个男孩说女孩怎么会开得快呢，卓伊退缩了，她想取消报名。恩佐知道他该站出来帮助家人重拾信心，为这', '情感体验', 1, '狗', NULL, NULL, NULL, NULL, NULL, NULL, '宠物关系', '冬', NULL, 1);
INSERT INTO `data` VALUES (1053, 2742, '田螺姑娘', '曹文轩、陈先云', '黄色', NULL, NULL, '8-12岁', 1, '本套大型阅读丛书，作为国家统编小学语文教科书一至六年级的组成部分，供师生在语文课堂上阅读交流使用。丛书精心遴选古今中外名家名篇，涵盖了不同体裁、不同主题的经典文学作品。\n\n与语文课堂教学同步，遵循阅读规律，兼顾学生阅读能力的差异性，以满足不同年级学生的阅读需求。\n\n全面实现课外阅读课程化，指导学生科学合理地学会阅读，提高自主阅读、独立阅读能力，养成良好的阅读习惯，从而解决读什么书、怎么读好书、为什么读书等长期困扰广大师生、家长的难题。', '传统文化', 1, NULL, NULL, NULL, NULL, NULL, NULL, '性格,身份', NULL, NULL, '家', 1);
INSERT INTO `data` VALUES (1054, 2749, '月亮，你好', '安德烈·德昂 ', '蓝色', '成名已久经典绘本：法国绘本大师安德鲁·德翰充满童心的佳作。', NULL, '3-6岁', 0, '一天晚上，小朋友在海上遇见玩荡秋千的月亮，就邀请她到家里做客……这个故事提供孩子自由发挥想象力的空间并满足更多元的阅读乐趣。', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '春,夏,秋,冬,傍晚,夜晚', '自然', 1);
INSERT INTO `data` VALUES (1055, 2750, '熊来了', '理查德·T.莫里斯 ', '蓝色、棕色', '获2020年凯迪克银奖', NULL, '3-6岁', 0, '2020年凯迪克银奖图画书《熊来了》从一开始就能成功抓住小读者的心，在封面上探出半个身子的熊，不正是一个又萌又憨又充满好奇心的孩子吗？不知从哪里奔涌过来的小河，朝着不知哪里欢腾而下，连小河也不知道自己是小河，更何况那只好奇的熊。\n那一定是一只非常年轻的熊，或者是刚刚从冬眠中醒来，反正还处于懵懵懂懂的状态，否则不能不知道小河是用来干什么的。可是，年轻*可贵的品质就是对于未知充满好奇，并且敢于尝试----熊趴在河边倒地的一株枯树干上，将胖乎乎的爪子探向河里......此时，一位有经验且责任心强的人类妈妈可能会大声喝止，从而确保了孩子的安全，却也结束了他那即将开始的美妙旅程。但莽撞的熊却顾不上自己的身体压断了树干，然后本能的、无知无畏地“跳”进了河里。', '情感体验', 1, '熊,乌龟,青蛙,鸭', NULL, NULL, NULL, NULL, NULL, NULL, '群体关系', NULL, '水域', 1);
INSERT INTO `data` VALUES (1056, 2755, '山梨', '宫泽贤治', '蓝色', '宫泽贤治被誉为“日本的安徒生”“日本的国民作家”，多篇作品入选日本中小学生语文教材。让孩子阅读世界经典，方可打下良好的精神底色。', NULL, '6-10岁', 1, '来自森林、田野、星空和铁道的故事宫崎骏、安房直子、高畑勋的灵感来源，樱桃小丸子也要背诵他的作品！入选“一千年来你最喜欢的日本文学家”宫泽贤治是日本昭和年代文学巨匠，他是一位诗人、儿童文学作家，在文学目前与安徒生、王尔德齐名。其作品中的清新悠长的意蕴、对大自然深深的眷恋与敬畏深受大小读者喜爱，也影响了宫崎骏、高田勋、安房直子等无数后代名家。《宫泽贤治小森林童话》从宫泽贤治超过100篇童话中，精选了12个最适合6-10岁阅读的童话故事，并特别邀请了10位日本当红绘本大师为图书进行插画，包括《窗边的小豆豆》设计师、博洛尼亚特别奖得主、日本绘本大奖得主等多位在国际上有较高知名度的作者，整套图书充满了艺术性与想象力。同时，本书中文译本邀请了日本儿童文学专家彭懿为每篇童话撰写导读，帮助家长和小朋友理解童话之美。', '情感体验', 1, '鸟,蟹,鱼', NULL, NULL, NULL, NULL, NULL, '情绪', '家庭关系,敌人关系', '四季轮转', '水域', 1);
INSERT INTO `data` VALUES (1057, 2756, '要求太多的餐馆', '宫泽贤治', '绿色', '宫泽贤治被誉为“日本的安徒生”“日本的国民作家”，多篇作品入选日本中小学生语文教材。让孩子阅读世界经典，方可打下良好的精神底色。', NULL, '7-10岁', 1, '该册童话主要寄寓了宫泽贤治对大自然、对社会、对人生的探索和思考，真挚而深邃。包含了《要求太多的餐馆》《猫儿事务所》《双子星》等名篇。', '哲理思考', 1, NULL, NULL, '猎人', NULL, NULL, NULL, '外貌,性格', '朋友关系,敌人关系', NULL, '自然,异世界', 1);
INSERT INTO `data` VALUES (1058, 2774, '飞天狐猴', '泽赫拉·希克斯', '黄色', '泽赫拉·希克斯于2009年获得麦克米伦童书插画奖\n绘本《都是我的！》获2016年英国凯特·格林纳威大奖提名，另有代表作品《讨厌牙刷的男孩》《喜欢雨靴的女孩》《飞天狐猴》《长颈鹿和小青蛙》（获2018年英国诺丁汉童书奖）', NULL, '3-10岁', 0, '狐猴家族的每个成员在跳跃方面都有独门绝技：妈妈很会在高空秋千上跳，爸爸的拿手绝活是在蹦床上跳。奶奶的炮打飞“猴”也超级棒！可是，小狐猴就是不敢跳。上台的时间就快到了，他该怎么做呢？这本书温柔而巧妙地帮助孩子克服对挑战的恐惧，大胆挖掘自己的潜能', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, NULL, '能力', '家庭关系', NULL, NULL, 0);
INSERT INTO `data` VALUES (1059, 2786, '在一个漆黑漆黑的夜晚', '海豚传媒', '黑色', NULL, NULL, '3-6岁', 0, '在一个漆黑漆黑的夜晚，一只小老鼠独自出外觅食，漆黑的街道、漆黑的猫头鹰、漆黑的大门、漆黑的楼梯……一切都笼罩在黑暗之中，一切都笼罩在静谧之中。小老鼠虽然万分恐惧，不过为了美味的食物，它毅然前行。一本极佳的睡前读物，让我们随着小老鼠在黑夜之中寻找梦中的香甜“奶酪”吧！\n\n跟随着小老鼠的视角，我们一路穿越了漆黑漆黑的森林，漆黑漆黑的小路，漆黑漆黑的小镇，漆黑漆黑的房间……绘本的故事在语言的重复中层层递进又富有变化，充满韵律感的文字既符合孩子的语言发展特征，又易于识记。\n\n绘者的插画设计极具镜头感，通过匠心独运的视角选取和精妙绝伦的细节暗示以及生动细腻的绘画风格，绘者营造出了一种极度紧张的气氛，让读者时刻为绘本的主人公小老鼠捏一把冷汗，却能欣慰地发现我们的主人公总能化险为夷。出色的图画表现力，能够帮助孩子很好地观察和推理情节的发展，享受这场美妙的冒险旅程。同时，对于怕黑和不敢一个人睡的孩子来说，这又是一场跟随主人公小老鼠一起战胜内心恐惧的勇气之旅，一起出发吧，甜美的果实总在“大冒险”之后。', '情感体验', 1, '鼠', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '夜晚', NULL, 1);
INSERT INTO `data` VALUES (1060, 2790, '小猫小猫玩泥巴', '长野英子', '黄色', NULL, NULL, '3-6岁', 0, '这是一本看似简单却非常适合两岁左右宝宝阅读的绘\n本。\n儿歌式的语言富有节奏和韵律，朗朗上口，可以充分\n引发宝宝的兴趣，并有助于宝宝语言能力的发展;\n生动好玩的画面正像是调皮可爱的宝宝，带孩子观察\n细节也充满了乐趣;\n玩泥巴、玩水，享用美食，都是这阶段宝宝很喜欢的\n事情了，特别吸引宝宝的注意力;\n结尾，宝宝快乐地进入梦乡，让孩子感觉充分安心美\n好。\n太阳光闪亮亮的一天，猫妈妈拿出充气泳池，小猫们兴奋极了，纷纷换了泳衣去玩水。玩着玩着，小猫们竟然全都玩起了泥巴！富有儿歌的韵律，游戏深受孩子喜爱，充满快乐和温情。', '情感体验', 1, '猫', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `data` VALUES (1061, 2795, '伦德和狗狗', ' 伊娃·林德斯特伦', '蓝色、棕色', '《伦德和狗狗》为国际安徒生奖六次提名奖画家代表作品，该作家作品屡获瑞典杰出绘本大奖。', 36, NULL, NULL, '《伦德和狗狗》以童话的体裁讲述了主人公伦德和他的狗之间的友情故事。情节满足了幼儿的奇幻想象，充满童趣，画面别致清新，结局出人意料，伦德最后竟给他的狗真的带回了月亮，充满喜剧感。《伦德和狗狗》为国际安徒生奖六次提名奖画家代表作品，该作家作品屡获瑞典杰出绘本大奖。', '情感体验', 1, '狗', NULL, NULL, NULL, NULL, NULL, NULL, '宠物关系', NULL, NULL, 1);
INSERT INTO `data` VALUES (1062, 2822, '面包超人和小骷髅', '柳濑嵩', '蓝色、绿色', '日本福禄贝尔馆镇社之宝\n日本”孩子心中自喜欢的角色大调查“第1名\n2009年荣获吉尼斯世界纪录\n超高人气，畅销日本40余年\n日本全国学校图书馆协会选定图书\n中国科学院心理学教授张梅玲推荐', 28, '3-6岁', 1, '面包超人是制作面包的果酱大叔所做出的红豆面包，因刚好生命之星坠落在面包上而诞生。有 ，面包超人发现只要帮助别人，内心就会变得暖呼呼的。此后，面包超人为了能在有人需要的时候伸出援手，他开始每天都在街上巡逻，遇到肚子饿的人也会将自己的脸分给他们吃。 但是，在面包超人诞生的同时，细菌人也诞生了。细菌人为了打败他的宿敌面包超人来到了这个星球。而且细菌人总是爱恶作剧，让大家感到很困扰。面包超人世界中的角色都各具特色，每一本书中的角色和故事都 热闹且欢乐。', '情感体验', 1, NULL, NULL, NULL, NULL, NULL, NULL, '外貌,情绪,身份,能力', NULL, NULL, NULL, 1);
